import itertools
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    freq = Counter()
    for transaction in transactions:
        freq.update(transaction)
    freq = {item: count for (item, count) in freq.items() if count >= min_sup}
    items = sorted(freq.keys(), key=lambda x: (-freq[x], x))
    item_to_idx = {item: idx for (idx, item) in enumerate(items)}
    processed_transactions = []
    for transaction in transactions:
        filtered = [item for item in transaction if item in freq]
        filtered.sort(key=lambda x: (-freq[x], x))
        processed_transactions.append(filtered)

    class Node:

        def __init__(self, item, count=1, parent=None):
            self.item = item
            self.count = count
            self.parent = parent
            self.children = {}
            self.next = None
    root = Node(None)
    header_table = defaultdict(list)
    for transaction in processed_transactions:
        current = root
        for item in transaction:
            if item not in current.children:
                current.children[item] = Node(item, parent=current)
            current = current.children[item]
            current.count += 1
    for transaction in processed_transactions:
        current = root
        for item in transaction:
            current = current.children[item]
            header_table[item].append(current)
    result = []

    def mine_tree(node, prefix):
        if node is None or node.item is None:
            return
        new_prefix = prefix + [node.item]
        if len(node.children) == 0:
            for i in range(1, len(new_prefix) + 1):
                for subset in itertools.combinations(new_prefix, i):
                    result.append(tuple(sorted(subset)))
            return
        for (item, child) in node.children.items():
            cond_pattern_base = []
            for node in header_table[item]:
                path = []
                current = node.parent
                while current.item is not None:
                    path.append(current.item)
                    current = current.parent
                cond_pattern_base.append(path[::-1])
            cond_tree = build_conditional_tree(cond_pattern_base, freq)
            mine_tree(cond_tree, new_prefix)

    def build_conditional_tree(patterns, freq):

        class CondNode:

            def __init__(self, item, count=1, parent=None):
                self.item = item
                self.count = count
                self.parent = parent
                self.children = {}
                self.next = None
        root = CondNode(None)
        for pattern in patterns:
            current = root
            for item in pattern:
                if item not in current.children:
                    current.children[item] = CondNode(item, parent=current)
                current = current.children[item]
                current.count += 1
        return root
    mine_tree(root, [])
    result = [tuple(sorted(set)) for set in result]
    result = sorted(result, key=lambda x: (len(x), x))
    return result

