import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    if epsilon == 0:
        return []
    (n_samples, n_features) = data.shape
    phi = 2 * math.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples

    def c(n):
        if n <= 1:
            return 0.0
        return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    def build_tree(subsample):
        if len(subsample) == 1:
            return {'type': 'leaf', 'sample_index': subsample[0], 'depth': 0}
        feature = np.random.randint(n_features)
        (min_val, max_val) = (data[subsample, feature].min(), data[subsample, feature].max())
        threshold = np.random.uniform(min_val, max_val)
        left_subsample = [i for i in subsample if data[i, feature] < threshold]
        right_subsample = [i for i in subsample if data[i, feature] >= threshold]
        left_tree = build_tree(left_subsample)
        right_tree = build_tree(right_subsample)
        return {'type': 'node', 'feature': feature, 'threshold': threshold, 'left': left_tree, 'right': right_tree, 'depth': 1 + max(left_tree['depth'], right_tree['depth'])}

    def get_path_length(tree, sample_index):
        if tree['type'] == 'leaf':
            return tree['depth']
        feature = tree['feature']
        threshold = tree['threshold']
        if data[sample_index, feature] < threshold:
            return 1 + get_path_length(tree['left'], sample_index)
        else:
            return 1 + get_path_length(tree['right'], sample_index)
    avg_path_lengths = np.zeros(n_samples)
    for _ in range(n_trees):
        if sample_size >= n_samples:
            subsample = np.arange(n_samples)
        else:
            subsample = np.random.choice(n_samples, size=sample_size, replace=False)
        tree = build_tree(subsample)
        for i in range(n_samples):
            path_length = get_path_length(tree, i)
            avg_path_lengths[i] += path_length
    avg_path_lengths /= n_trees
    anomaly_scores = np.exp(-avg_path_lengths / phi)
    num_outliers = int(np.ceil(epsilon * n_samples))
    outlier_indices = np.argsort(anomaly_scores)[-num_outliers:]
    return sorted(outlier_indices.tolist())

