def local_outlier_factor(data, k, epsilon=1.5):
    """Compute the Local Outlier Factor (LOF) for each sample in the data."""
    n_samples = data.shape[0]
    dist_matrix = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(n_samples):
            dist_matrix[i, j] = np.linalg.norm(data[i] - data[j])
    k_distances = np.zeros(n_samples)
    k_neighbors = [[] for _ in range(n_samples)]
    for i in range(n_samples):
        sorted_indices = np.argsort(dist_matrix[i])
        k_neighbors[i] = sorted_indices[1:k + 1]
        k_distances[i] = dist_matrix[i][k_neighbors[i][-1]]
    lrd = np.zeros(n_samples)
    for i in range(n_samples):
        reach_dist_sum = 0.0
        for j in k_neighbors[i]:
            reach_dist = max(k_distances[j], dist_matrix[i, j])
            reach_dist_sum += reach_dist
        lrd[i] = k / reach_dist_sum
    lof_scores = np.zeros(n_samples)
    for i in range(n_samples):
        lrd_sum = 0.0
        for j in k_neighbors[i]:
            lrd_sum += lrd[j]
        lof_scores[i] = lrd_sum / k / lrd[i]
    lof_scores_rounded = np.round(lof_scores, 4)
    outlier_indices = [i for i in range(n_samples) if lof_scores_rounded[i] > epsilon]
    return (lof_scores_rounded.tolist(), sorted(outlier_indices))
