import numpy as np
def glm_links():
    """Construct and return standard GLM link functions.

    Returns
    -------
    dict
        A three-entry dictionary (identity, log, logit) where each entry is a
        dictionary containing callables for the link, its inverse and its
        derivative with respect to μ. All functions must work with numpy
        scalars as well as 1-D/2-D numpy arrays via element-wise operations.
    """

    def identity_link(mu):
        return mu

    def identity_inv_link(eta):
        return eta

    def identity_derivative(mu):
        return np.ones_like(mu)

    def log_link(mu):
        return np.log(mu)

    def log_inv_link(eta):
        return np.exp(eta)

    def log_derivative(mu):
        return 1 / mu

    def logit_link(mu):
        return np.log(mu / (1 - mu))

    def logit_inv_link(eta):
        return 1 / (1 + np.exp(-eta))

    def logit_derivative(mu):
        return 1 / (mu * (1 - mu))
    return {'identity': {'link': identity_link, 'inv_link': identity_inv_link, 'derivative': identity_derivative}, 'log': {'link': log_link, 'inv_link': log_inv_link, 'derivative': log_derivative}, 'logit': {'link': logit_link, 'inv_link': logit_inv_link, 'derivative': logit_derivative}}

import math
from collections import Counter
from itertools import combinations
from typing import List, Dict, Tuple, Hashable
def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                       itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemset (stored as a tuple of
        sorted items) to its absolute support count.  If no itemset reaches
        the threshold the function must return an empty dictionary.
    """
    N = len(transactions)
    min_support_count = math.ceil(minsup * N)
    item_counts = Counter()
    for transaction in transactions:
        unique_items = set(transaction)
        item_counts.update(unique_items)
    frequent_itemsets = {}
    for (item, count) in item_counts.items():
        if count >= min_support_count:
            frequent_itemsets[item,] = count
    k = 2
    while True:
        candidates = set()
        for itemset in frequent_itemsets:
            for item in item_counts:
                if item > itemset[-1]:
                    new_itemset = tuple(sorted(itemset + (item,)))
                    candidates.add(new_itemset)
        candidate_support = Counter()
        for transaction in transactions:
            unique_items = set(transaction)
            for candidate in candidates:
                if set(candidate).issubset(unique_items):
                    candidate_support[candidate] += 1
        new_frequent = {}
        for (candidate, count) in candidate_support.items():
            if count >= min_support_count:
                new_frequent[candidate] = count
        if not new_frequent:
            break
        frequent_itemsets.update(new_frequent)
        k += 1
    return frequent_itemsets

def check_data(a, b):
    """Validate and convert the two inputs so that both are numeric.

    The function must accept integers, floats, or numeric strings.  Any string
    should be converted with `float()`; existing numeric values stay as they
    are.  The pair `(a, b)` must then be returned.
    """

    def convert_value(value):
        if isinstance(value, str):
            return float(value)
        return value
    a = convert_value(a)
    b = convert_value(b)
    return (a, b)
def validate_input(function):

    def wrapper(a, b):
        (a, b) = check_data(a, b)
        return function(a, b)
    return wrapper
@validate_input
def sum_numbers(a, b):
    """Return the arithmetic sum of *a* and *b* after validation."""
    return a + b

from typing import List, Union
Number = Union[int, float]
def k_smallest(nums: List[Number], k: int) -> List[Number]:
    """Return the k smallest numbers contained in *nums*.

    The function scans the input list once, maintaining a custom
    max-heap (size at most k) implemented with a Python list.  At the end,
    return the k smallest elements in ascending order.  If *k* is invalid
    (≤0 or larger than len(nums)) return -1.

    The use of the built-in ``heapq`` module is **not** allowed.
    """
    if k <= 0 or k > len(nums):
        return -1

    def _move_up(heap, index):
        while index > 0:
            parent = (index - 1) // 2
            if heap[parent] < heap[index]:
                (heap[parent], heap[index]) = (heap[index], heap[parent])
                index = parent
            else:
                break

    def _move_down(heap, index):
        while True:
            left = 2 * index + 1
            right = 2 * index + 2
            largest = index
            if left < len(heap) and heap[left] > heap[largest]:
                largest = left
            if right < len(heap) and heap[right] > heap[largest]:
                largest = right
            if largest != index:
                (heap[index], heap[largest]) = (heap[largest], heap[index])
                index = largest
            else:
                break
    heap = []
    for num in nums:
        if len(heap) < k:
            heap.append(num)
            _move_up(heap, len(heap) - 1)
        elif num < heap[0]:
            heap[0] = num
            _move_down(heap, 0)
    return sorted(heap)

import numpy as np
def newton(X: np.ndarray, y: np.ndarray, epsilon: float=1e-06, max_iter: int=1000) -> list[list[float]]:
    """Solve a two-variable linear least-squares problem using Newton's method."""
    w = np.array([[1.0], [1.0]])
    for _ in range(max_iter):
        gradient = 2 / X.shape[0] * X.T @ (X @ w - y)
        hessian = 2 / X.shape[0] * X.T @ X
        if np.linalg.norm(gradient) < epsilon:
            break
        try:
            inv_hessian = np.linalg.inv(hessian)
        except np.linalg.LinAlgError:
            inv_hessian = np.linalg.pinv(hessian)
        w = w - inv_hessian @ gradient
    return [[round(w[0][0], 4)], [round(w[1][0], 4)]]

import numpy as np
def softplus(x, deriv: bool=False):
    """
    Compute the SoftPlus activation or its derivative (logistic/sigmoid function).

    Parameters:
        x (scalar, list, or numpy.ndarray): Input values.
        deriv (bool): If True, return the derivative (sigmoid) instead of SoftPlus.

    Returns:
        list or float: Rounded to 4 decimal places.
    """
    x = np.asarray(x)
    if deriv:
        return np.round(1 / (1 + np.exp(-x)), 4).tolist()
    else:
        return np.round(np.log1p(np.exp(x)), 4).tolist()

import numpy as np
from collections import Counter
def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*.

    Args:
        corpus: A list of word tokens used for training.
        sequence: A list of word tokens for which to compute the log-probability.
        N: The order of the n-gram (must be ≥ 1).

    Returns:
        The total log-probability of the sequence under the model, rounded to 4 decimal places.
        If any required count is zero, returns float('-inf').
    """
    if N == 1:
        unigram_counts = Counter(corpus)
        total_words = len(corpus)
        for word in sequence:
            if unigram_counts.get(word, 0) == 0:
                return float('-inf')
        log_prob = 0.0
        for word in sequence:
            prob = unigram_counts[word] / total_words
            log_prob += np.log(prob)
        return round(log_prob, 4)
    else:
        ngram_counts = Counter()
        prefix_counts = Counter()
        for i in range(len(corpus) - N + 1):
            ngram = tuple(corpus[i:i + N])
            prefix = tuple(corpus[i:i + N - 1])
            ngram_counts[ngram] += 1
            prefix_counts[prefix] += 1
        for i in range(len(sequence) - N + 1):
            ngram = tuple(sequence[i:i + N])
            prefix = tuple(sequence[i:i + N - 1])
            if ngram_counts.get(ngram, 0) == 0 or prefix_counts.get(prefix, 0) == 0:
                return float('-inf')
        log_prob = 0.0
        for i in range(len(sequence) - N + 1):
            ngram = tuple(sequence[i:i + N])
            prefix = tuple(sequence[i:i + N - 1])
            prob = ngram_counts[ngram] / prefix_counts[prefix]
            log_prob += np.log(prob)
        return round(log_prob, 4)

import numpy as np
def compute_impurity(y, criterion):
    """Compute a node's impurity.

    Args:
        y (numpy.ndarray): 1-D array with the target values.
        criterion (str): One of {"entropy", "gini", "mse"}.

    Returns:
        float: Impurity value rounded to 4 decimal places.
    """
    if len(y) == 0:
        return round(0.0, 4)
    if criterion not in {'entropy', 'gini', 'mse'}:
        raise ValueError("Invalid criterion. Must be one of {'entropy', 'gini', 'mse'}.")
    if criterion == 'entropy':
        (_, counts) = np.unique(y, return_counts=True)
        probabilities = counts / len(y)
        entropy = -np.sum(probabilities * np.log2(probabilities))
        return round(entropy, 4)
    elif criterion == 'gini':
        (_, counts) = np.unique(y, return_counts=True)
        probabilities = counts / len(y)
        gini = 1 - np.sum(probabilities ** 2)
        return round(gini, 4)
    elif criterion == 'mse':
        mean_y = np.mean(y)
        mse = np.mean((y - mean_y) ** 2)
        return round(mse, 4)

import numpy as np
def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]): Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or −1 on invalid input.
    """
    if not observations:
        return -1
    n = len(S)
    if len(A) != n or any((len(row) != n for row in A)):
        return -1
    m = len(B[0]) if n > 0 else 0
    if len(B) != n or any((len(row) != m for row in B)):
        return -1
    if any((obs < 0 or obs >= m for obs in observations)):
        return -1
    T = len(observations)
    alpha = [[0.0 for _ in range(n)] for _ in range(T)]
    for i in range(n):
        alpha[0][i] = S[i] * B[i][observations[0]]
    for t in range(1, T):
        for j in range(n):
            total = 0.0
            for i in range(n):
                total += alpha[t - 1][i] * A[i][j]
            alpha[t][j] = total * B[j][observations[t]]
    probability = sum((alpha[T - 1][i] for i in range(n)))
    return round(probability, 4)

from collections import Counter, defaultdict
from typing import List, Tuple, Dict
def fp_growth(transactions: List[List[str]], min_support: int) -> List[List[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth."""
    if not transactions:
        return []
    item_counts = Counter()
    for transaction in transactions:
        item_counts.update(transaction)
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_support}
    if not frequent_items:
        return []
    sorted_items = sorted(frequent_items, key=lambda x: (-item_counts[x], x))

    class FPTreeNode:

        def __init__(self, item: str, count: int, parent: 'FPTreeNode'=None):
            self.item = item
            self.count = count
            self.parent = parent
            self.children = dict()
            self.next = None
    header_table = {item: [0, None] for item in sorted_items}
    root = FPTreeNode('root', 0)
    for transaction in transactions:
        filtered = [item for item in transaction if item in frequent_items]
        filtered.sort(key=lambda x: (-item_counts[x], x))
        current = root
        for item in filtered:
            if item not in current.children:
                current.children[item] = FPTreeNode(item, 1, current)
                if header_table[item][1] is None:
                    header_table[item][1] = current.children[item]
                else:
                    node = header_table[item][1]
                    while node.next:
                        node = node.next
                    node.next = current.children[item]
                header_table[item][0] += 1
            else:
                current.children[item].count += 1
                header_table[item][0] += 1
            current = current.children[item]
    frequent_itemsets = []

    def mine_tree(node: FPTreeNode, prefix: List[str]):
        if node is None or node.item == 'root':
            return
        new_prefix = prefix + [node.item]
        if not node.children:
            count = node.count
            itemset = sorted(new_prefix)
            frequent_itemsets.append(itemset)
            return
        pattern_base = defaultdict(int)
        current = node.next
        while current:
            path = []
            temp = current
            while temp.parent and temp.parent.item != 'root':
                path.append(temp.parent.item)
                temp = temp.parent
            path.reverse()
            for item in path:
                pattern_base[item] += current.count
            current = current.next
        conditional_items = [item for (item, count) in pattern_base.items() if count >= min_support]
        if not conditional_items:
            return
        conditional_items.sort(key=lambda x: (-pattern_base[x], x))
        conditional_root = FPTreeNode('conditional_root', 0)
        for transaction in transactions:
            filtered = [item for item in transaction if item in conditional_items]
            filtered.sort(key=lambda x: (-pattern_base[x], x))
            current = conditional_root
            for item in filtered:
                if item not in current.children:
                    current.children[item] = FPTreeNode(item, 1, current)
                else:
                    current.children[item].count += 1
                current = current.children[item]
        for item in conditional_items:
            mine_tree(conditional_root.children[item], new_prefix)
    for item in sorted_items:
        if header_table[item][0] >= min_support:
            mine_tree(header_table[item][1], [])
    frequent_itemsets = [sorted(set(itemset)) for itemset in frequent_itemsets]
    frequent_itemsets = list({tuple(itemset) for itemset in frequent_itemsets})
    frequent_itemsets = [list(itemset) for itemset in frequent_itemsets]
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets

import numpy as np
def relu_(Z):
    """Apply the element-wise Rectified Linear Unit (ReLU) activation.

    The function keeps the container type of the input:
    scalar → scalar, list → list, NumPy array → NumPy array.

    Args:
        Z (int | float | list | numpy.ndarray): Input data – scalar, 1-D or 2-D.

    Returns:
        Same type as *Z* with all negative values clipped to 0.
    """
    if isinstance(Z, (int, float)):
        return max(0, Z)
    elif isinstance(Z, list):
        return [max(0, x) for x in Z]
    elif isinstance(Z, np.ndarray):
        return np.maximum(Z, 0)
    else:
        raise TypeError('Unsupported input type. Use int, float, list, or numpy.ndarray.')

import numpy as np
TIME_STEPS = 20
def string_to_int(text: str, time_steps: int, vocabulary: dict[str, int]) -> list[int]:
    """Encodes a text string into a fixed-length list of integer token ids."""
    encoded = []
    for char in text:
        encoded.append(vocabulary.get(char, 0))
    if len(encoded) > time_steps:
        encoded = encoded[:time_steps]
    else:
        encoded += [0] * (time_steps - len(encoded))
    return encoded
def int_to_string(indices, inverse_vocab: dict[int, str]) -> str:
    """Converts a list of integer ids back to text, ignoring padding tokens (0)."""
    return ''.join((inverse_vocab.get(idx, '') for idx in indices if idx != 0))
def run_example(model, input_vocabulary: dict[str, int], inv_output_vocabulary: dict[int, str], text: str) -> str:
    """Encodes text, runs prediction, and decodes the output."""
    encoded_text = string_to_int(text, TIME_STEPS, input_vocabulary)
    batch = np.array([encoded_text])
    predictions = model.predict(batch)
    predicted_indices = np.argmax(predictions, axis=-1)
    decoded_text = int_to_string(predicted_indices[0], inv_output_vocabulary)
    return decoded_text

import re
from collections import Counter
class Token:

    def __init__(self, word):
        self.count = 0
        self.word = word

    def __repr__(self):
        return "Token(word='{}', count={})".format(self.word, self.count)
def tokenize_and_count(text: str) -> list[Token]:
    words = re.findall('[a-zA-Z]+', text)
    words = [word.lower() for word in words]
    word_counts = Counter(words)
    if not word_counts:
        return []
    tokens = [Token(word) for word in word_counts]
    for token in tokens:
        token.count = word_counts[token.word]
    tokens.sort(key=lambda t: (-t.count, t.word))
    return tokens

import numpy as np
def linear(z):
    """Linear (identity) activation function.

    Args:
        z: Scalar, list, tuple or NumPy ndarray.

    Returns:
        The same value(s) as *z*, obeying the rules:
            • Scalars are returned unchanged.
            • Non-scalar inputs are returned as a NumPy ndarray.
    """
    if isinstance(z, (int, float)):
        return z
    elif isinstance(z, (list, tuple)):
        return np.array(z)
    elif isinstance(z, np.ndarray):
        return z
    else:
        raise TypeError(f'Unsupported input type: {type(z)}')

import numpy as np
def best_arm(payoff_probs: list[float]) -> tuple[float, int]:
    """Find the arm with the highest expected reward in a Bernoulli bandit.

    Parameters
    ----------
    payoff_probs : list[float]
        A list where each element is the success probability of the
        corresponding arm. Probabilities must lie in the interval [0, 1].

    Returns
    -------
    tuple
        (max_expected_reward, best_arm_index).  If the input list is empty or
        contains an invalid probability (<0 or >1), the function must return
        (-1.0, -1).
    """
    if not payoff_probs:
        return (-1.0, -1)
    for p in payoff_probs:
        if not 0 <= p <= 1:
            return (-1.0, -1)
    max_prob = max(payoff_probs)
    best_arm_index = payoff_probs.index(max_prob)
    return (max_prob, best_arm_index)

from collections import deque
import math
def escape_fire_maze(grid: list[str]) -> int:
    n = len(grid)
    if grid[0][0] == '#' or grid[n - 1][n - 1] == '#':
        return -1
    if grid[0][0] == 'F' or grid[n - 1][n - 1] == 'F':
        return -1
    if n == 1:
        return 0
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    fire_time = [[math.inf] * n for _ in range(n)]
    q = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_time[i][j] = 0
                q.append((i, j))
    while q:
        (x, y) = q.popleft()
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (grid[nx][ny] != '#') and (fire_time[nx][ny] == math.inf):
                fire_time[nx][ny] = fire_time[x][y] + 1
                q.append((nx, ny))
    visited = [[False] * n for _ in range(n)]
    q = deque()
    q.append((0, 0, 0))
    visited[0][0] = True
    while q:
        (x, y, t) = q.popleft()
        if x == n - 1 and y == n - 1:
            return t
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (not visited[nx][ny]) and (grid[nx][ny] != '#'):
                if t + 1 < fire_time[nx][ny]:
                    visited[nx][ny] = True
                    q.append((nx, ny, t + 1))
    return -1

import numpy as np
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """

    def gini_impurity(y):
        """Calculate Gini impurity for a set of labels."""
        (_, counts) = np.unique(y, return_counts=True)
        return 1 - np.sum((counts / len(y)) ** 2)

    def split_data(X, y, feature_idx, threshold):
        """Split the data based on a feature and threshold."""
        left_mask = X[:, feature_idx] <= threshold
        right_mask = ~left_mask
        return (X[left_mask], y[left_mask], X[right_mask], y[right_mask])

    def best_split(X, y):
        """Find the best feature and threshold to split on."""
        best_gain = 0
        (best_feature, best_threshold) = (None, None)
        (n_samples, n_features) = X.shape
        for feature_idx in range(n_features):
            sorted_indices = np.argsort(X[:, feature_idx])
            sorted_values = X[:, feature_idx][sorted_indices]
            sorted_labels = y[sorted_indices]
            unique_values = np.unique(sorted_values)
            for i in range(1, len(unique_values)):
                threshold = (unique_values[i - 1] + unique_values[i]) / 2
                (X_left, y_left, X_right, y_right) = split_data(X, y, feature_idx, threshold)
                if len(y_left) == 0 or len(y_right) == 0:
                    continue
                gain = gini_impurity(y) - (len(y_left) / len(y) * gini_impurity(y_left) + len(y_right) / len(y) * gini_impurity(y_right))
                if gain > best_gain:
                    best_gain = gain
                    best_feature = feature_idx
                    best_threshold = threshold
        return (best_feature, best_threshold, best_gain)

    def build_tree(X, y, depth=0):
        """Recursively build the decision tree."""
        if len(np.unique(y)) == 1 or (max_depth is not None and depth >= max_depth):
            return {'leaf': True, 'value': np.argmax(np.bincount(y))}
        (feature, threshold, gain) = best_split(X, y)
        if gain == 0:
            return {'leaf': True, 'value': np.argmax(np.bincount(y))}
        (X_left, y_left, X_right, y_right) = split_data(X, y, feature, threshold)
        if len(y_left) == 0 or len(y_right) == 0:
            return {'leaf': True, 'value': np.argmax(np.bincount(y))}
        left_child = build_tree(X_left, y_left, depth + 1)
        right_child = build_tree(X_right, y_right, depth + 1)
        return {'leaf': False, 'feature': feature, 'threshold': threshold, 'left': left_child, 'right': right_child}

    def predict_sample(tree, sample):
        """Predict the class of a single sample using the tree."""
        if tree['leaf']:
            return tree['value']
        if sample[tree['feature']] <= tree['threshold']:
            return predict_sample(tree['left'], sample)
        else:
            return predict_sample(tree['right'], sample)
    tree = build_tree(X_train, y_train)
    predictions = [predict_sample(tree, sample) for sample in X_test]
    return predictions

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def validate_vector(vec):
        if not isinstance(vec, (list, tuple, np.ndarray)):
            return False
        if not len(vec):
            return False
        if len(vec) != 1 and (not np.ndim(vec) == 1):
            return False
        try:
            np.array(vec, dtype=float)
        except ValueError:
            return False
        return True

    def euclidean(x, y):
        if not (validate_vector(x) and validate_vector(y)):
            return -1
        if len(x) != len(y):
            return -1
        return round(np.sqrt(np.sum((np.array(x) - np.array(y)) ** 2)), 4)

    def manhattan(x, y):
        if not (validate_vector(x) and validate_vector(y)):
            return -1
        if len(x) != len(y):
            return -1
        return round(np.sum(np.abs(np.array(x) - np.array(y))), 4)

    def chebyshev(x, y):
        if not (validate_vector(x) and validate_vector(y)):
            return -1
        if len(x) != len(y):
            return -1
        return round(np.max(np.abs(np.array(x) - np.array(y))), 4)

    def cosine(x, y):
        if not (validate_vector(x) and validate_vector(y)):
            return -1
        if len(x) != len(y):
            return -1
        x = np.array(x, dtype=float)
        y = np.array(y, dtype=float)
        norm_x = np.linalg.norm(x)
        norm_y = np.linalg.norm(y)
        if norm_x == 0 or norm_y == 0:
            return -1
        dot_product = np.dot(x, y)
        return round(1 - dot_product / (norm_x * norm_y), 4)
    metrics = {'euclidean': euclidean, 'manhattan': manhattan, 'chebyshev': chebyshev, 'cosine': cosine}
    return metrics.get(name, lambda x, y: -1)

import numpy as np
def softsign(z):
    """Compute the Softsign activation function.

    Softsign(z) = z / (1 + |z|)

    The function accepts either a scalar or a list/NumPy array. For a scalar it
    returns a float; for an array-like input it returns a list of floats, with
    every element rounded to 4 decimal places.
    """
    if isinstance(z, (int, float)):
        result = z / (1 + abs(z))
        return round(result, 4)
    elif isinstance(z, (list, np.ndarray)):
        if len(z) == 0:
            return []
        z_array = np.array(z, dtype=float)
        result = z_array / (1 + np.abs(z_array))
        return [round(val, 4) for val in result]
    else:
        raise TypeError('Input must be a scalar, list, or NumPy array.')

import numpy as np
def one_hot_encode(labels: list[str], categories: list[str] | None=None) -> list[list[int]]:
    """Convert categorical labels to a one-hot encoded matrix.

    Args:
        labels: A list of categorical labels of length *N*.
        categories: Optional complete list of unique categories of length *C*.
            If *None*, the set of categories is inferred from *labels* in the
            order of their first appearance.

    Returns:
        A list of lists that represents the one-hot encoded matrix of shape
        (N, C) where *C* is the number of categories. If an unknown label is
        found when *categories* is supplied, the function returns -1.
    """
    if categories is None:
        seen = set()
        categories = []
        for label in labels:
            if label not in seen:
                seen.add(label)
                categories.append(label)
    else:
        for label in labels:
            if label not in categories:
                return -1
    category_to_index = {category: idx for (idx, category) in enumerate(categories)}
    n_samples = len(labels)
    n_categories = len(categories)
    one_hot = np.zeros((n_samples, n_categories), dtype=int)
    for (i, label) in enumerate(labels):
        one_hot[i, category_to_index[label]] = 1
    return one_hot.tolist()

import functools
from typing import Callable, Any, ParamSpec
P = ParamSpec('P')
def doublewrap(decorator_func: Callable[..., Callable[..., Any]]) -> Callable[..., Any]:
    """Return a decorator that can be used *with* or *without* parentheses.

    Args:
        decorator_func: A callable that expects the function to decorate as
            its first positional argument followed only by *optional*
            positional or keyword arguments.

    Returns:
        A new decorator that forwards the call in the correct form so that
        ``decorator_func`` can be applied either directly or after receiving
        its own optional parameters.
    """

    @functools.wraps(decorator_func)
    def wrapper(*args, **kwargs):
        if len(args) > 0 and callable(args[0]):
            return decorator_func(*args, **kwargs)
        else:

            def real_decorator(func: Callable[P, Any]) -> Callable[P, Any]:
                return decorator_func(func, *args, **kwargs)
            return real_decorator
    return wrapper

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def hand_value(hand):
        """Calculate the value of a hand considering the usable ace rule."""
        total = sum(hand)
        aces = hand.count(1)
        for _ in range(aces):
            if total + 10 <= 21:
                total += 10
        return total
    player_score = hand_value(player)
    dealer_score = hand_value(dealer)
    player_natural = len(player) == 2 and sorted(player) == [1, 10]
    dealer_natural = len(dealer) == 2 and sorted(dealer) == [1, 10]
    if player_score > 21:
        return -1.0
    if dealer_score > 21:
        return 1.0
    if player_score > dealer_score:
        if player_natural:
            return 1.5
        else:
            return 1.0
    elif player_score < dealer_score:
        return -1.0
    else:
        return 0.0

