import numpy as np
def random_binary_tensor(shape: tuple, sparsity: float=0.5) -> list:
    """Generate a random binary tensor.

    The function should create a tensor of the requested ``shape`` such that
    each entry independently takes the value ``1.0`` with probability equal
    to ``sparsity`` and ``0.0`` otherwise.

    The result **must** be returned as a regular (nested) Python list obtained
    via NumPy’s ``tolist`` method.  If either ``shape`` or ``sparsity`` is
    illegal (see task description) the function must return **-1**.
    """
    if not isinstance(shape, tuple) or len(shape) == 0:
        return -1
    for dim in shape:
        if not isinstance(dim, int) or dim <= 0:
            return -1
    if not isinstance(sparsity, (int, float)) or not 0.0 <= sparsity <= 1.0:
        return -1
    random_values = np.random.rand(*shape)
    binary_tensor = (random_values < sparsity).astype(float)
    return binary_tensor.tolist()

import numpy as np
from typing import list, tuple
def gaussian_bandit_oracle(payoff_dists: list[tuple[float, float]], payoff_probs: list[float]) -> tuple[float, int] | int:
    """Determine the arm with the highest expected reward in a Gaussian bandit.

    The function must compute the expected reward for each arm (p_k * μ_k) and
    return a tuple (best_ev, best_arm), where best_ev is rounded to 4 decimal
    places and best_arm is the index of the arm with that expected reward.  If
    the input is invalid (see task description) the function should return -1.
    """
    if len(payoff_dists) != len(payoff_probs):
        return -1
    if len(payoff_dists) == 0:
        return -1
    for ((mu, sigma2), p) in zip(payoff_dists, payoff_probs):
        if sigma2 <= 0 or not 0 <= p <= 1:
            return -1
    expected_rewards = np.array([p * mu for ((mu, _), p) in zip(payoff_dists, payoff_probs)])
    best_ev = np.max(expected_rewards)
    best_arm = np.argmin(np.where(expected_rewards == best_ev, np.arange(len(expected_rewards)), np.inf))
    return (round(best_ev, 4), best_arm)

import numpy as np
def sigmoid(x):
    """Element-wise logistic sigmoid."""
    x = np.array(x)
    result = 1 / (1 + np.exp(-x))
    return np.round(result, 4)
def tanh(x):
    """Element-wise hyperbolic tangent."""
    x = np.array(x)
    result = np.tanh(x)
    return np.round(result, 4)
def relu(x):
    """Element-wise Rectified Linear Unit."""
    x = np.array(x)
    result = np.maximum(0, x)
    return np.round(result, 4)
def leaky_relu(x, alpha: float=0.01):
    """Element-wise Leaky-ReLU with slope *alpha* for x<0."""
    x = np.array(x)
    result = np.where(x > 0, x, alpha * x)
    return np.round(result, 4)
def softmax(x):
    """Numerically stable softmax over a 1-D input."""
    x = np.array(x)
    exps = np.exp(x - np.max(x))
    result = exps / np.sum(exps)
    return np.round(result, 4)
def get_activation(name):
    """Return the activation function by *name*."""
    if name == 'sigmoid':
        return sigmoid
    elif name == 'tanh':
        return tanh
    elif name == 'relu':
        return relu
    elif name == 'leaky_relu':
        return leaky_relu
    elif name == 'softmax':
        return softmax
    else:
        raise ValueError('Invalid activation function.')

