def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:
    """
    Builds a decision tree using entropy and information gain.

    Args:
        examples (List[Dict]): List of example dictionaries with attribute-value pairs.
        attributes (List[str]): List of attribute names to consider for splitting.
        target_attr (str): Name of the target attribute (class label).

    Returns:
        Dict: A nested dictionary representing the decision tree.
    """

    def entropy(class_counts):
        """Calculate entropy from class counts."""
        total = sum(class_counts)
        return -sum((count / total * math.log2(count / total) for count in class_counts if count > 0))

    def get_class_distribution(examples):
        """Get distribution of class labels in the examples."""
        return [example[target_attr] for example in examples]

    def info_gain(parent_dist, left_dist, right_dist):
        """Calculate information gain from splitting into two subsets."""
        total = sum(parent_dist)
        left_total = sum(left_dist)
        right_total = sum(right_dist)
        return entropy(parent_dist) - (left_total / total * entropy(left_dist) + right_total / total * entropy(right_dist))

    def all_same_class(examples):
        """Check if all examples have the same class label."""
        first_class = examples[0][target_attr]
        return all((example[target_attr] == first_class for example in examples))

    def majority_vote(examples):
        """Return the most common class in the examples."""
        class_counts = Counter(get_class_distribution(examples))
        return class_counts.most_common(1)[0][0]

    def build_tree(examples, attributes):
        if not examples:
            return majority_vote(all_examples)
        if all_same_class(examples):
            return examples[0][target_attr]
        if not attributes:
            return majority_vote(examples)
        best_attr = None
        max_gain = -float('inf')
        parent_dist = Counter(get_class_distribution(examples))
        for attr in attributes:
            if attr == target_attr:
                continue
            values = set((example[attr] for example in examples))
            left = [ex for ex in examples if ex[attr] == next(iter(values))]
            right = [ex for ex in examples if ex[attr] != next(iter(values))]
            gain = info_gain(list(parent_dist.values()), [Counter(get_class_distribution(left))[v] for v in parent_dist], [Counter(get_class_distribution(right))[v] for v in parent_dist])
            if gain > max_gain:
                max_gain = gain
                best_attr = attr
        if best_attr is None:
            return majority_vote(examples)
        remaining_attrs = [a for a in attributes if a != best_attr]
        tree = {best_attr: {}}
        values = set((example[best_attr] for example in examples))
        for val in values:
            subset = [ex for ex in examples if ex[best_attr] == val]
            subtree = build_tree(subset, remaining_attrs)
            tree[best_attr][val] = subtree
        return tree
    all_examples = examples.copy()
    return build_tree(examples, attributes)
