def calculate_brightness(img):
    if not img or not img[0]:
        return -1
    num_rows = len(img)
    num_cols = len(img[0])
    for row in img:
        if len(row) != num_cols:
            return -1
        for pixel in row:
            if not isinstance(pixel, int) or not 0 <= pixel <= 255:
                return -1
    total_brightness = sum((sum(row) for row in img))
    total_pixels = num_rows * num_cols
    average_brightness = total_brightness / total_pixels
    return round(average_brightness, 2)
