def performance_metrics(actual: list[int], predicted: list[int]) -> tuple:
    if len(actual) != len(predicted):
        raise ValueError("The lengths of 'actual' and 'predicted' lists must be equal.")
    for label in actual + predicted:
        if label not in {0, 1}:
            raise ValueError("All elements in 'actual' and 'predicted' must be either 0 or 1.")
    TP = FP = TN = FN = 0
    for (a, p) in zip(actual, predicted):
        if a == 1 and p == 1:
            TP += 1
        elif a == 0 and p == 1:
            FP += 1
        elif a == 0 and p == 0:
            TN += 1
        elif a == 1 and p == 0:
            FN += 1
    confusion_matrix = [[TN, FP], [FN, TP]]
    total = TP + FP + TN + FN
    accuracy = round((TP + TN) / total, 3) if total > 0 else 0.0
    precision = round(TP / (TP + FP), 3) if TP + FP > 0 else 0.0
    recall = round(TP / (TP + FN), 3) if TP + FN > 0 else 0.0
    f1_score = round(2 * (precision * recall) / (precision + recall), 3) if precision + recall > 0 else 0.0
    specificity = round(TN / (TN + FP), 3) if TN + FP > 0 else 0.0
    negative_predictive_value = round(TN / (TN + FN), 3) if TN + FN > 0 else 0.0
    return (confusion_matrix, accuracy, f1_score, specificity, negative_predictive_value)
