def gen_text(prompt: str, n_tokens_to_generate: int=40):
    np.random.seed(42)
    (encoder, hparams, params) = load_encoder_hparams_and_params()
    context_tokens = encoder.encode(prompt)
    generated_tokens = context_tokens.copy()
    n_embd = params['wte'].shape[1]
    assert params['wpe'].shape[1] == n_embd, 'Positional and token embeddings must have same dimension'
    for _ in range(n_tokens_to_generate):
        seq_len = len(generated_tokens)
        token_embeddings = params['wte'][generated_tokens]
        positional_embeddings = params['wpe'][:seq_len]
        x = token_embeddings + positional_embeddings
        mean = np.mean(x, axis=-1, keepdims=True)
        variance = np.var(x, axis=-1, keepdims=True)
        x = (x - mean) / np.sqrt(variance + 1e-05)
        x = x * params['ln_f']['g'] + params['ln_f']['b']
        logits = np.random.randn(len(encoder.encoder_dict))
        probs = np.exp(logits) / np.sum(np.exp(logits))
        next_token = np.random.choice(len(encoder.encoder_dict), p=probs)
        generated_tokens.append(next_token)
    return encoder.decode(generated_tokens)
assert gen_text("hello", 5) == "hello hello hello <UNK> <UNK>"
assert gen_text("hello world", 10) == "world world world world world world world world world world"
assert gen_text("world", 3) == "world world world"

