from collections import Counter, defaultdict
class FPNODE:

    def __init__(self, name: str, count: int, parent):
        self.name = name
        self.count = count
        self.parent = parent
        self.children = {}
        self.node_link = None
class FPTree:

    def __init__(self):
        self.root = FPNODE('Null', 1, None)
        self.header_table = {}

    def add_transaction(self, transaction: list[str], item_counts: dict[str, int]):
        """Add a transaction to the FP-Tree."""
        current_node = self.root
        sorted_items = sorted([item for item in transaction if item in item_counts and item_counts[item] >= min_support], key=lambda x: (item_counts[x], x), reverse=True)
        for item in sorted_items:
            if item in current_node.children:
                current_node.children[item].count += 1
                current_node = current_node.children[item]
            else:
                new_node = FPNODE(item, 1, current_node)
                current_node.children[item] = new_node
                current_node = new_node
                if item not in self.header_table:
                    self.header_table[item] = [new_node]
                else:
                    self.header_table[item].append(new_node)
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth."""
    if not transactions:
        return []
    item_counts = Counter()
    for transaction in transactions:
        for item in transaction:
            item_counts[item] += 1
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_support}
    if not frequent_items:
        return []
    tree = FPTree()
    for transaction in transactions:
        filtered_transaction = [item for item in transaction if item in frequent_items]
        if filtered_transaction:
            tree.add_transaction(filtered_transaction, item_counts)
    frequent_itemsets = []

    def get_conditional_pattern_base(item: str) -> list[tuple[list[str], int]]:
        """Get conditional pattern base for an item."""
        pattern_base = []
        for node in tree.header_table[item]:
            prefix_path = []
            current_node = node.parent
            while current_node.name != 'Null':
                prefix_path.append((current_node.name, current_node.count))
                current_node = current_node.parent
            unique_prefix_path = []
            seen_items = set()
            for (item_name, count) in reversed(prefix_path):
                if item_name not in seen_items:
                    seen_items.add(item_name)
                    unique_prefix_path.append((item_name, count))
            unique_prefix_path.reverse()
            pattern_base.append((unique_prefix_path, node.count))
        return pattern_base

    def mine_tree(header_table: dict, min_support: int, current_prefix: list[str], frequent_itemsets: list[list[str]]):
        """Recursively mine the FP-Tree."""
        sorted_items = sorted(header_table.keys(), key=lambda x: (len(x), x))
        for item in sorted_items:
            new_prefix = sorted(current_prefix + [item])
            frequent_itemsets.append(new_prefix)
            conditional_pattern_base = get_conditional_pattern_base(item)
            conditional_counts = Counter()
            for (path, count) in conditional_pattern_base:
                for (item_name, _) in path:
                    conditional_counts[item_name] += count
            conditional_frequent_items = {item_name for (item_name, count) in conditional_counts.items() if count >= min_support}
            if conditional_frequent_items:
                conditional_tree = FPTree()
                for (path, count) in conditional_pattern_base:
                    filtered_path = [(name, count) for (name, _) in path if name in conditional_frequent_items]
                    if filtered_path:
                        transaction = [name for (name, _) in filtered_path]
                        current_node = conditional_tree.root
                        for (name, _) in filtered_path:
                            if name in current_node.children:
                                current_node.children[name].count += count
                                current_node = current_node.children[name]
                            else:
                                new_node = FPNODE(name, count, current_node)
                                current_node.children[name] = new_node
                                current_node = new_node
                                if name not in conditional_tree.header_table:
                                    conditional_tree.header_table[name] = [new_node]
                                else:
                                    conditional_tree.header_table[name].append(new_node)
                mine_tree(conditional_tree.header_table, min_support, new_prefix, frequent_itemsets)
    mine_tree(tree.header_table, min_support, [], frequent_itemsets)
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets
