import numpy as np
def isolation_tree_path_length(data: np.ndarray) -> list[int]:
    np.random.seed(0)
    (n_samples, n_features) = data.shape

    def build_tree(indices):
        if len(indices) < 3:
            return {'type': 'leaf', 'indices': indices}
        f = np.random.randint(0, n_features)
        values = data[indices, f]
        down = np.min(values)
        up = np.max(values)
        v = np.random.uniform(down, up)
        left_indices = []
        right_indices = []
        for idx in indices:
            if data[idx, f] <= v:
                left_indices.append(idx)
            else:
                right_indices.append(idx)
        left_child = build_tree(left_indices)
        right_child = build_tree(right_indices)
        return {'type': 'internal', 'feature': f, 'value': v, 'left': left_child, 'right': right_child}
    root = build_tree(list(range(n_samples)))

    def get_depth(sample_index):
        current_node = root
        depth = 0
        while True:
            if current_node['type'] == 'leaf':
                return depth
            f = current_node['feature']
            v = current_node['value']
            if data[sample_index, f] <= v:
                current_node = current_node['left']
            else:
                current_node = current_node['right']
            depth += 1
    return [get_depth(i) for i in range(n_samples)]

def check_data(a, b):
    """Validate and convert the two inputs so that both are numeric.

    The function must accept integers, floats, or numeric strings.  Any string
    should be converted with `float()`; existing numeric values stay as they
    are.  The pair `(a, b)` must then be returned.
    """
    a_converted = a if not isinstance(a, str) else float(a)
    b_converted = b if not isinstance(b, str) else float(b)
    return (a_converted, b_converted)
def validate_input(function):

    def wrapper(a, b):
        (a, b) = check_data(a, b)
        return function(a, b)
    return wrapper
@validate_input
def sum_numbers(a, b):
    """Return the arithmetic sum of *a* and *b* after validation."""
    result = a + b
    if isinstance(result, float) and result.is_integer():
        return int(result)
    return result

from typing import List, Union
Number = Union[int, float]
def k_smallest(nums: List[Number], k: int) -> List[Number]:
    """Return the k smallest numbers contained in *nums*.

    The function should scan the input list once, maintaining a custom
    max-heap (size at most k) implemented with a Python list.  At the end,
    return the k smallest elements in ascending order.  If *k* is invalid
    (≤0 or larger than len(nums)) return -1.

    The use of the built-in ``heapq`` module is **not** allowed.
    """
    if k <= 0 or k > len(nums):
        return -1
    heap = []

    def _move_up(i: int):
        while i > 0:
            parent = (i - 1) // 2
            if heap[i] > heap[parent]:
                (heap[i], heap[parent]) = (heap[parent], heap[i])
                i = parent
            else:
                break

    def _move_down(i: int):
        while True:
            left = 2 * i + 1
            if left >= len(heap):
                break
            right = 2 * i + 2
            max_child = left
            if right < len(heap) and heap[right] > heap[left]:
                max_child = right
            if heap[i] < heap[max_child]:
                (heap[i], heap[max_child]) = (heap[max_child], heap[i])
                i = max_child
            else:
                break
    for num in nums:
        if len(heap) < k:
            heap.append(num)
            _move_up(len(heap) - 1)
        elif num < heap[0]:
            heap[0] = num
            _move_down(0)
    heap.sort()
    return heap

import numpy as np
def softplus(x, deriv: bool=False):
    """
    Compute the SoftPlus activation function or its derivative (sigmoid).

    Parameters:
    - x: scalar, list, or numpy array of real numbers.
    - deriv: bool, if True, returns the derivative (sigmoid) of SoftPlus.

    Returns:
    - A float or list of floats, rounded to 4 decimal places.
    """
    is_scalar_input = isinstance(x, (int, float))
    x_arr = np.asarray(x, dtype=np.float64)
    if deriv:
        result = 1.0 / (1.0 + np.exp(-x_arr))
    else:
        result = np.maximum(x_arr, 0) + np.log1p(np.exp(-np.abs(x_arr)))
    result = np.round(result, 4)
    if is_scalar_input and x_arr.size == 1:
        return float(result.item())
    else:
        return result.tolist()

import numpy as np
def compute_impurity(y, criterion):
    """
    Compute a node's impurity based on the specified criterion.

    Args:
        y (numpy.ndarray): 1-D array with the target values.
        criterion (str): One of {"entropy", "gini", "mse"}.

    Returns:
        float: Impurity value rounded to 4 decimal places.

    Raises:
        ValueError: If the criterion is not one of the allowed values.
    """
    if len(y) == 0:
        return 0.0
    if criterion not in {'entropy', 'gini', 'mse'}:
        raise ValueError("Invalid criterion. Must be one of 'entropy', 'gini', or 'mse'.")
    if criterion == 'mse':
        impurity = np.var(y, ddof=0)
    else:
        (unique, counts) = np.unique(y, return_counts=True)
        probabilities = counts / len(y)
        if criterion == 'entropy':
            impurity = -np.sum(probabilities * np.log2(probabilities))
        else:
            impurity = 1 - np.sum(probabilities ** 2)
    return round(impurity, 4)

def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """
    Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]): Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or −1 on invalid input.
    """
    if not observations:
        return -1
    if not S or not A or (not B):
        return -1
    n_S = len(S)
    n_A = len(A)
    if n_S != n_A:
        return -1
    n_A_cols = len(A[0]) if A else 0
    for row in A:
        if len(row) != n_A_cols or len(row) != n_A:
            return -1
    n_B_rows = len(B)
    if n_B_rows != n_S:
        return -1
    if not B or not B[0]:
        return -1
    m = len(B[0])
    for row in B:
        if len(row) != m:
            return -1
    for obs in observations:
        if obs < 0 or obs >= m:
            return -1
    n = n_S
    T = len(observations)
    prev_alpha = [0.0] * n
    first_obs = observations[0]
    for i in range(n):
        prev_alpha[i] = S[i] * B[i][first_obs]
    for t in range(1, T):
        current_obs = observations[t]
        new_alpha = [0.0] * n
        for i in range(n):
            total = 0.0
            for j in range(n):
                total += prev_alpha[j] * A[j][i]
            new_alpha[i] = total * B[i][current_obs]
        prev_alpha = new_alpha
    total_prob = sum(prev_alpha)
    return round(total_prob, 4)

from collections import Counter, defaultdict
class FPNODE:

    def __init__(self, name: str, count: int, parent):
        self.name = name
        self.count = count
        self.parent = parent
        self.children = {}
        self.node_link = None
class FPTree:

    def __init__(self):
        self.root = FPNODE('Null', 1, None)
        self.header_table = {}

    def add_transaction(self, transaction: list[str], item_counts: dict[str, int]):
        """Add a transaction to the FP-Tree."""
        current_node = self.root
        sorted_items = sorted([item for item in transaction if item in item_counts and item_counts[item] >= min_support], key=lambda x: (item_counts[x], x), reverse=True)
        for item in sorted_items:
            if item in current_node.children:
                current_node.children[item].count += 1
                current_node = current_node.children[item]
            else:
                new_node = FPNODE(item, 1, current_node)
                current_node.children[item] = new_node
                current_node = new_node
                if item not in self.header_table:
                    self.header_table[item] = [new_node]
                else:
                    self.header_table[item].append(new_node)
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth."""
    if not transactions:
        return []
    item_counts = Counter()
    for transaction in transactions:
        for item in transaction:
            item_counts[item] += 1
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_support}
    if not frequent_items:
        return []
    tree = FPTree()
    for transaction in transactions:
        filtered_transaction = [item for item in transaction if item in frequent_items]
        if filtered_transaction:
            tree.add_transaction(filtered_transaction, item_counts)
    frequent_itemsets = []

    def get_conditional_pattern_base(item: str) -> list[tuple[list[str], int]]:
        """Get conditional pattern base for an item."""
        pattern_base = []
        for node in tree.header_table[item]:
            prefix_path = []
            current_node = node.parent
            while current_node.name != 'Null':
                prefix_path.append((current_node.name, current_node.count))
                current_node = current_node.parent
            unique_prefix_path = []
            seen_items = set()
            for (item_name, count) in reversed(prefix_path):
                if item_name not in seen_items:
                    seen_items.add(item_name)
                    unique_prefix_path.append((item_name, count))
            unique_prefix_path.reverse()
            pattern_base.append((unique_prefix_path, node.count))
        return pattern_base

    def mine_tree(header_table: dict, min_support: int, current_prefix: list[str], frequent_itemsets: list[list[str]]):
        """Recursively mine the FP-Tree."""
        sorted_items = sorted(header_table.keys(), key=lambda x: (len(x), x))
        for item in sorted_items:
            new_prefix = sorted(current_prefix + [item])
            frequent_itemsets.append(new_prefix)
            conditional_pattern_base = get_conditional_pattern_base(item)
            conditional_counts = Counter()
            for (path, count) in conditional_pattern_base:
                for (item_name, _) in path:
                    conditional_counts[item_name] += count
            conditional_frequent_items = {item_name for (item_name, count) in conditional_counts.items() if count >= min_support}
            if conditional_frequent_items:
                conditional_tree = FPTree()
                for (path, count) in conditional_pattern_base:
                    filtered_path = [(name, count) for (name, _) in path if name in conditional_frequent_items]
                    if filtered_path:
                        transaction = [name for (name, _) in filtered_path]
                        current_node = conditional_tree.root
                        for (name, _) in filtered_path:
                            if name in current_node.children:
                                current_node.children[name].count += count
                                current_node = current_node.children[name]
                            else:
                                new_node = FPNODE(name, count, current_node)
                                current_node.children[name] = new_node
                                current_node = new_node
                                if name not in conditional_tree.header_table:
                                    conditional_tree.header_table[name] = [new_node]
                                else:
                                    conditional_tree.header_table[name].append(new_node)
                mine_tree(conditional_tree.header_table, min_support, new_prefix, frequent_itemsets)
    mine_tree(tree.header_table, min_support, [], frequent_itemsets)
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets

import numpy as np
def drelu(Z):
    """Return the element-wise derivative of the ReLU activation.

    Parameters
    ----------
    Z : int | float | list | numpy.ndarray
        Input data that can be a scalar, list (any depth), or ndarray.

    Returns
    -------
    float | list
        Derivative of ReLU with the same shape layout as *Z* (float if *Z* is a scalar).
    """
    if np.isscalar(Z):
        return 1.0 if Z > 0 else 0.0
    elif isinstance(Z, list):
        return [drelu(sub_z) for sub_z in Z]
    elif isinstance(Z, np.ndarray):
        return (Z > 0).astype(float).tolist()
    else:
        raise ValueError('Input must be a scalar, list, or numpy ndarray.')

import numpy as np
def relu_(Z):
    """Apply the element-wise Rectified Linear Unit (ReLU) activation.

    The function keeps the container type of the input:
    scalar → scalar, list → list, NumPy array → NumPy array.

    Args:
        Z (int | float | list | numpy.ndarray): Input data – scalar, 1-D or 2-D.

    Returns:
        Same type as *Z* with all negative values clipped to 0.
    """
    if isinstance(Z, (int, float)):
        return max(0, Z)
    elif isinstance(Z, list):
        arr = np.array(Z)
        processed = np.maximum(arr, 0)
        return processed.tolist()
    elif isinstance(Z, np.ndarray):
        return np.maximum(Z, 0)
    else:
        raise TypeError('Input must be a scalar, list, or NumPy array.')

