        return np.array(z)

def best_arm(payoff_probs: list[float]) -> tuple[float, int]:
    """Find the arm with the highest expected reward in a Bernoulli bandit.

    Parameters
    ----------
    payoff_probs : list[float]
        A list where each element is the success probability of the
        corresponding arm. Probabilities must lie in the interval [0, 1].

    Returns
    -------
    tuple
        (max_expected_reward, best_arm_index).  If the input list is empty or
        contains an invalid probability (<0 or >1), the function must return
        (-1.0, -1).
    """
    if not payoff_probs:
        return (-1.0, -1)
    for p in payoff_probs:
        if not 0.0 <= p <= 1.0:
            return (-1.0, -1)
    max_prob = payoff_probs[0]
    best_index = 0
    for i in range(1, len(payoff_probs)):
        if payoff_probs[i] > max_prob:
            max_prob = payoff_probs[i]
            best_index = i
    return (max_prob, best_index)

from collections import deque
import math
def escape_fire_maze(grid: list[str]) -> int:
    n = len(grid)
    if n == 1:
        return 0 if grid[0][0] == '.' else -1
    fire_time = [[math.inf] * n for _ in range(n)]
    fire_queue = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_time[i][j] = 0
                fire_queue.append((i, j))
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    while fire_queue:
        (x, y) = fire_queue.popleft()
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (fire_time[nx][ny] == math.inf):
                fire_time[nx][ny] = fire_time[x][y] + 1
                fire_queue.append((nx, ny))
    if fire_time[0][0] == 0 or fire_time[n - 1][n - 1] == 0:
        return -1
    agent_time = [[math.inf] * n for _ in range(n)]
    agent_queue = deque()
    agent_queue.append((0, 0))
    agent_time[0][0] = 0
    while agent_queue:
        (x, y) = agent_queue.popleft()
        current_time = agent_time[x][y]
        if x == n - 1 and y == n - 1:
            return current_time
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (grid[nx][ny] != '#'):
                arrival_time = current_time + 1
                if arrival_time < fire_time[nx][ny] and agent_time[nx][ny] > arrival_time:
                    agent_time[nx][ny] = arrival_time
                    agent_queue.append((nx, ny))
    return -1

import numpy as np
def tanh_grad(z):
    """
    Derivative of the hyper-bolic tangent (tanh) activation function.

    The derivative is computed element-wise as 1 - tanh(z)**2.

    Args:
        z: A scalar, Python list or NumPy ndarray of floats/ints.

    Returns:
        float | list[float]: If `z` is scalar, a float is returned, otherwise
        a Python list with each element rounded to 4 decimals.
    """
    z_arr = np.asarray(z, dtype=float)
    grad = 1 - np.tanh(z_arr) ** 2
    rounded = np.round(grad, 4)
    if isinstance(z, (int, float)):
        return float(rounded)
    elif rounded.ndim == 0:
        return [rounded.tolist()]
    else:
        return rounded.tolist()

import numpy as np
def metric(name: str):
    """
    Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """
    supported = {'euclidean', 'manhattan', 'chebyshev', 'cosine'}

    def invalid_func(x, y):
        return -1.0
    if name not in supported:
        return invalid_func

    def distance_function(x, y):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return -1.0
        try:
            x_arr = np.array(x, dtype=float)
            y_arr = np.array(y, dtype=float)
        except (TypeError, ValueError):
            return -1.0
        if x_arr.ndim != 1 or y_arr.ndim != 1:
            return -1.0
        if x_arr.size == 0 or y_arr.size == 0:
            return -1.0
        if x_arr.shape[0] != y_arr.shape[0]:
            return -1.0
        if name == 'cosine':
            x_norm = np.linalg.norm(x_arr)
            y_norm = np.linalg.norm(y_arr)
            if x_norm == 0 or y_norm == 0:
                return -1.0
        if name == 'euclidean':
            dist = np.sqrt(np.sum((x_arr - y_arr) ** 2))
        elif name == 'manhattan':
            dist = np.sum(np.abs(x_arr - y_arr))
        elif name == 'chebyshev':
            dist = np.max(np.abs(x_arr - y_arr))
        elif name == 'cosine':
            dot_product = x_arr.dot(y_arr)
            x_norm = np.linalg.norm(x_arr)
            y_norm = np.linalg.norm(y_arr)
            dist = 1 - dot_product / (x_norm * y_norm)
        else:
            return -1.0
        return round(dist, 4)
    return distance_function

import numpy as np
def compute_l1_penalty(weights, C):
    """
    Compute the L1 regularization penalty.

    Args:
        weights: Sequence or np.ndarray containing numeric weights (any shape).
        C: Regularization strength (non-negative float).

    Returns:
        L1 penalty (float) rounded to 4 decimal places.
    """

    def get_sum_abs(element):
        """
        Recursively computes the sum of absolute values of all elements in a nested structure.
        """
        if isinstance(element, np.ndarray):
            return np.sum(np.abs(element))
        elif isinstance(element, (list, tuple)):
            total = 0.0
            for item in element:
                total += get_sum_abs(item)
            return total
        else:
            return abs(element)
    total_sum = get_sum_abs(weights)
    penalty = C * total_sum
    return round(penalty, 4)

import numpy as np
import math
def num_missing(data) -> int:
    """Count missing values in a 1-D iterable.

    An element is missing if it is ``None`` or ``NaN``.

    Args:
        data: Any 1-D iterable (list, tuple, NumPy array, etc.).

    Returns:
        int: The number of missing elements.
    """
    count = 0
    for x in data:
        if x is None:
            count += 1
        else:
            try:
                if np.isnan(x):
                    count += 1
            except:
                pass
    return count

def one_hot_encode(labels: list[str], categories: list[str] | None=None) -> list[list[int]]:
    """
    Convert categorical labels to a one-hot encoded matrix.

    Args:
        labels: A list of categorical labels of length *N*.
        categories: Optional complete list of unique categories of length *C*.
            If *None*, the set of categories is inferred from *labels* in the
            order of their first appearance.

    Returns:
        A list of lists that represents the one-hot encoded matrix of shape
        (N, C) where *C* is the number of categories. If an unknown label is
        found when *categories* is supplied, the function returns -1.
    """
    if categories is None:
        categories = []
        for label in labels:
            if label not in categories:
                categories.append(label)
    else:
        category_set = set(categories)
        for label in labels:
            if label not in category_set:
                return -1
    category_to_index = {cat: idx for (idx, cat) in enumerate(categories)}
    n = len(labels)
    c = len(categories)
    matrix = np.zeros((n, c), dtype=int)
    for (i, label) in enumerate(labels):
        matrix[i, category_to_index[label]] = 1
    return matrix.tolist()

import functools
from typing import Callable, Any
def doublewrap(decorator_func: Callable[..., Callable[..., Any]]) -> Callable[..., Any]:
    """Return a decorator that can be used *with* or *without* parentheses.

    Args:
        decorator_func: A callable that expects the function to decorate as
            its first positional argument followed only by *optional*
            positional or keyword arguments.

    Returns:
        A new decorator that forwards the call in the correct form so that
        ``decorator_func`` can be applied either directly or after receiving
        its own optional parameters.
    """

    @functools.wraps(decorator_func)
    def wrapper(*args, **kwargs):
        if len(args) == 1 and (not kwargs) and callable(args[0]):
            return decorator_func(args[0])
        else:

            def real_decorator(func):
                return decorator_func(func, *args, **kwargs)
            return real_decorator
    return wrapper

import numpy as np
def leaky_relu(z, a=0.01):
    """Apply the Leaky ReLU activation to every element in *z*.

    Args:
        z: A scalar, list (possibly nested) or ``numpy.ndarray`` of numbers.
        a: Optional float in [0,1) — the slope for negative inputs. Defaults to 0.01.

    Returns:
        A Python list with the same structure as *z* where each value has been transformed
        by the Leaky ReLU activation.
    """
    if isinstance(z, np.ndarray):
        return np.where(z >= 0, z, a * z).tolist()
    elif isinstance(z, list):
        return [leaky_relu(element, a) for element in z]
    else:
        return z if z >= 0 else a * z

import numpy as np
def value_network_forward(state: list[float], W1: list[list[float]], W2: list[list[float]], W3: list[list[float]] | list[float]) -> float:
    """
    Forward pass of a 2-hidden-layer value network using tanh activations.

    Parameters
    ----------
    state : list[float]
        1-D state vector of length N.
    W1    : list[list[float]]
        Weight matrix with shape (N, H1).
    W2    : list[list[float]]
        Weight matrix with shape (H1, H2).
    W3    : list[list[float]] | list[float]
        Weight matrix with shape (H2, 1) or (H2,) for the output layer.

    Returns
    -------
    float
        The scalar state-value rounded to 4 decimals, or −1 on shape error.
    """
    N = len(state)
    if len(W1) != N:
        return -1
    if not W1:
        return -1
    if not isinstance(W1[0], list):
        return -1
    H1 = len(W1[0])
    for row in W1:
        if not isinstance(row, list) or len(row) != H1:
            return -1
    if len(W2) != H1:
        return -1
    if not W2:
        return -1
    if not isinstance(W2[0], list):
        return -1
    H2 = len(W2[0])
    for row in W2:
        if not isinstance(row, list) or len(row) != H2:
            return -1
    if not isinstance(W3, list):
        return -1
    all_lists = all((isinstance(x, list) for x in W3))
    if all_lists:
        if len(W3) != H2:
            return -1
        for row in W3:
            if len(row) != 1:
                return -1
    else:
        for x in W3:
            if isinstance(x, list):
                return -1
        if len(W3) != H2:
            return -1
    s = np.array(state)
    W1_np = np.array(W1)
    W2_np = np.array(W2)
    W3_np = np.array(W3)
    h1 = np.tanh(s @ W1_np)
    h2 = np.tanh(h1 @ W2_np)
    v = h2 @ W3_np
    return float(np.round(v, 4))

def is_natural(hand: list[int]) -> bool:
    """Returns True if the hand contains exactly an Ace (1) and a ten-valued card (10)."""
    return len(hand) == 2 and set(hand) == {1, 10}

from typing import List
def score(hand: List[str]) -> int:
    """Return the Blackjack score of *hand* or 0 if the hand is bust.

    Args:
        hand: A list of card labels ('2'-'10', 'J', 'Q', 'K', 'A').

    Returns:
        An integer between 0 and 21 (inclusive).  If every possible value of
        the hand exceeds 21, the function returns 0.
    """
    base = 0
    aces = 0
    for card in hand:
        if card == 'A':
            aces += 1
        elif card in {'J', 'Q', 'K'}:
            base += 10
        else:
            base += int(card)
    for i in range(aces, -1, -1):
        total = base + aces + 10 * i
        if total <= 21:
            return total
    return 0

def create_encoding_dicts(obs_ids: list, action_ids: list, obs_encoder: dict | None=None, act_encoder: dict | None=None) -> dict:
    """
    Generate forward and backward mappings for observations and actions.

    Args:
        obs_ids (list): All distinct observation identifiers.
        action_ids (list): All distinct action identifiers.
        obs_encoder (dict | None, optional): Existing obs→number mapping.
        act_encoder (dict | None, optional): Existing act→number mapping.

    Returns:
        dict: A dictionary that contains the keys 'obs2num', 'num2obs',
              'act2num', and 'num2act'.
    """
    if obs_encoder is not None:
        obs2num = obs_encoder
    else:
        obs2num = {obs: idx for (idx, obs) in enumerate(obs_ids)}
    num2obs = {v: k for (k, v) in obs2num.items()}
    if act_encoder is not None:
        act2num = act_encoder
    else:
        act2num = {act: idx for (idx, act) in enumerate(action_ids)}
    num2act = {v: k for (k, v) in act2num.items()}
    return {'obs2num': obs2num, 'num2obs': num2obs, 'act2num': act2num, 'num2act': num2act}

import numpy as np
def sigmoid(x):
    """Element-wise logistic sigmoid function."""
    if isinstance(x, (int, float)):
        return round(1 / (1 + np.exp(-x)), 4)
    x_arr = np.asarray(x)
    result = 1 / (1 + np.exp(-x_arr))
    rounded = np.round(result, 4)
    if isinstance(x, list):
        return rounded.tolist()
    elif isinstance(x, tuple):
        return tuple(rounded.tolist())
    else:
        return rounded
def tanh(x):
    """Element-wise hyperbolic tangent function."""
    if isinstance(x, (int, float)):
        return round(np.tanh(x), 4)
    x_arr = np.asarray(x)
    result = np.tanh(x_arr)
    rounded = np.round(result, 4)
    if isinstance(x, list):
        return rounded.tolist()
    elif isinstance(x, tuple):
        return tuple(rounded.tolist())
    else:
        return rounded
def relu(x):
    """Element-wise Rectified Linear Unit (ReLU) function."""
    if isinstance(x, (int, float)):
        return round(max(0, x), 4)
    x_arr = np.asarray(x)
    result = np.maximum(x_arr, 0)
    rounded = np.round(result, 4)
    if isinstance(x, list):
        return rounded.tolist()
    elif isinstance(x, tuple):
        return tuple(rounded.tolist())
    else:
        return rounded
def leaky_relu(x, alpha: float=0.01):
    """Element-wise Leaky ReLU function with slope `alpha` for negative inputs."""
    if isinstance(x, (int, float)):
        return round(x if x > 0 else alpha * x, 4)
    x_arr = np.asarray(x)
    result = np.where(x_arr > 0, x_arr, alpha * x_arr)
    rounded = np.round(result, 4)
    if isinstance(x, list):
        return rounded.tolist()
    elif isinstance(x, tuple):
        return tuple(rounded.tolist())
    else:
        return rounded
def softmax(x):
    """Numerically stable softmax function over a 1-D input."""
    x_arr = np.asarray(x)
    exps = np.exp(x_arr - np.max(x_arr))
    softmax_output = exps / np.sum(exps)
    rounded = np.round(softmax_output, 4)
    if isinstance(x, (int, float)):
        return float(rounded)
    elif isinstance(x, list):
        return rounded.tolist()
    elif isinstance(x, tuple):
        return tuple(rounded.tolist())
    else:
        return rounded
def get_activation(name):
    """Return the activation function by name.

    Args:
        name (str): One of {"sigmoid", "tanh", "relu", "leaky_relu", "softmax"}

    Returns:
        Callable: the requested activation function.

    Raises:
        ValueError: If *name* is not recognized.
    """
    activations = {'sigmoid': sigmoid, 'tanh': tanh, 'relu': relu, 'leaky_relu': leaky_relu, 'softmax': softmax}
    if name not in activations:
        raise ValueError('Invalid activation function.')
    return activations[name]

import numpy as np
from itertools import combinations, permutations
def random_unweighted_graph(n_vertices: int, edge_prob: float=0.5, directed: bool=False) -> list[list[int]]:
    """
    Generate an un-weighted Erdős–Rényi random graph.

    The function returns the adjacency matrix (a nested list) of the graph.

    Args:
        n_vertices (int): Number of vertices in the graph (labelled 0..n-1).
        edge_prob  (float): Probability *p* that an admissible edge exists.
        directed   (bool):  Whether the graph is directed (default ``False``).

    Returns:
        list[list[int]]: The adjacency matrix *A* where *A[i][j] == 1* iff an
        edge from vertex *i* to vertex *j* exists.  The matrix must consist of
        integers (0/1) only and have shape *(n_vertices, n_vertices)*.
    """
    if n_vertices == 1:
        return [[0]]
    adj = [[0 for _ in range(n_vertices)] for _ in range(n_vertices)]
    if directed:
        for i in range(n_vertices):
            for j in range(n_vertices):
                if i == j:
                    continue
                if np.random.rand() <= edge_prob:
                    adj[i][j] = 1
    else:
        for i in range(n_vertices):
            for j in range(i + 1, n_vertices):
                if np.random.rand() <= edge_prob:
                    adj[i][j] = 1
                    adj[j][i] = 1
    return adj

import math
from collections import Counter
def best_split(feature, target):
    """
    Determine the numerical threshold that produces the highest information gain.

    Parameters
    ----------
    feature : list[int | float] | 1-D numpy.ndarray
        Numerical values of a single attribute.
    target  : list[int] | 1-D numpy.ndarray
        Corresponding class labels.

    Returns
    -------
    tuple
        (threshold, information_gain) where
        * threshold – float rounded to 4 decimal places or None when no useful split exists;
        * information_gain – float rounded to 4 decimal places.
    """
    feature = list(feature)
    target = list(target)
    n = len(target)
    unique_f = sorted(set(feature))
    if len(unique_f) < 2:
        return (None, 0.0)
    parent_counter = Counter(target)
    parent_entropy = 0.0
    for count in parent_counter.values():
        p = count / n
        parent_entropy -= p * math.log2(p)
    if parent_entropy == 0.0:
        return (None, 0.0)
    candidates = []
    for i in range(len(unique_f) - 1):
        t = (unique_f[i] + unique_f[i + 1]) / 2
        candidates.append(t)
    max_ig = -1.0
    best_threshold = None
    for t in candidates:
        left_counts = Counter()
        right_counts = Counter()
        left_size = 0
        right_size = 0
        for (f_val, label) in zip(feature, target):
            if f_val < t:
                left_counts[label] += 1
                left_size += 1
            else:
                right_counts[label] += 1
                right_size += 1
        ent_left = 0.0
        if left_size > 0:
            for count in left_counts.values():
                p = count / left_size
                ent_left -= p * math.log2(p)
        ent_right = 0.0
        if right_size > 0:
            for count in right_counts.values():
                p = count / right_size
                ent_right -= p * math.log2(p)
        ig = parent_entropy - left_size / n * ent_left - right_size / n * ent_right
        if ig > max_ig:
            max_ig = ig
            best_threshold = t
        elif ig == max_ig:
            if t < best_threshold:
                best_threshold = t
    if max_ig <= 0:
        return (None, 0.0)
    else:
        return (round(best_threshold, 4), round(max_ig, 4))

def evaluate_decision_tree(tree: dict, sample: list[float]):
    """Traverse a binary decision-tree represented by nested dictionaries.

    Args:
        tree (dict): Root node of the decision tree. Internal nodes contain
            'feature_i', 'threshold', 'true_branch', 'false_branch'. Leaf
            nodes contain only 'value'.
        sample (list[float]): Feature vector that will be classified/regressed.

    Returns:
        The value stored in the reached leaf (int, float, or str).
    """
    if 'value' in tree:
        return tree['value']
    feature_i = tree['feature_i']
    threshold = tree['threshold']
    if sample[feature_i] <= threshold:
        return evaluate_decision_tree(tree['true_branch'], sample)
    else:
        return evaluate_decision_tree(tree['false_branch'], sample)

import numpy as np
def L1Regularization(alpha: float):
    """Create a callable object that implements L1 regularisation.

    The returned object must support two operations:
    1. call with a NumPy array to obtain the L1 penalty value
    2. call its .grad(w) method to obtain the gradient of the penalty

    Args:
        alpha: Non-negative float controlling the strength of the regularisation.

    Returns:
        An instance that fulfils the described interface.
    """

    class Regularizer:

        def __init__(self, alpha):
            self.alpha = alpha

        def __call__(self, w):
            penalty = self.alpha * np.sum(np.abs(w))
            return float(penalty)

        def grad(self, w):
            return self.alpha * np.sign(w)
    return Regularizer(alpha)

import numpy as np
from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
@dataclass
class Node:
    """An internal decision-tree node.

    Attributes:
        feature (int):   Index of the feature to test.
        threshold (float): Threshold that splits the data.
        left (Node | Leaf):  Sub-tree for samples with feature value < threshold.
        right (Node | Leaf): Sub-tree for samples with feature value ≥ threshold.
    """
    feature: int
    threshold: float
    left: object
    right: object
def compare_trees(tree_a, tree_b):
    """Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if type(tree_a) != type(tree_b):
        return False
    if isinstance(tree_a, Leaf) and isinstance(tree_b, Leaf):
        a_val = np.array(tree_a.value)
        b_val = np.array(tree_b.value)
        return np.allclose(a_val, b_val, atol=1e-08, rtol=0.0)
    if isinstance(tree_a, Node) and isinstance(tree_b, Node):
        if tree_a.feature != tree_b.feature:
            return False
        if not np.allclose(tree_a.threshold, tree_b.threshold, atol=1e-08, rtol=0.0):
            return False
        return compare_trees(tree_a.left, tree_b.left) and compare_trees(tree_a.right, tree_b.right)
    return False

import heapq
from typing import List, Union
Number = Union[int, float]
def top_k_elements(sequence: List[Number], k: int, order: str='largest') -> List[Number]:
    """
    Keeps only the k largest or k smallest numbers from *sequence*.

    The function must visit each element exactly once and store no more than
    *k* items at any time by making use of the heapq module (binary heap).

    Args:
        sequence: A list of integers or floats representing the data stream.
        k: The number of extreme values to keep.
        order: "largest" to retain the k biggest values, "smallest" to retain
            the k smallest.

    Returns:
        A list with at most k numbers sorted according to *order*.
    """
    if not sequence or k <= 0:
        return []
    n = len(sequence)
    if k >= n:
        if order == 'largest':
            return sorted(sequence, reverse=True)
        else:
            return sorted(sequence)
    heap = []
    if order == 'largest':
        for num in sequence:
            heapq.heappush(heap, num)
            if len(heap) > k:
                heapq.heappop(heap)
        return sorted(heap, reverse=True)
    else:
        for num in sequence:
            if len(heap) < k:
                heapq.heappush(heap, -num)
            else:
                current_max = -heap[0]
                if num < current_max:
                    heapq.heappop(heap)
                    heapq.heappush(heap, -num)
        return sorted((-x for x in heap))

def inorder_keys(pairs):
    """Build a BST from (key, value) *pairs* and return keys from an in-order traversal.

    Args:
        pairs (list[tuple[Any, int | float]]): List of (key, numeric value) pairs.

    Returns:
        list: Keys in in-order (ascending by numeric value). Return an empty
        list if *pairs* is empty.
    """
    if not pairs:
        return []
    root = Node(pairs[0][1])
    root.key = pairs[0][0]
    for (key, value) in pairs[1:]:
        new_node = Node(value)
        new_node.key = key
        current = root
        while True:
            if new_node < current:
                if current.left is None:
                    current.left = new_node
                    break
                else:
                    current = current.left
            elif current.right is None:
                current.right = new_node
                break
            else:
                current = current.right
    result = []

    def traverse(node):
        if node is not None:
            traverse(node.left)
            result.append(node.key)
            traverse(node.right)
    traverse(root)
    return result

import numpy as np
import numpy as np
def get_loss(name: str):
    """Return a loss function specified by *name*.

    The function must recognise the following identifiers (case-insensitive,
    underscores are ignored):
        • MAE  →  "mae" | "mean_absolute_error"
        • MSE  →  "mse" | "mean_squared_error"
        • Binary Cross Entropy  →  "cross_entropy" | "log_loss" | "ce"

    The returned object has the signature `fn(y_true, y_pred) -> float` where
    *y_true* and *y_pred* are 1-D sequences (list or NumPy array) of equal
    length.  The resulting float must be rounded to **four** decimal places.

    Args:
        name: Name or alias of the desired loss function.

    Returns:
        A Python callable implementing the requested loss.

    Raises:
        ValueError: If *name* does not correspond to a supported loss.
    """
    processed = name.lower().replace('_', '')
    mae_names = {'mae', 'meanabsoluteerror'}
    mse_names = {'mse', 'meansquarederror'}
    ce_names = {'crossentropy', 'logloss', 'ce'}
    if processed in mae_names:

        def loss(y_true, y_pred):
            y_true_arr = np.asarray(y_true)
            y_pred_arr = np.asarray(y_pred)
            return round(np.mean(np.abs(y_true_arr - y_pred_arr)), 4)
        return loss
    elif processed in mse_names:

        def loss(y_true, y_pred):
            y_true_arr = np.asarray(y_true)
            y_pred_arr = np.asarray(y_pred)
            return round(np.mean((y_true_arr - y_pred_arr) ** 2), 4)
        return loss
    elif processed in ce_names:

        def loss(y_true, y_pred):
            y_true_arr = np.asarray(y_true)
            y_pred_arr = np.asarray(y_pred)
            y_pred_clipped = np.clip(y_pred_arr, 1e-15, 1 - 1e-15)
            return round(-np.mean(y_true_arr * np.log(y_pred_clipped) + (1 - y_true_arr) * np.log(1 - y_pred_clipped)), 4)
        return loss
    else:
        raise ValueError('Invalid loss function.')

import numpy as np
import string
def genetic_algorithm(target: str, population_size: int, mutation_rate: float, iterations: int) -> str:
    np.random.seed(42)
    alphabet = ' ' + string.ascii_letters
    char_to_index = {c: i for (i, c) in enumerate(alphabet)}
    length = len(target)
    population = []
    for _ in range(population_size):
        individual = ''.join([alphabet[np.random.randint(0, len(alphabet))] for _ in range(length)])
        population.append(individual)

    def compute_fitness(individual):
        sum_diff = 0.0
        for (c_ind, c_targ) in zip(individual, target):
            sum_diff += abs(char_to_index[c_ind] - char_to_index[c_targ])
        return 1.0 / (sum_diff + 1e-06)
    for ind in population:
        if ind == target:
            return ind
    current_fitness = [compute_fitness(ind) for ind in population]
    best_individual = population[0]
    best_fitness = current_fitness[0]
    for i in range(1, population_size):
        if current_fitness[i] > best_fitness:
            best_individual = population[i]
            best_fitness = current_fitness[i]
    for _ in range(iterations):

        def select_parents(fitnesses):
            available = list(range(len(fitnesses)))
            selected = []
            while len(selected) < len(fitnesses):
                total = sum((fitnesses[i] for i in available))
                spin = np.random.uniform(0, total)
                current = 0.0
                for i in available:
                    current += fitnesses[i]
                    if current >= spin:
                        selected.append(i)
                        available.remove(i)
                        break
            return selected
        parent_indices = select_parents(current_fitness)
        children = []
        for i in range(0, len(parent_indices), 2):
            if i + 1 < len(parent_indices):
                p1 = population[parent_indices[i]]
                p2 = population[parent_indices[i + 1]]
                cross_point = np.random.randint(0, length)
                c1 = p1[:cross_point] + p2[cross_point:]
                c2 = p2[:cross_point] + p1[cross_point:]
                c1 = list(c1)
                for j in range(length):
                    if np.random.rand() < mutation_rate:
                        c1[j] = alphabet[np.random.randint(0, len(alphabet))]
                c1 = ''.join(c1)
                c2 = list(c2)
                for j in range(length):
                    if np.random.rand() < mutation_rate:
                        c2[j] = alphabet[np.random.randint(0, len(alphabet))]
                c2 = ''.join(c2)
                if c1 == target:
                    return c1
                if c2 == target:
                    return c2
                children.append(c1)
                children.append(c2)
            else:
                p1 = population[parent_indices[i]]
                c = list(p1)
                for j in range(length):
                    if np.random.rand() < mutation_rate:
                        c[j] = alphabet[np.random.randint(0, len(alphabet))]
                c = ''.join(c)
                if c == target:
                    return c
                children.append(c)
        current_fitness = []
        for child in children:
            fit = compute_fitness(child)
            current_fitness.append(fit)
            if fit > best_fitness:
                best_individual = child
                best_fitness = fit
                if best_fitness == 1000000.0:
                    return best_individual
        population = children
    return best_individual

from collections import Counter
def build_fp_tree(transactions: list[list[str]], min_support: int) -> dict:
    """Construct an FP-Tree using only nested dictionaries.

    Parameters
    ----------
    transactions : list[list[str]]
        A list where each element is a list of items representing one transaction.
    min_support : int
        Minimum number of transactions an item has to appear in to be kept.

    Returns
    -------
    dict
        The root node of the FP-Tree.  Each node contains two keys:
        "support" and "children" (the latter mapping item → child-node).
    """
    item_counts = Counter()
    for transaction in transactions:
        items = set(transaction)
        for item in items:
            item_counts[item] += 1
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_support}
    if not frequent_items:
        return {'support': 0, 'children': {}}
    sorted_items = sorted(frequent_items, key=lambda x: (-item_counts[x], x))
    root = {'support': 0, 'children': {}}
    for transaction in transactions:
        items = set(transaction)
        filtered = [item for item in items if item in frequent_items]
        ordered = sorted(filtered, key=lambda x: (-item_counts[x], x))
        current = root
        for item in ordered:
            if item in current['children']:
                current['children'][item]['support'] += 1
            else:
                current['children'][item] = {'support': 1, 'children': {}}
            current = current['children'][item]
    return root

import numpy as np
def get_initializer(name: str):
    """Return a weight-initialisation callable associated with *name*.

    The callable must have the signature
        initializer(shape: tuple[int, ...], scale: float = 1.0) -> np.ndarray
    and create a numpy array filled according to the requested strategy.

    Supported strategies (case-insensitive):
        - "zeros"
        - "ones"
        - "normal"
        - "glorot_uniform"

    If *name* is not recognised the function must raise `ValueError`.
    """
    name = name.lower()
    if name not in {'zeros', 'ones', 'normal', 'glorot_uniform'}:
        raise ValueError(f'Unsupported initializer name: {name}')
    if name == 'zeros':

        def initializer(shape, scale=1.0):
            return np.zeros(shape)
    elif name == 'ones':

        def initializer(shape, scale=1.0):
            return np.ones(shape) * scale
    elif name == 'normal':

        def initializer(shape, scale=1.0):
            return np.random.normal(0.0, scale, size=shape)
    elif name == 'glorot_uniform':

        def initializer(shape, scale=1.0):
            if len(shape) < 2:
                raise ValueError("Shape must be at least 2D for 'glorot_uniform' initializer")
            fan_in = shape[0]
            fan_out = shape[1]
            limit = scale * np.sqrt(6.0 / (fan_in + fan_out))
            return np.random.uniform(-limit, limit, size=shape)
    return initializer

import heapq
from collections import Counter
def huffman_encode(tokens: list[str]) -> dict[str, str]:
    """Generate a Huffman code for the supplied tokens.

    The function must return a dictionary mapping **every** unique token in
    *tokens* to its binary Huffman code. All codes must be prefix-free and
    consist only of the characters ‘0’ and ‘1’.
    """
    if not tokens:
        return {}
    freqs = Counter(tokens)
    if len(freqs) == 1:
        token = next(iter(freqs))
        return {token: '0'}
    heap = []
    for (token, freq) in freqs.items():
        heapq.heappush(heap, (freq, token, (token,)))
    while len(heap) > 1:
        (a_freq, a_token, a_node) = heapq.heappop(heap)
        (b_freq, b_token, b_node) = heapq.heappop(heap)
        new_freq = a_freq + b_freq
        new_node = (a_node, b_node)
        heapq.heappush(heap, (new_freq, '', new_node))
    (_, _, root_node) = heapq.heappop(heap)
    codes = {}

    def traverse(node, current_code):
        if len(node) == 1:
            token = node[0]
            codes[token] = current_code
        else:
            (left, right) = node
            traverse(left, current_code + '0')
            traverse(right, current_code + '1')
    traverse(root_node, '')
    return codes

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """
    cont_action = False
    if tuple_action:
        if isinstance(env.action_space, Tuple):
            cont_action = all((isinstance(space, Box) for space in env.action_space.spaces))
        elif isinstance(env.action_space, Dict):
            cont_action = all((isinstance(space, Box) for space in env.action_space.spaces.values()))
    else:
        cont_action = isinstance(env.action_space, Box)
    cont_obs = False
    if tuple_obs:
        if isinstance(env.observation_space, Tuple):
            cont_obs = all((isinstance(space, Box) for space in env.observation_space.spaces))
        elif isinstance(env.observation_space, Dict):
            cont_obs = all((isinstance(space, Box) for space in env.observation_space.spaces.values()))
    else:
        cont_obs = isinstance(env.observation_space, Box)
    return (cont_action, cont_obs)

import re
from typing import Any, Dict, Optional
class KernelBase:
    """Minimal base class for all kernels.  Do NOT modify this class."""

    def __init__(self, **kwargs: Any) -> None:
        self.params: Dict[str, Any] = kwargs

    def set_params(self, state: Dict[str, Any]):
        """(Re)sets parameters from a previously stored summary dict."""
        hp = state.get('hyperparameters', {})
        self.params.update({k: v for (k, v) in hp.items() if k != 'id'})
        return self

    def summary(self) -> Dict[str, Any]:
        """Dictionary description that can be fed back to *initialize_kernel*."""
        return {'hyperparameters': {'id': self.__class__.__name__, **self.params}}
class LinearKernel(KernelBase):

    def __init__(self):
        super().__init__()
class PolynomialKernel(KernelBase):

    def __init__(self, degree: int=3, coef0: float=1.0):
        super().__init__(degree=degree, coef0=coef0)
class RBFKernel(KernelBase):

    def __init__(self, gamma: float=1.0):
        super().__init__(gamma=gamma)
def initialize_kernel(param: Optional[Any]=None) -> KernelBase:
    """Create and return a kernel object from various representations.

    Args:
        param: None (default LinearKernel), an existing KernelBase instance, a
            string description such as 'RBF(gamma=0.5)', or a dictionary with a
            'hyperparameters' entry as produced by KernelBase.summary().

    Returns:
        KernelBase: the newly created or forwarded kernel instance.
    """
    if param is None:
        return LinearKernel()
    if isinstance(param, KernelBase):
        return param
    if isinstance(param, str):
        match = re.fullmatch('(\\w+)\\s*(?:$([^)]*)$)?', param.strip())
        if not match:
            raise NotImplementedError(f'Invalid string format for kernel: {param}')
        name_part = match.group(1)
        params_part = match.group(2) or ''
        kernel_name_lower = name_part.lower()
        kernel_string_info = {'linear': {'class': LinearKernel, 'params': {}}, 'polynomial': {'class': PolynomialKernel, 'params': {'degree': int, 'coef0': float}}, 'rbf': {'class': RBFKernel, 'params': {'gamma': float}}}
        if kernel_name_lower not in kernel_string_info:
            raise NotImplementedError(f'Unknown kernel name: {name_part}')
        kernel_info = kernel_string_info[kernel_name_lower]
        kernel_class = kernel_info['class']
        param_types = kernel_info['params']
        params = {}
        if params_part:
            parts = [p.strip() for p in params_part.split(',')]
            for part in parts:
                if not part:
                    continue
                if '=' not in part:
                    raise NotImplementedError(f'Invalid parameter format: {part}')
                (key, value_str) = part.split('=', 1)
                key = key.strip()
                value_str = value_str.strip()
                if key not in param_types:
                    raise NotImplementedError(f'Unknown parameter {key} for kernel {kernel_name_lower}')
                target_type = param_types[key]
                if target_type == bool:
                    if value_str.lower() == 'true':
                        value = True
                    elif value_str.lower() == 'false':
                        value = False
                    else:
                        raise ValueError(f'Invalid boolean value: {value_str}')
                elif target_type == int:
                    value = int(value_str)
                elif target_type == float:
                    value = float(value_str)
                elif target_type == str:
                    value = value_str
                else:
                    raise ValueError(f'Unsupported type {target_type} for parameter {key}')
                params[key] = value
        return kernel_class(**params)
    if isinstance(param, dict):
        if 'hyperparameters' not in param:
            raise ValueError("Dictionary is missing 'hyperparameters' key")
        hp = param['hyperparameters']
        if 'id' not in hp:
            raise ValueError("hyperparameters dictionary is missing 'id' key")
        kernel_id = hp['id']
        kernel_class_map = {'LinearKernel': LinearKernel, 'PolynomialKernel': PolynomialKernel, 'RBFKernel': RBFKernel}
        if kernel_id not in kernel_class_map:
            raise NotImplementedError(f'Unknown kernel ID: {kernel_id}')
        kernel_class = kernel_class_map[kernel_id]
        params = {k: v for (k, v) in hp.items() if k != 'id'}
        return kernel_class(**params)
    raise ValueError(f'Unsupported parameter type: {type(param)}')

import math
def gaussian_cdf(x: float, mean: float, variance: float) -> float:
    """
    Compute Φ(x; μ, σ²) – the Gaussian CDF evaluated at *x*.

    Parameters
    ----------
    x : float
        The point at which to evaluate the CDF.
    mean : float
        The mean (μ) of the Gaussian distribution.
    variance : float
        The variance (σ²) of the Gaussian distribution. Must be non-negative.

    Returns
    -------
    float
        The probability that a Gaussian random variable with given parameters
        does not exceed *x*, rounded to six decimal places.
    """
    if variance == 0.0:
        if x < mean:
            return round(0.0, 6)
        elif x == mean:
            return round(0.5, 6)
        else:
            return round(1.0, 6)
    else:
        sigma = math.sqrt(variance)
        z = (x - mean) / (sigma * math.sqrt(2))
        erf_z = math.erf(z)
        result = 0.5 * (1 + erf_z)
        return round(result, 6)

import numpy as np
import re
def apply_activation(x, param=None):
    """
    Apply a chosen activation function to the input data.

    Args:
        x (list[float] | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        param (str | None, optional): Description of the activation function.
            Supported values: None, "identity", "relu", "tanh", "sigmoid",
            "leaky relu", "leaky relu(alpha=...)", "affine(slope=..., intercept=...)".

    Returns:
        list[float]: Result of applying the activation function element-wise,
            rounded to six decimal places and converted to a Python list.

    Raises:
        ValueError: If the activation function is unknown or parameters are invalid.
    """
    x_arr = np.array(x, dtype=float)
    if param is None:
        result = x_arr
    else:
        s = param.lower()
        if s == 'identity':
            result = x_arr
        elif s == 'relu':
            result = np.maximum(x_arr, 0)
        elif s == 'tanh':
            result = np.tanh(x_arr)
        elif s == 'sigmoid':
            result = 1 / (1 + np.exp(-x_arr))
        elif s.startswith('leaky'):
            leaky_match = re.fullmatch('^leaky\\s+relu$($alpha=([^)]+))?$', s)
            if leaky_match:
                alpha_str = leaky_match.group(2)
                if alpha_str is None:
                    alpha = 0.01
                else:
                    try:
                        alpha = float(alpha_str)
                    except ValueError:
                        raise ValueError(f'Unknown activation: {param}')
                result = np.where(x_arr > 0, x_arr, alpha * x_arr)
            else:
                raise ValueError(f'Unknown activation: {param}')
        elif s.startswith('affine'):
            affine_match = re.fullmatch('^affine$($slope=([-+]?\\d*\\.?\\d+),\\s*intercept=([-+]?\\d*\\.?\\d+))?$', s)
            if affine_match:
                slope_str = affine_match.group(2)
                intercept_str = affine_match.group(3)
                try:
                    slope = float(slope_str)
                    intercept = float(intercept_str)
                except ValueError:
                    raise ValueError(f'Unknown activation: {param}')
                result = slope * x_arr + intercept
            else:
                raise ValueError(f'Unknown activation: {param}')
        else:
            raise ValueError(f'Unknown activation: {param}')
    rounded = np.round(result, 6)
    output = rounded.tolist()
    if not isinstance(output, list):
        output = [output]
    return output

import numpy as np
def env_stats(actions: list, observations: list) -> dict:
    """Compute statistics for collections of actions and observations.

    The function inspects *actions* and *observations* and returns a dictionary
    containing information about dimensionality, data type (discrete or
    continuous) and the unique values appearing in every dimension.

    Parameters
    ----------
    actions : list
        A list with the actions that were taken.  Each element is either a
        scalar or an iterable of scalars (for multi-dimensional spaces).
    observations : list
        A list with the corresponding observations.  Same structural
        requirements as *actions*.

    Returns
    -------
    dict
        A dictionary with the keys described in the task description.
    """

    def process_data(data):
        is_tuple = any((isinstance(x, (list, tuple, np.ndarray)) for x in data))
        if is_tuple:
            first_element = data[0]
            if isinstance(first_element, np.ndarray):
                dim = first_element.shape[0]
            else:
                dim = len(first_element)
            per_dim_values = []
            for i in range(dim):
                col = []
                for x in data:
                    if isinstance(x, np.ndarray):
                        col.append(x[i].item())
                    else:
                        col.append(x[i])
                per_dim_values.append(col)
            all_values = []
            for x in data:
                if isinstance(x, (list, tuple)):
                    for y in x:
                        if isinstance(y, np.generic):
                            all_values.append(y.item())
                        else:
                            all_values.append(y)
                elif isinstance(x, np.ndarray):
                    for y in x.tolist():
                        if isinstance(y, np.generic):
                            all_values.append(y.item())
                        else:
                            all_values.append(y)
        else:
            dim = 1
            per_dim_values = [data]
            all_values = []
            for x in data:
                if isinstance(x, np.generic):
                    all_values.append(x.item())
                else:
                    all_values.append(x)
        return (is_tuple, dim, per_dim_values, all_values)
    (tuple_actions, action_dim, per_dim_actions, all_actions_values) = process_data(actions)
    (tuple_observations, obs_dim, per_dim_observations, all_observations_values) = process_data(observations)
    continuous_actions = any((isinstance(x, float) and (not x.is_integer()) for x in all_actions_values))
    continuous_observations = any((isinstance(x, float) and (not x.is_integer()) for x in all_observations_values))
    n_actions_per_dim = []
    action_ids = []
    for dim_values in per_dim_actions:
        unique_vals = sorted(list(set(dim_values)))
        n_actions_per_dim.append(len(unique_vals))
        action_ids.append(unique_vals)
    n_obs_per_dim = []
    obs_ids = []
    for dim_values in per_dim_observations:
        unique_vals = sorted(list(set(dim_values)))
        n_obs_per_dim.append(len(unique_vals))
        obs_ids.append(unique_vals)
    result = {'tuple_actions': tuple_actions, 'tuple_observations': tuple_observations, 'multidim_actions': action_dim > 1, 'multidim_observations': obs_dim > 1, 'continuous_actions': continuous_actions, 'continuous_observations': continuous_observations, 'n_actions_per_dim': n_actions_per_dim, 'action_dim': action_dim, 'n_obs_per_dim': n_obs_per_dim, 'obs_dim': obs_dim, 'action_ids': action_ids, 'obs_ids': obs_ids}
    return result

def get_metric(name: str):
    """Return an evaluation metric function by *name*.

    Supported names:
    - 'accuracy'
    - 'precision'
    - 'recall'
    - 'f1'

    If the name is invalid, the function should return -1.
    The returned metric functions must accept two equal-length iterables
    (y_true, y_pred) containing only 0s and 1s and return the metric value
    rounded to 4 decimal places.
    """

    def accuracy(y_true, y_pred):
        """Compute the accuracy metric."""
        correct = sum((t == p for (t, p) in zip(y_true, y_pred)))
        total = len(y_true)
        return round(correct / total, 4)

    def precision(y_true, y_pred):
        """Compute the precision metric."""
        tp = sum((t * p for (t, p) in zip(y_true, y_pred)))
        fp = sum(((1 - t) * p for (t, p) in zip(y_true, y_pred)))
        denominator = tp + fp
        if denominator == 0:
            return 0.0
        return round(tp / denominator, 4)

    def recall(y_true, y_pred):
        """Compute the recall metric."""
        tp = sum((t * p for (t, p) in zip(y_true, y_pred)))
        fn = sum((t * (1 - p) for (t, p) in zip(y_true, y_pred)))
        denominator = tp + fn
        if denominator == 0:
            return 0.0
        return round(tp / denominator, 4)

    def f1(y_true, y_pred):
        """Compute the F1 score metric."""
        tp = sum((t * p for (t, p) in zip(y_true, y_pred)))
        fp = sum(((1 - t) * p for (t, p) in zip(y_true, y_pred)))
        fn = sum((t * (1 - p) for (t, p) in zip(y_true, y_pred)))
        denominator_p = tp + fp
        if denominator_p == 0:
            p = 0.0
        else:
            p = tp / denominator_p
        denominator_r = tp + fn
        if denominator_r == 0:
            r = 0.0
        else:
            r = tp / denominator_r
        if p + r == 0.0:
            return 0.0
        f1_score = 2 * p * r / (p + r)
        return round(f1_score, 4)
    if name == 'accuracy':
        return accuracy
    elif name == 'precision':
        return precision
    elif name == 'recall':
        return recall
    elif name == 'f1':
        return f1
    else:
        return -1

import numpy as np
def bayesian_linear_regression_pred(X: np.ndarray, y: np.ndarray, X_new: np.ndarray, mu: float | np.ndarray=0.0, sigma: float=1.0, V: float | np.ndarray | None=None, fit_intercept: bool=True) -> list[float]:
    """Bayesian linear regression with known variance.

    The function must return the MAP predictive means for every row in
    `X_new`.  See the task description for full details.
    """
    if fit_intercept:
        X = np.hstack([np.ones((X.shape[0], 1)), X])
        X_new = np.hstack([np.ones((X_new.shape[0], 1)), X_new])
    P = X.shape[1]
    if isinstance(mu, (int, float)):
        mu_vec = np.full(P, mu, dtype=np.float64)
    else:
        mu_vec = np.array(mu, dtype=np.float64).flatten()
        if mu_vec.shape[0] != P:
            raise ValueError('Length of mu must match the number of features (including intercept).')
    if V is None:
        V = np.eye(P)
    elif isinstance(V, (int, float)):
        V = np.eye(P) * V
    elif isinstance(V, np.ndarray):
        if V.ndim == 1:
            if len(V) != P:
                raise ValueError('Length of V must match the number of features (including intercept).')
            V = np.diag(V)
        elif V.shape != (P, P):
            raise ValueError(f'V must be a {P}x{P} matrix.')
    else:
        raise ValueError('V must be a scalar, 1D array, or 2D matrix.')
    V_inv = np.linalg.inv(V)
    XtX = X.T @ X
    Xty = X.T @ y
    term = V_inv + XtX
    Sigma_p = np.linalg.inv(term)
    mu_p = Sigma_p @ (V_inv @ mu_vec + Xty)
    y_pred = X_new @ mu_p
    return [round(float(val), 4) for val in y_pred]

def classification_accuracy(y_true: list, y_pred: list) -> float:
    """Compute the accuracy between the true and predicted class labels.

    Args:
        y_true (list): Ground-truth class labels.
        y_pred (list): Predicted class labels produced by a model.

    Returns:
        float: The accuracy score rounded to 4 decimal places.
              Returns -1 when the inputs have different lengths or when
              either of the inputs is empty.
    """
    if not y_true or not y_pred:
        return -1
    if len(y_true) != len(y_pred):
        return -1
    correct = 0
    for (true, pred) in zip(y_true, y_pred):
        if true == pred:
            correct += 1
    accuracy = correct / len(y_true)
    return round(accuracy, 4)

import numpy as np
def oracle_payoff(context_probs: np.ndarray, context: np.ndarray) -> tuple[float, int]:
    """
    Oracle that returns the optimal expected reward and the corresponding arm.

    Parameters
    ----------
    context_probs : np.ndarray
        A 2-D array of shape (D, K) where each row represents a context and each
        column an arm.  Entry (i, j) is the success probability of arm *j* when
        the active context is *i*.
    context : np.ndarray
        A one-hot vector of length *D* identifying the current context.

    Returns
    -------
    tuple[float, int]
        (optimal_rwd, optimal_arm): the best achievable expected reward (rounded
        to four decimals) and the index of the arm that achieves it.  If the
        input context is invalid the function returns (-1, -1).
    """
    if context.ndim != 1:
        return (-1, -1)
    D = context_probs.shape[0]
    if context.shape[0] != D:
        return (-1, -1)
    if not np.all(np.isin(context, [0, 1])):
        return (-1, -1)
    if context.sum() != 1:
        return (-1, -1)
    context_index = np.where(context == 1)[0][0]
    probs = context_probs[context_index]
    optimal_arm = probs.argmax()
    optimal_rwd = np.round(probs[optimal_arm], 4)
    return (optimal_rwd, optimal_arm)

from typing import List, Dict, Set, Tuple
def topological_sort(vertices: List, edges: List[Tuple]) -> List | None:
    """Determine a topological ordering of a directed graph.

    The implementation uses depth-first search (DFS) and follows the
    deterministic exploration rule described in the task description.

    Args:
        vertices: List of vertex identifiers (hashable).
        edges:    List of directed edges given as 2-tuples (source, target).

    Returns:
        • List of vertices in topological order when the graph is acyclic.
        • None when the graph contains at least one directed cycle.
    """
    if not vertices:
        return []
    vertex_indices = {v: idx for (idx, v) in enumerate(vertices)}
    adj = {v: [] for v in vertices}
    for (u, v) in edges:
        if v not in adj[u]:
            adj[u].append(v)
    for u in adj:
        adj[u].sort(key=lambda x: vertex_indices[x])
    visited = set()
    recursion_stack = set()
    result = []

    def dfs(node):
        if node in recursion_stack:
            return False
        if node in visited:
            return True
        recursion_stack.add(node)
        for neighbor in adj[node]:
            if not dfs(neighbor):
                return False
        recursion_stack.remove(node)
        visited.add(node)
        result.append(node)
        return True
    for node in vertices:
        if node not in visited:
            if not dfs(node):
                return None
    return result[::-1]

import numpy as np
def sigmoid(x, order: int=0):
    """
    Compute the logistic sigmoid function or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in (0, 1, 2):
        return -1
    is_scalar = not (isinstance(x, list) or isinstance(x, np.ndarray))
    x_arr = np.array(x)
    sigma_0 = 1 / (1 + np.exp(-x_arr))
    if order == 0:
        result = sigma_0
    elif order == 1:
        result = sigma_0 * (1 - sigma_0)
    else:
        result = sigma_0 * (1 - sigma_0) * (1 - 2 * sigma_0)
    result_rounded = np.round(result, 4)
    if is_scalar:
        return float(result_rounded.item())
    else:
        return result_rounded.tolist()

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """
    Compute the Radial Basis Function (RBF) kernel matrix.

    Parameters:
    - X: List of N feature vectors (each of length C).
    - Y: (Optional) List of M feature vectors (each of length C). If None, defaults to X.
    - sigma: (Optional) Scale parameter(s). If None, uses default value sqrt(C / 2).

    Returns:
    - A nested list of shape (N, M) representing the RBF kernel matrix.
    - Returns -1 if any input is invalid.
    """
    if not X:
        return -1
    if Y is not None and (not Y):
        return -1
    if Y is None:
        Y = X
    cx = len(X[0])
    for vec in X:
        if len(vec) != cx:
            return -1
    cy = len(Y[0])
    for vec in Y:
        if len(vec) != cy:
            return -1
    if cx != cy:
        return -1
    C = cx
    if sigma is not None:
        if isinstance(sigma, (int, float)):
            if sigma <= 0:
                return -1
            sigma_sq = np.full(C, sigma ** 2)
        elif isinstance(sigma, (list, np.ndarray)):
            if len(sigma) != C:
                return -1
            for s in sigma:
                if not (isinstance(s, (int, float)) or isinstance(s, np.number)):
                    return -1
                if s <= 0:
                    return -1
            sigma_arr = np.array(sigma)
            sigma_sq = sigma_arr ** 2
        else:
            return -1
    else:
        sigma_val = np.sqrt(C / 2)
        sigma_sq = np.full(C, sigma_val ** 2)
    X_np = np.array(X, dtype=float)
    Y_np = np.array(Y, dtype=float)
    squared_diff = (X_np[:, np.newaxis, :] - Y_np[np.newaxis, :, :]) ** 2
    sum_divided = np.sum(squared_diff / sigma_sq, axis=2)
    kernel_matrix = np.exp(-0.5 * sum_divided)
    kernel_matrix_rounded = np.round(kernel_matrix, 4).tolist()
    return kernel_matrix_rounded

import math
import heapq
import numpy as np
from collections import Counter
class KDNode:

    def __init__(self, point, label, axis, left, right):
        self.point = point
        self.label = label
        self.axis = axis
        self.left = left
        self.right = right
def euclidean_distance(a, b):
    return math.sqrt(sum(((ai - bi) ** 2 for (ai, bi) in zip(a, b))))
def build_kd_tree(data, depth):
    if not data:
        return None
    n_features = len(data[0][0])
    axis = depth % n_features
    data_sorted = sorted(data, key=lambda xy: xy[0][axis])
    median_idx = len(data_sorted) // 2
    median_xy = data_sorted[median_idx]
    left_data = data_sorted[:median_idx]
    right_data = data_sorted[median_idx + 1:]
    left = build_kd_tree(left_data, depth + 1)
    right = build_kd_tree(right_data, depth + 1)
    return KDNode(median_xy[0], median_xy[1], axis, left, right)
def search_kd_tree(node, query, heap, k, depth):
    if node is None:
        return
    dist = euclidean_distance(query, node.point)
    if len(heap) < k:
        heapq.heappush(heap, (-dist, node.label))
    elif dist < -heap[0][0]:
        heapq.heappushpop(heap, (-dist, node.label))
    axis = node.axis
    split_val = node.point[axis]
    query_val = query[axis]
    if query_val < split_val:
        first_subtree = node.left
        other_subtree = node.right
    else:
        first_subtree = node.right
        other_subtree = node.left
    search_kd_tree(first_subtree, query, heap, k, depth + 1)
    if len(heap) < k:
        search_kd_tree(other_subtree, query, heap, k, depth + 1)
    else:
        current_max_dist = -heap[0][0]
        current_max_dist_sq = current_max_dist ** 2
        dist_to_plane_sq = (query_val - split_val) ** 2
        if dist_to_plane_sq < current_max_dist_sq:
            search_kd_tree(other_subtree, query, heap, k, depth + 1)
def majority_vote(labels):
    counts = Counter(labels)
    sorted_labels = sorted(counts.items(), key=lambda x: (-x[1], x[0]))
    return sorted_labels[0][0]
def knn_classifier(X_train, y_train, X_query, k, method='naive'):
    X_train = np.array(X_train)
    y_train = np.array(y_train)
    X_query = np.array(X_query)
    predictions = []
    if method == 'naive':
        for query in X_query:
            distances = []
            for i in range(len(X_train)):
                dist = euclidean_distance(query, X_train[i])
                distances.append((dist, y_train[i]))
            distances.sort()
            k_labels = [label for (d, label) in distances[:k]]
            predictions.append(majority_vote(k_labels))
    elif method == 'heap':
        for query in X_query:
            heap = []
            for i in range(len(X_train)):
                dist = euclidean_distance(query, X_train[i])
                if len(heap) < k:
                    heapq.heappush(heap, (-dist, y_train[i]))
                elif dist < -heap[0][0]:
                    heapq.heappushpop(heap, (-dist, y_train[i]))
            k_labels = [label for (d, label) in heap]
            predictions.append(majority_vote(k_labels))
    elif method == 'kdtree':
        data = list(zip(X_train, y_train))
        root = build_kd_tree(data, 0)
        for query in X_query:
            heap = []
            search_kd_tree(root, query, heap, k, 0)
            k_labels = [label for (d, label) in heap]
            predictions.append(majority_vote(k_labels))
    else:
        raise ValueError('Unknown method: {}'.format(method))
    return predictions

import math
def compute_ball_radius(centroid: list[float], X: list[list[float]]) -> float:
    """Compute the maximum Euclidean distance between *centroid* and the
    points in *X* (i.e., the Ball-tree node radius), rounded to 4 decimals.
    """
    if not X:
        return 0.0
    max_sq = 0.0
    for point in X:
        current_sq = 0.0
        for (x_val, c_val) in zip(point, centroid):
            diff = x_val - c_val
            current_sq += diff * diff
        if current_sq > max_sq:
            max_sq = current_sq
    max_dist = math.sqrt(max_sq)
    return round(max_dist, 4)

import numpy as np
def calculate_variance(X):
    """
    Return the population variance of every feature (column) in X.

    Parameters
    ----------
    X : list[list[int | float]] | np.ndarray
        A 2-D data set where each row is a sample and each column is a feature.

    Returns
    -------
    list[float] | int
        A list containing the variance of each feature rounded to four
        decimals. Returns -1 if the input is empty or not 2-D.
    """
    if isinstance(X, np.ndarray):
        if X.ndim != 2:
            return -1
        (n_samples, n_features) = X.shape
        if n_samples < 1 or n_features < 1:
            return -1
    elif isinstance(X, list):
        if not X or not all((isinstance(row, list) for row in X)):
            return -1
        if not X[0] or not all((len(row) == len(X[0]) for row in X)):
            return -1
        n_samples = len(X)
        n_features = len(X[0])
    else:
        return -1
    if isinstance(X, list):
        try:
            X_array = np.array(X, dtype=float)
        except:
            return -1
    else:
        X_array = X
    variances = np.var(X_array, axis=0)
    rounded_variances = np.round(variances, 4).tolist()
    return rounded_variances

import numpy as np
def to_nominal(one_hot):
    """Convert a batch of one-hot encoded vectors to nominal class labels.

    Parameters
    ----------
    one_hot : list[list[int]] | numpy.ndarray
        A 2-D structure where each row is expected to be a one-hot encoded
        vector (all zeros except for a single 1).

    Returns
    -------
    list[int] | int
        A list of integer class labels when the input is valid; otherwise the
        integer -1.
    """
    if not isinstance(one_hot, (list, np.ndarray)):
        return -1
    if isinstance(one_hot, list):
        if not one_hot:
            return -1
        if not all((isinstance(row, list) for row in one_hot)):
            return -1
        row_lengths = set((len(row) for row in one_hot))
        if len(row_lengths) != 1:
            return -1
        n_classes = len(one_hot[0])
    else:
        if one_hot.ndim != 2:
            return -1
        if one_hot.shape[0] == 0:
            return -1
        n_classes = one_hot.shape[1]
    for row in one_hot:
        for x in row:
            if x != 0 and x != 1:
                return -1
        if sum(row) != 1:
            return -1
    result = []
    for row in one_hot:
        for (i, x) in enumerate(row):
            if x == 1:
                result.append(i)
                break
    return result

import numpy as np
def leaky_relu(x: np.ndarray | list | int | float, alpha: float=0.3, order: int=0) -> list:
    """Compute Leaky ReLU, its first derivative, or its second derivative.

    Args:
        x: Values to be transformed. Accepts NumPy array, list, or scalar.
        alpha: Slope used when an element is non-positive (default 0.3).
        order: 0 – return the function value; 1 – return the first derivative; 2 – return the second derivative.

    Returns:
        Python list with the same shape as *x* that contains the requested quantity.
    """
    if order not in {0, 1, 2}:
        return []
    x_arr = np.array(x, dtype=float)
    if order == 0:
        result = np.where(x_arr > 0, x_arr, alpha * x_arr)
    elif order == 1:
        result = np.where(x_arr > 0, 1, alpha)
    else:
        result = np.zeros_like(x_arr)
    if result.ndim == 0:
        return [result.tolist()]
    else:
        return result.tolist()

import numpy as np
def sigmoid_grad(z):
    """
    Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  
    The derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """
    if isinstance(z, np.ndarray):
        grad = z * (1 - z)
        grad = np.round(grad, 4)
        return grad.tolist()
    elif isinstance(z, list):
        if not z:
            return []
        first_element = z[0]
        if isinstance(first_element, list):
            return [sigmoid_grad(sublist) for sublist in z]
        else:
            return [sigmoid_grad(element) for element in z]
    else:
        grad = z * (1 - z)
        return float(np.round(grad, 4))

import numpy as np
def relu_grad(z):
    """
    Compute the element-wise derivative of the ReLU activation function.

    Parameters:
    z (int, float, list, or np.ndarray): Input value(s) to compute the ReLU gradient for.

    Returns:
    int or list: If input is a scalar, returns 0 or 1. Otherwise, returns a list of 0s and 1s
                 with the same shape as the input.
    """
    if isinstance(z, (int, float)):
        return 1 if z > 0 else 0
    elif isinstance(z, np.ndarray):
        if z.ndim == 0:
            return 1 if z.item() > 0 else 0
        else:
            return (z > 0).astype(int).tolist()
    elif isinstance(z, list):
        return [relu_grad(element) for element in z]
    else:
        raise TypeError('Input must be a scalar, list, or numpy array.')

import os
from typing import List, Union
def extract_archive(file_path: str, path: str='.', archive_format: Union[str, List[str], None]='auto') -> bool:
    """Checks whether *file_path* belongs to an allowed archive format.

    The function MUST NOT raise any exception and MUST NOT perform real file
    extraction. It inspects the file name extension to determine if it matches
    one of the supported archive formats.

    Args:
        file_path: Full path (or name) of the archive file.
        path: Destination folder – ignored in this simplified version.
        archive_format: See task description.

    Returns:
        True if the file name matches one of the allowed formats, False otherwise.
    """
    if archive_format is None:
        return False
    if isinstance(archive_format, list) and len(archive_format) == 0:
        return False
    allowed_formats = []
    if archive_format == 'auto':
        allowed_formats = ['tar', 'zip']
    elif isinstance(archive_format, str):
        allowed_formats = [archive_format]
    elif isinstance(archive_format, list):
        allowed_formats = archive_format
    valid_formats = set()
    for fmt in allowed_formats:
        if isinstance(fmt, str):
            lower_fmt = fmt.lower()
            if lower_fmt == 'tar':
                valid_formats.add('tar')
            elif lower_fmt == 'zip':
                valid_formats.add('zip')
    if not valid_formats:
        return False
    tar_extensions = {'.tar', '.tar.gz', '.tgz', '.tar.bz', '.tar.bz2', '.tbz'}
    zip_extensions = {'.zip'}
    all_extensions = set()
    if 'tar' in valid_formats:
        all_extensions.update(tar_extensions)
    if 'zip' in valid_formats:
        all_extensions.update(zip_extensions)
    file_lower = str(file_path).lower()
    for ext in all_extensions:
        if file_lower.endswith(ext):
            return True
    return False

import numpy as np
def sigmoid_(Z):
    """
    Compute the logistic sigmoid activation element-wise.

    Args:
        Z: A scalar, 1-D list/array, or 2-D list/array of real numbers.

    Returns:
        Sigmoid-transformed value(s) rounded to 4 decimals. Returns a float for
        scalar input, otherwise returns a Python list preserving the input shape.
    """
    is_python_scalar = isinstance(Z, (int, float))
    arr = np.array(Z)
    res_arr = 1 / (1 + np.exp(-arr))
    res_arr = np.round(res_arr, 4)
    if is_python_scalar:
        return float(res_arr)
    elif res_arr.ndim == 0:
        return [float(res_arr)]
    else:
        return res_arr.tolist()

def tree_stats(root: 'node | None') -> tuple[int, int]:
    """Return the number of leaf nodes and the maximum depth of the tree.

    A **leaf** is defined as a node whose ``results`` attribute is not ``None``.

    Parameters
    ----------
    root : node | None
        Root of the decision tree.  If *None*, the tree is considered empty.

    Returns
    -------
    tuple[int, int]
        ``(leaf_count, max_depth)`` where
        * *leaf_count* is the total number of leaves in the tree, and
        * *max_depth* is the length of the longest path from *root* to a leaf.
    """

    def helper(n):
        if n is None:
            return (0, 0)
        if n.results is not None:
            return (1, 1)
        (left_count, left_depth) = helper(n.left)
        (right_count, right_depth) = helper(n.right)
        total_count = left_count + right_count
        current_depth = 1 + max(left_depth, right_depth)
        return (total_count, current_depth)
    return helper(root)

from collections import Counter
from typing import Any, Dict, List
def build_tree(data: List[List[Any]]) -> Dict[str, Any]:
    """Build a CART decision tree using the Gini impurity criterion.

    The *data* argument is a list where every inner list represents a training
    sample.  All but the last elements of each sample are feature values; the
    last element is the class label.

    The function should return the root of the generated tree expressed as a
    nested dictionary:
        Leaf   -> {"label": class_label}
        Branch -> {"feature": index,
                    "value":   split_value,
                    "left":    <subtree for samples with feature == value>,
                    "right":   <subtree for samples with feature != value>}

    If *data* is empty return the empty dictionary.
    """
    if not data:
        return {}
    first_class = data[0][-1]
    if all((sample[-1] == first_class for sample in data)):
        return {'label': first_class}

    def gini_impurity(samples):
        if not samples:
            return 0.0
        counts = Counter((sample[-1] for sample in samples))
        n = len(samples)
        return 1.0 - sum(((count / n) ** 2 for count in counts.values()))
    parent_gini = gini_impurity(data)
    n_features = len(data[0]) - 1
    best_gain = 0
    best_feature = None
    best_value = None
    best_left = None
    best_right = None
    for feature_idx in range(n_features):
        values = [sample[feature_idx] for sample in data]
        unique_vals = sorted(set(values))
        for val in unique_vals:
            left = [sample for sample in data if sample[feature_idx] == val]
            right = [sample for sample in data if sample[feature_idx] != val]
            if not left or not right:
                continue
            gini_left = gini_impurity(left)
            gini_right = gini_impurity(right)
            gain = parent_gini - (len(left) / len(data) * gini_left + len(right) / len(data) * gini_right)
            if gain > best_gain:
                best_gain = gain
                best_feature = feature_idx
                best_value = val
                best_left = left
                best_right = right
    if best_gain <= 0:
        labels = [sample[-1] for sample in data]
        unique_classes = []
        for label in labels:
            if label not in unique_classes:
                unique_classes.append(label)
        counts = Counter(labels)
        max_count = max(counts.values())
        for cls in unique_classes:
            if counts[cls] == max_count:
                majority_class = cls
                break
        return {'label': majority_class}
    left_subtree = build_tree(best_left)
    right_subtree = build_tree(best_right)
    return {'feature': best_feature, 'value': best_value, 'left': left_subtree, 'right': right_subtree}

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if hasattr(root, 'y'):
        return root.y
    if task_type == 'classification':
        current_feature = features[root.label]
        selected_child = None
        for child in root.child:
            if child.x == current_feature:
                selected_child = child
                break
        if selected_child is None:
            selected_child = root.child[1]
        return predict_sample(selected_child, features, task_type)
    else:
        current_feature = features[root.label]
        if current_feature <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)

