import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    """Implements a MaxEnt classifier using Generalized Iterative Scaling (GIS)."""
    features = set()
    for (x, y) in zip(X_train, y_train):
        for c in range(X_train.shape[1]):
            v = x[c]
            features.add((c, v, y))
    features = list(features)
    if not features:
        counts = np.bincount(y_train)
        majority = np.argmax(counts)
        return [majority] * len(X_test)
    feature_dict = {feat: idx for (idx, feat) in enumerate(features)}
    N = len(X_train)
    empirical_counts = np.zeros(len(features))
    for (i, (x, y)) in enumerate(zip(X_train, y_train)):
        for c in range(X_train.shape[1]):
            v = x[c]
            feat = (c, v, y)
            if feat in feature_dict:
                idx = feature_dict[feat]
                empirical_counts[idx] += 1
    mu_empirical = empirical_counts / N

import numpy as np
def minesweeper_step(ground: list[list[float | int]], flags: list[list[bool]]) -> tuple[list[tuple[int, int]], list[tuple[int, int]]]:
    """Perform one logical deduction step on a Minesweeper board.

    Args:
        ground: A 2D list where each cell is either a number (0–8) or np.nan (hidden).
        flags: A 2D list of booleans indicating where the player has placed flags.

    Returns:
        A tuple of two sorted lists:
        1. **clicks** – Coordinates of squares that can be safely revealed.
        2. **new_flags** – Coordinates of squares that must be flagged.
    """
    rows = len(ground)
    if rows == 0:
        return ([], [])
    cols = len(ground[0])
    clicks = set()
    new_flags = set()
    for r in range(rows):
        for c in range(cols):
            if not np.isnan(ground[r][c]) and (not flags[r][c]):
                value = ground[r][c]
                bombs = 0
                unexplored_coords = []
                for dr in [-1, 0, 1]:
                    for dc in [-1, 0, 1]:
                        if dr == 0 and dc == 0:
                            continue
                        (nr, nc) = (r + dr, c + dc)
                        if 0 <= nr < rows and 0 <= nc < cols:
                            if np.isnan(ground[nr][nc]):
                                if flags[nr][nc]:
                                    bombs += 1
                                else:
                                    unexplored_coords.append((nr, nc))
                if value == 0:
                    clicks.update(unexplored_coords)
                elif value == 8:
                    new_flags.update(unexplored_coords)
                else:
                    unexplored_count = len(unexplored_coords)
                    if bombs == value:
                        clicks.update(unexplored_coords)
                    elif value - bombs == unexplored_count:
                        new_flags.update(unexplored_coords)
    return (sorted(clicks), sorted(new_flags))

import numpy as np
def convert_maze_to_graph(maze: np.ndarray) -> dict:
    """
    Convert a binary maze to an adjacency-list representation.

    Args:
        maze: A 2-D NumPy array of 0s and 1s, where 1 represents a free cell.

    Returns:
        A dictionary representing the adjacency list of the maze as an undirected graph.
        Each key is a coordinate (row, col) of a free cell, and the value is a sorted list
        of its adjacent free neighbors.
    """
    (rows, cols) = maze.shape
    graph = {}
    for i in range(rows):
        for j in range(cols):
            if maze[i][j] == 1:
                neighbors = []
                if i - 1 >= 0 and maze[i - 1][j] == 1:
                    neighbors.append((i - 1, j))
                if i + 1 < rows and maze[i + 1][j] == 1:
                    neighbors.append((i + 1, j))
                if j - 1 >= 0 and maze[i][j - 1] == 1:
                    neighbors.append((i, j - 1))
                if j + 1 < cols and maze[i][j + 1] == 1:
                    neighbors.append((i, j + 1))
                neighbors.sort()
                graph[i, j] = neighbors
    return graph
