import numpy as np
def compute_tf_idf(corpus, query):
    """
    Computes TF-IDF scores for each word in the query against each document in the corpus.

    Parameters:
    - corpus: List of documents, where each document is a list of words.
    - query: List of words for which to compute TF-IDF scores.

    Returns:
    - A list of lists containing TF-IDF scores for the query words in each document,
      rounded to 5 decimal places.
    """
    if not corpus:
        raise ValueError('The corpus cannot be empty.')
    vocab = set()
    doc_freq = {}
    docs = []
    for (i, doc) in enumerate(corpus):
        docs.append(doc)
        vocab.update(doc)
        for term in doc:
            if term in doc_freq:
                doc_freq[term] += 1
            else:
                doc_freq[term] = 1
    tf_idf_scores = []
    for doc in docs:
        tf = {}
        for term in query:
            if term in doc:
                tf_count = doc.count(term)
                tf[term] = tf_count / len(doc) if len(doc) > 0 else 0
            else:
                tf[term] = 0.0
        idf = {}
        for term in query:
            df = doc_freq.get(term, 0)
            idf[term] = np.log((len(corpus) + 1) / (df + 1)) if df > 0 else 0.0
        doc_scores = []
        for term in query:
            doc_scores.append(round(tf[term] * idf[term], 5))
        tf_idf_scores.append(doc_scores)
    return np.array(tf_idf_scores).tolist()
