def compressed_row_sparse_matrix(dense_matrix):
    """
    Convert a dense matrix to its Compressed Row Sparse (CSR) representation.

    :param dense_matrix: 2D list representing a dense matrix
    :return: A tuple containing (values array, column indices array, row pointer array)
    """
    if not dense_matrix or not dense_matrix[0]:
        return ([], [], [])
    rows = len(dense_matrix)
    cols = len(dense_matrix[0])
    values = []
    col_indices = []
    row_count = 0
    for i in range(rows):
        for j in range(cols):
            if dense_matrix[i][j] != 0:
                values.append(dense_matrix[i][j])
                col_indices.append(j)
                row_count += 1
    row_pointers = [0] * (rows + 1)
    for i in range(rows):
        row_pointers[i + 1] = row_pointers[i] + len([val for val in values if val is not None and val != 0 and (val != 0)])
    return (values, col_indices, row_pointers)
assert compressed_row_sparse_matrix([[1, 0, 0, 0], [0, 2, 0, 0], [3, 0, 4, 0], [1, 0, 0, 5]]) == ([1, 2, 3, 4, 1, 5], [0, 1, 0, 2, 0, 3], [0, 1, 2, 4, 6])
assert compressed_row_sparse_matrix([[0, 0, 0], [1, 2, 0], [0, 3, 4]]) == ([1, 2, 3, 4], [0, 1, 1, 2], [0, 0, 2, 4])
assert compressed_row_sparse_matrix([[0, 0, 3, 0, 0], [0, 4, 0, 0, 0], [5, 0, 0, 6, 0], [0, 0, 0, 0, 0], [0, 7, 0, 0, 8]]) == ([3, 4, 5, 6, 7, 8], [2, 1, 0, 3, 1, 4], [0, 1, 2, 4, 4, 6])

def calculate_brightness(img):
    if not img:
        return -1
    row_lengths = set((len(row) for row in img))
    if len(row_lengths) > 1:
        return -1
    for row in img:
        for pixel in row:
            if not 0 <= pixel <= 255:
                return -1
    total_brightness = sum((sum(row) for row in img))
    num_pixels = len(img) * len(img[0])
    average_brightness = total_brightness / num_pixels
    return round(average_brightness, 2)
