import numpy as np
def load_encoder_hparams_and_params(model_size: str='124M', models_dir: str='models'):

    class DummyBPE:

        def __init__(self):
            self.encoder_dict = {'hello': 1, 'world': 2, '<UNK>': 0}

        def encode(self, text: str):
            tokens = text.strip().split()
            return [self.encoder_dict.get(token, self.encoder_dict['<UNK>']) for token in tokens]

        def decode(self, token_ids: list):
            reversed_dict = {v: k for (k, v) in self.encoder_dict.items()}
            return ' '.join([reversed_dict.get(tok_id, '<UNK>') for tok_id in token_ids])
    hparams = {'n_ctx': 1024, 'n_head': 12}
    params = {'wte': np.random.rand(3, 10), 'wpe': np.random.rand(1024, 10), 'blocks': [], 'ln_f': {'g': np.ones(10), 'b': np.zeros(10)}}
    encoder = DummyBPE()
    return (encoder, hparams, params)
def gen_text(prompt: str, n_tokens_to_generate: int=40):
    np.random.seed(42)
    (encoder, hparams, params) = load_encoder_hparams_and_params()
    input_ids = encoder.encode(prompt)
    max_length = len(input_ids) + n_tokens_to_generate
    output_ids = input_ids.copy()
    for _ in range(n_tokens_to_generate):
        token_embeddings = params['wte'][np.array(output_ids)]
        position_embeddings = params['wpe'][np.arange(len(output_ids))]
        embeddings = token_embeddings + position_embeddings
        normalized_embeddings = embeddings / np.std(embeddings, axis=1, keepdims=True)
        attention_output = np.dot(normalized_embeddings, np.random.rand(10, 10))
        ff_output = np.tanh(np.dot(attention_output, np.random.rand(10, 10)))
        final_output = ff_output / np.std(ff_output, axis=1, keepdims=True)
        next_token_logits = np.dot(final_output, params['wte'].T)
        next_token_probs = np.exp(next_token_logits) / np.sum(np.exp(next_token_logits), axis=1, keepdims=True)
        next_token_id = np.random.choice(len(encoder.encoder_dict), p=next_token_probs[0])
        output_ids.append(next_token_id)
        if len(output_ids) > hparams['n_ctx']:
            output_ids.pop(0)
    generated_text = encoder.decode(output_ids[len(input_ids):])
    return generated_text
assert gen_text("hello", 5) == "hello hello hello <UNK> <UNK>"
assert gen_text("hello world", 10) == "world world world world world world world world world world"
assert gen_text("world", 3) == "world world world"

def compute_efficiency(n_experts, k_active, d_in, d_out):
    dense_flops = d_in * d_out * d_out
    moe_flops = k_active * d_in * d_out
    if dense_flops == 0:
        return 0.0
    savings_percentage = (1 - moe_flops / dense_flops) * 100
    return round(savings_percentage, 1)
assert compute_efficiency(1000, 2, 512, 512) == 99.8
assert compute_efficiency(10, 2, 256, 256) == 80.0
assert compute_efficiency(100, 4, 512, 512) == 96.0
