import numpy as np
def sigmoid(x):
    """Implement element-wise logistic sigmoid."""
    if isinstance(x, (int, float)):
        return round(1 / (1 + np.exp(-x)), 4)
    elif isinstance(x, (list, tuple)):
        return [round(1 / (1 + np.exp(-val)), 4) for val in x]
    elif isinstance(x, np.ndarray):
        return np.round(1 / (1 + np.exp(-x)), 4)
    else:
        raise TypeError('Input must be a scalar, list, tuple, or NumPy array.')
def tanh(x):
    """Implement element-wise hyperbolic tangent."""
    if isinstance(x, (int, float)):
        return round(np.tanh(x), 4)
    elif isinstance(x, (list, tuple)):
        return [round(np.tanh(val), 4) for val in x]
    elif isinstance(x, np.ndarray):
        return np.round(np.tanh(x), 4)
    else:
        raise TypeError('Input must be a scalar, list, tuple, or NumPy array.')
def relu(x):
    """Implement element-wise Rectified Linear Unit."""
    if isinstance(x, (int, float)):
        return round(max(0, x), 4)
    elif isinstance(x, (list, tuple)):
        return [round(max(0, val), 4) for val in x]
    elif isinstance(x, np.ndarray):
        return np.round(np.maximum(0, x), 4)
    else:
        raise TypeError('Input must be a scalar, list, tuple, or NumPy array.')
def leaky_relu(x, alpha: float=0.01):
    """Implement element-wise Leaky-ReLU with slope *alpha* for x<0."""
    if isinstance(x, (int, float)):
        return round(max(alpha * x, x), 4)
    elif isinstance(x, (list, tuple)):
        return [round(max(alpha * val, val), 4) for val in x]
    elif isinstance(x, np.ndarray):
        return np.round(np.maximum(alpha * x, x), 4)
    else:
        raise TypeError('Input must be a scalar, list, tuple, or NumPy array.')
def softmax(x):
    """Implement numerically stable softmax over a 1-D input."""
    if not isinstance(x, np.ndarray) or x.ndim != 1:
        raise ValueError('Input must be a 1-D NumPy array.')
    x_exp = np.exp(x - np.max(x))
    sum_exp = np.sum(x_exp)
    return np.round(x_exp / sum_exp, 4)
def get_activation(name):
    """Return the activation function by *name*.

    Args:
        name (str): One of {"sigmoid", "tanh", "relu", "leaky_relu", "softmax"}

    Returns:
        Callable: the requested activation function.

    Raises:
        ValueError: If *name* is not recognised.
    """
    if name == 'sigmoid':
        return sigmoid
    elif name == 'tanh':
        return tanh
    elif name == 'relu':
        return relu
    elif name == 'leaky_relu':
        return lambda x: leaky_relu(x, alpha=0.01)
    elif name == 'softmax':
        return softmax
