from copy import deepcopy
from copy import deepcopy
from collections import defaultdict
def deduce_mines(constraints: list[list]) -> tuple[list[str], list[str]]:
    """Simplify Minesweeper constraints using logical deduction rules."""
    constraints = [deepcopy(c) for c in constraints]
    known_safe = set()
    known_mines = set()
    active_constraints = deepcopy(constraints)
    while True:
        new_info = False
        updated_constraints = []
        for constraint in active_constraints:
            (variables, value) = constraint
            if value == 0:
                for var in variables:
                    if var not in known_mines:
                        known_safe.add(var)
                        new_info = True
            elif value == len(variables):
                for var in variables:
                    if var not in known_safe:
                        known_mines.add(var)
                        new_info = True
            else:
                updated_constraints.append(constraint)
        if new_info:
            for var in known_safe:
                for constraint in updated_constraints:
                    if var in constraint[0]:
                        idx = constraint[0].index(var)
                        constraint[1] -= 1
                        constraint[0].pop(idx)
            for var in known_mines:
                for constraint in updated_constraints:
                    if var in constraint[0]:
                        idx = constraint[0].index(var)
                        constraint[0].pop(idx)
            updated_constraints = [c for c in updated_constraints if len(c[0]) > 0]
        updated_constraints.sort(key=lambda x: len(x[0]))
        new_constraints = []
        seen = set()
        for (i, c1) in enumerate(updated_constraints):
            (variables1, value1) = c1
            for (j, c2) in enumerate(updated_constraints[i + 1:], start=i + 1):
                (variables2, value2) = c2
                if set(variables1).issubset(set(variables2)):
                    new_vars = [v for v in variables2 if v not in variables1]
                    new_value = value2 - value1
                    new_constraints.append([new_vars, new_value])
                    seen.add(j)
                elif set(variables2).issubset(set(variables1)):
                    new_vars = [v for v in variables1 if v not in variables2]
                    new_value = value1 - value2
                    new_constraints.append([new_vars, new_value])
                    seen.add(i)
        for (i, c) in enumerate(updated_constraints):
            if i not in seen:
                new_constraints.append(c)
        unique_constraints = []
        seen = set()
        for c in new_constraints:
            key = tuple(sorted(c[0])) + (c[1],)
            if key not in seen:
                seen.add(key)
                unique_constraints.append(c)
        if updated_constraints == unique_constraints:
            break
        active_constraints = unique_constraints
    safe = sorted(known_safe)
    mines = sorted(known_mines)
    return (safe, mines)
