import numpy as np
def glm_links():
    """Construct and return standard GLM link functions.

    Returns
    -------
    dict
        A three-entry dictionary (identity, log, logit) where each entry is a
        dictionary containing callables for the link, its inverse and its
        derivative with respect to μ. All functions must work with numpy
        scalars as well as 1-D/2-D numpy arrays via element-wise operations.
    """

    def identity_link(mu):
        return mu

    def identity_inv_link(eta):
        return eta

    def identity_derivative(mu):
        return np.ones_like(mu)

    def log_link(mu):
        return np.log(mu)

    def log_inv_link(eta):
        return np.exp(eta)

    def log_derivative(mu):
        return 1 / mu

    def logit_link(mu):
        return np.log(mu / (1 - mu))

    def logit_inv_link(eta):
        return 1 / (1 + np.exp(-eta))

    def logit_derivative(mu):
        return 1 / (mu * (1 - mu))
    return {'identity': {'link': identity_link, 'inv_link': identity_inv_link, 'derivative': identity_derivative}, 'log': {'link': log_link, 'inv_link': log_inv_link, 'derivative': log_derivative}, 'logit': {'link': logit_link, 'inv_link': logit_inv_link, 'derivative': logit_derivative}}

import numpy as np
def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]]: Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or −1 on invalid input.
    """
    n = len(S)
    m = len(B[0]) if B else 0
    for row in A:
        if len(row) != n:
            return -1
    for row in B:
        if len(row) != m:
            return -1
    if n != len(S):
        return -1
    if not observations:
        return -1
    for obs in observations:
        if not 0 <= obs < m:
            return -1
    T = len(observations)
    alpha = np.zeros((T, n))
    for i in range(n):
        alpha[0, i] = S[i] * B[i][observations[0]]
    for t in range(1, T):
        for j in range(n):
            for i in range(n):
                alpha[t, j] += alpha[t - 1, i] * A[i][j] * B[j][observations[t]]
    total_prob = np.sum(alpha[T - 1, :])
    return round(total_prob, 4)

