import math
def escape_fire_maze(grid: list[str]) -> int:
    n = len(grid)
    if n == 1:
        if grid[0][0] == '.':
            return 0
        else:
            return -1
    fire_time = [[math.inf] * n for _ in range(n)]
    fire_queue = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_time[i][j] = 0
                fire_queue.append((i, j))
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    while fire_queue:
        (x, y) = fire_queue.popleft()
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (grid[nx][ny] != '#') and (fire_time[nx][ny] > fire_time[x][y] + 1):
                fire_time[nx][ny] = fire_time[x][y] + 1
                fire_queue.append((nx, ny))
    if grid[0][0] == 'F' or grid[n - 1][n - 1] == 'F':
        return -1
    agent_queue = deque()
    agent_queue.append((0, 0, 0))
    visited = [[False] * n for _ in range(n)]
    visited[0][0] = True
    while agent_queue:
        (x, y, t) = agent_queue.popleft()
        if x == n - 1 and y == n - 1:
            return t
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (not visited[nx][ny]) and (grid[nx][ny] != '#'):
                if fire_time[nx][ny] > t + 1:
                    visited[nx][ny] = True
                    agent_queue.append((nx, ny, t + 1))
    return -1

import numpy as np
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """
    tree = build_tree(X_train, y_train, max_depth)
    return predict(tree, X_test)
def build_tree(X, y, max_depth=None):
    """Build a decision tree recursively."""
    if len(np.unique(y)) == 1:
        return LeafNode(value=np.unique(y)[0])
    if max_depth is not None and max_depth <= 0:
        return LeafNode(value=most_common_class(y))
    best_gain = -1
    best_feature = None
    best_threshold = None
    for feature_idx in range(X.shape[1]):
        sorted_indices = np.argsort(X[:, feature_idx])
        feature_values = X[sorted_indices, feature_idx]
        unique_values = np.unique(feature_values)
        for i in range(1, len(unique_values)):
            threshold = (unique_values[i - 1] + unique_values[i]) / 2
            left_mask = X[:, feature_idx] <= threshold
            right_mask = ~left_mask
            if np.sum(left_mask) == 0 or np.sum(right_mask) == 0:
                continue
            gini = gini_impurity(y)
            left_gini = gini_impurity(y[left_mask])
            right_gini = gini_impurity(y[right_mask])
            weighted_gini = np.sum(left_mask) / len(y) * left_gini + np.sum(right_mask) / len(y) * right_gini
            gain = gini - weighted_gini
            if gain > best_gain:
                best_gain = gain
                best_feature = feature_idx
                best_threshold = threshold
    if best_gain < 0:
        return LeafNode(value=most_common_class(y))
    left_mask = X[:, best_feature] <= best_threshold
    right_mask = ~left_mask
    left_subtree = build_tree(X[left_mask], y[left_mask], max_depth - 1 if max_depth is not None else None)
    right_subtree = build_tree(X[right_mask], y[right_mask], max_depth - 1 if max_depth is not None else None)
    return InternalNode(feature=best_feature, threshold=best_threshold, left=left_subtree, right=right_subtree)
def predict(tree, X):
    """Predict class labels for a given test set using the decision tree."""
    return [tree.predict(x) for x in X]
class Node:
    """Base class for nodes in the decision tree."""

    def predict(self, x):
        raise NotImplementedError('This method should be overridden by subclasses.')
class LeafNode(Node):
    """Leaf node representing a final classification."""

    def __init__(self, value):
        self.value = value

    def predict(self, x):
        return self.value
class InternalNode(Node):
    """Internal node representing a decision based on a feature and threshold."""

    def __init__(self, feature, threshold, left, right):
        self.feature = feature
        self.threshold = threshold
        self.left = left
        self.right = right

    def predict(self, x):
        if x[self.feature] <= self.threshold:
            return self.left.predict(x)
        else:
            return self.right.predict(x)
def gini_impurity(y):
    """Compute the Gini impurity of a set of class labels."""
    counts = np.bincount(y)
    probabilities = counts / len(y)
    return 1 - np.sum(probabilities ** 2)
def most_common_class(y):
    """Return the most common class in the array."""
    return np.bincount(y).argmax()

import numpy as np
from typing import Callable, Iterable
def metric(name: str) -> Callable[[Iterable, Iterable], float]:
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float]: A distance function with
        integrated validation.
    """

    def validate_vectors(x, y):
        if not all((isinstance(arg, Iterable) for arg in (x, y))):
            return -1
        x = list(x)
        y = list(y)
        if len(x) != len(y):
            return -1
        if not x or not y:
            return -1
        if len(x) != len(y):
            return -1
        return (x, y)

    def euclidean(x, y):
        diff = np.array(x) - np.array(y)
        return round(np.sqrt(np.sum(diff ** 2)), 4)

    def manhattan(x, y):
        diff = np.array(x) - np.array(y)
        return round(np.sum(np.abs(diff)), 4)

    def chebyshev(x, y):
        diff = np.array(x) - np.array(y)
        return round(np.max(np.abs(diff)), 4)

    def cosine(x, y):
        x_vec = np.array(x)
        y_vec = np.array(y)
        dot_product = np.dot(x_vec, y_vec)
        norm_x = np.linalg.norm(x_vec)
        norm_y = np.linalg.norm(y_vec)
        if norm_x == 0 or norm_y == 0:
            return -1
        cosine_sim = dot_product / (norm_x * norm_y)
        return round(1 - cosine_sim, 4)
    metric_functions = {'euclidean': euclidean, 'manhattan': manhattan, 'chebyshev': chebyshev, 'cosine': cosine}
    if name not in metric_functions:
        return lambda x, y: -1
    metric_func = metric_functions[name]

    def d(x, y):
        result = validate_vectors(x, y)
        if result == -1:
            return -1
        (x, y) = result
        try:
            _ = [float(val) for val in x]
            _ = [float(val) for val in y]
        except (ValueError, TypeError):
            return -1
        return metric_func(x, y)
    return d

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function follows the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def calculate_hand_value(hand: list[int]) -> int:
        total = sum(hand)
        aces = hand.count(1)
        for _ in range(aces):
            if total + 10 <= 21:
                total += 10
            else:
                break
        return total
    player_value = calculate_hand_value(player)
    dealer_value = calculate_hand_value(dealer)
    is_player_natural_blackjack = len(player) == 2 and 1 in player and (10 in player)
    is_dealer_natural_blackjack = len(dealer) == 2 and 1 in dealer and (10 in dealer)
    if player_value > 21:
        return -1
    if dealer_value > 21:
        return 1
    if player_value > dealer_value:
        reward = 1
    elif player_value < dealer_value:
        reward = -1
    else:
        reward = 0
    if is_player_natural_blackjack and (not is_dealer_natural_blackjack):
        return 1.5
    return reward

import numpy as np
def leaky_relu(z, a=0.01):
    """Apply the Leaky ReLU activation to every element in *z*.

    Args:
        z: A scalar, list (possibly nested) or ``numpy.ndarray`` of numbers.
        a: Optional float in [0,1) — the slope for negative inputs. Defaults to 0.01.

    Returns:
        A Python list with the same structure as *z* where each value has been transformed
        by the Leaky ReLU activation.
    """
    if isinstance(z, (list, tuple)):

        def apply_leaky_relu(data):
            if isinstance(data, (list, tuple)):
                return [apply_leaky_relu(item) for item in data]
            elif isinstance(data, np.ndarray):
                return leaky_relu(data, a).tolist()
            else:
                return data if data >= 0 else a * data
        return apply_leaky_relu(z)
    elif isinstance(z, np.ndarray):
        return (z * (z >= 0) + a * z * (z < 0)).tolist()
    else:
        return z if z >= 0 else a * z

import numpy as np
from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
@dataclass
class Node:
    """An internal decision-tree node.

    Attributes:
        feature (int):   Index of the feature to test.
        threshold (float): Threshold that splits the data.
        left (Node | Leaf):  Sub-tree for samples with feature value < threshold.
        right (Node | Leaf): Sub-tree for samples with feature value ≥ threshold.
    """
    feature: int
    threshold: float
    left: object
    right: object
def compare_trees(tree_a, tree_b):
    """Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if isinstance(tree_a, Leaf) and isinstance(tree_b, Leaf):
        return np.allclose(tree_a.value, tree_b.value)
    if isinstance(tree_a, Leaf) != isinstance(tree_b, Leaf):
        return False
    if not isinstance(tree_a, Node) or not isinstance(tree_b, Node):
        return False
    if abs(tree_a.feature - tree_b.feature) > 1e-08:
        return False
    if abs(tree_a.threshold - tree_b.threshold) > 1e-08:
        return False
    return compare_trees(tree_a.left, tree_b.left) and compare_trees(tree_a.right, tree_b.right)

