from collections import Counter, defaultdict
from typing import List
def ngram_log_prob(corpus: List[str], query: str, N: int) -> float:
    """Return the total log-probability of *query* under an N-gram MLE model.

    Parameters
    ----------
    corpus : list[str]
        A list of training sentences. Each sentence is a plain string; tokens
        are assumed to be separated by white-spaces.
    query : str
        The sentence whose probability should be calculated.
    N : int
        The order of the N-gram model (N ≥ 1).

    Returns
    -------
    float
        The sum of natural logarithms of the probabilities of every
        consecutive N-gram occurring in *query*, rounded to 4 decimal places.
        If any necessary N-gram is unseen in the corpus the function returns
        float('-inf').
    """
    if N < 1:
        raise ValueError('N must be at least 1.')
    padded_corpus = []
    for sentence in corpus:
        tokens = sentence.split()
        if N > 1:
            padded_tokens = ['<bol>'] * (N - 1) + tokens + ['<eol>']
        else:
            padded_tokens = tokens
        padded_corpus.append(padded_tokens)
    ngram_counts = defaultdict(int)
    prefix_counts = defaultdict(int)
    for sentence in padded_corpus:
        for i in range(len(sentence) - N + 1):
            ngram = tuple(sentence[i:i + N])
            prefix = tuple(sentence[i:i + N - 1])
            ngram_counts[ngram] += 1
            prefix_counts[prefix] += 1
    tokens = query.split()
    if N == 1:
        if len(tokens) < 1:
            return float('-inf')
        token_freq = Counter(tokens)
        total_tokens = sum(token_freq.values())
        if total_tokens == 0:
            return float('-inf')
        log_prob = 0.0
        for token in tokens:
            count = token_freq[token]
            if count == 0:
                return float('-inf')
            prob = count / total_tokens
            log_prob += math.log(prob)
        return round(log_prob, 4)
    else:
        if len(tokens) < N - 1:
            return float('-inf')
        padded_query = ['<bol>'] * (N - 1) + tokens + ['<eol>']
        log_prob = 0.0
        for i in range(len(padded_query) - N + 1):
            ngram = tuple(padded_query[i:i + N])
            prefix = tuple(padded_query[i:i + N - 1])
            if prefix_counts[prefix] == 0 or ngram_counts[ngram] == 0:
                return float('-inf')
            prob = ngram_counts[ngram] / prefix_counts[prefix]
            log_prob += math.log(prob)
        return round(log_prob, 4)

import numpy as np
def mel_filterbank(N: int, n_filters: int=20, fs: int=44000, min_freq: int=0, max_freq: int | None=None, normalize: bool=True) -> list[list[float]]:
    """Build a Mel filterbank transformation matrix.

    The returned matrix has *n_filters* rows and *N//2 + 1* columns.  Each row
    is a triangular filter defined on the Mel scale.  See the detailed task
    description for the exact algorithm that has to be implemented.
    """
    if max_freq is None:
        max_freq = fs / 2

    def hz_to_mel(f):
        return 2595 * np.log10(1 + f / 700)

    def mel_to_hz(m):
        return 700 * (10 ** (m / 2595) - 1)
    low_mel = hz_to_mel(min_freq)
    high_mel = hz_to_mel(max_freq)
    mel_points = np.linspace(low_mel, high_mel, n_filters + 2)
    f_points = [mel_to_hz(m) for m in mel_points]
    n_fft = N
    n_bins = N // 2 + 1
    F = [[0.0 for _ in range(n_bins)] for _ in range(n_filters)]
    for i in range(n_filters):
        f_left = f_points[i]
        f_center = f_points[i + 1]
        f_right = f_points[i + 2]
        for k in range(n_bins):
            freq_k = k * fs / n_fft
            if freq_k < f_left:
                weight = 0.0
            elif f_left <= freq_k < f_center:
                weight = (freq_k - f_left) / (f_center - f_left)
            elif f_center <= freq_k < f_right:
                weight = (f_right - freq_k) / (f_right - f_center)
            else:
                weight = 0.0
            F[i][k] = weight
    if normalize:
        for i in range(n_filters):
            f_left = f_points[i]
            f_right = f_points[i + 2]
            if f_right != f_left:
                for k in range(n_bins):
                    F[i][k] *= 2 / (f_right - f_left)
    for i in range(n_filters):
        for k in range(n_bins):
            F[i][k] = round(F[i][k], 4)
    return F

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """
    if isinstance(env.action_space, Box):
        cont_action = True
    elif tuple_action:
        if isinstance(env.action_space, (Tuple, Dict)):
            cont_action = all((is_continuous_subspace(space) for space in get_subspaces(env.action_space)))
        else:
            cont_action = False
    else:
        cont_action = False
    if isinstance(env.observation_space, Box):
        cont_obs = True
    elif tuple_obs:
        if isinstance(env.observation_space, (Tuple, Dict)):
            cont_obs = all((is_continuous_subspace(space) for space in get_subspaces(env.observation_space)))
        else:
            cont_obs = False
    else:
        cont_obs = False
    return (cont_action, cont_obs)
def is_continuous_subspace(space: Space) -> bool:
    """Helper function to determine if a subspace is continuous."""
    return isinstance(space, Box)
def get_subspaces(space: Space) -> Iterable[Space]:
    """Helper function to extract subspaces from a Tuple or Dict."""
    if isinstance(space, Tuple):
        return space.spaces
    elif isinstance(space, Dict):
        return space.spaces.values()
    else:
        return []

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in [0, 1, 2]:
        return -1
    x_array = np.array(x)
    if order == 0:
        result = 1 / (1 + np.exp(-x_array))
    elif order == 1:
        sigma = 1 / (1 + np.exp(-x_array))
        result = sigma * (1 - sigma)
    elif order == 2:
        sigma = 1 / (1 + np.exp(-x_array))
        result = sigma * (1 - sigma) * (1 - 2 * sigma)
    result = np.round(result, 4)
    if np.isscalar(x):
        return float(result)
    else:
        return result.tolist()

import numpy as np
from typing import List, Union
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    if Y is None:
        Y = X
    X_np = np.array(X)
    Y_np = np.array(Y)
    if X_np.shape[1] != Y_np.shape[1]:
        return -1
    C = X_np.shape[1]
    if sigma is None:
        sigma = np.sqrt(C / 2)
    elif isinstance(sigma, float):
        if sigma <= 0:
            return -1
    elif isinstance(sigma, list):
        if len(sigma) != C or any((s <= 0 for s in sigma)):
            return -1
    else:
        return -1
    diff = X_np[:, np.newaxis, :] - Y_np[np.newaxis, :, :]
    squared_diff = np.sum(diff ** 2, axis=2)
    if isinstance(sigma, float):
        sigma_squared = sigma ** 2
        scaled_squared_diff = squared_diff / sigma_squared
    else:
        sigma_squared = np.array(sigma) ** 2
        scaled_squared_diff = np.sum(diff ** 2 / sigma_squared[:, np.newaxis], axis=2)
    kernel_matrix = np.exp(-0.5 * scaled_squared_diff)
    kernel_list = [[round(val, 4) for val in row] for row in kernel_matrix]
    return kernel_list

from collections import Counter, defaultdict
import itertools
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : list of list of hashable items
        A collection of transactions.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    item_counts = Counter()
    for transaction in transactions:
        item_counts.update(transaction)
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_sup}
    frequent_items = dict(sorted(frequent_items.items()))
    item_rank = {item: idx for (idx, item) in enumerate(frequent_items.keys(), start=1)}
    item_rank_rev = {idx: item for (item, idx) in item_rank.items()}

    class Node:

        def __init__(self, name, count, parent=None):
            self.name = name
            self.count = count
            self.parent = parent
            self.children = {}
            self.next = None

    def build_tree(transactions, head_table, item_rank):
        tree = None
        for transaction in transactions:
            ordered_items = [item for item in transaction if item in frequent_items]
            ordered_items.sort(key=lambda x: -item_rank[x])
            if not ordered_items:
                continue
            prefix_path = []
            node = tree
            for item in ordered_items:
                if not node:
                    node = Node(item, 1)
                    tree = node
                elif item in node.children:
                    node.children[item].count += 1
                    node = node.children[item]
                else:
                    new_node = Node(item, 1)
                    node.children[item] = new_node
                    if head_table[item] is None:
                        head_table[item] = new_node
                    else:
                        current = head_table[item]
