    head_table = defaultdict(lambda : None)
    (tree, head_table) = build_tree(transactions, head_table, item_rank)

    def mine(tree, head_table, prefix, frequent_itemsets):
        if tree:
            current_items = [node.name for node in prefix]
            if len(current_items) > 0:
                frequent_itemsets.append(tuple(sorted(current_items)))
            for child in tree.children.values():
                mine(child, head_table, prefix + [child], frequent_itemsets)
        elif head_table:
