import numpy as np
import numpy as np
class RegressionTree:

    def __init__(self, max_depth=3):
        self.max_depth = max_depth
        self.tree = {}

    def _best_split(self, X, y):
        m = len(y)
        if m <= 1:
            return (None, None, None)
        parent_var = np.var(y) * m
        best_var = parent_var
        best_idx = None
        best_thr = None
        for idx in range(X.shape[1]):
            thresholds = np.unique(X[:, idx])
            for thr in thresholds:
                left_mask = X[:, idx] <= thr
                right_mask = ~left_mask
                if np.sum(left_mask) == 0 or np.sum(right_mask) == 0:
                    continue
                var = np.sum(np.var(y[left_mask]) * np.sum(left_mask)) + np.sum(np.var(y[right_mask]) * np.sum(right_mask))
                if var < best_var:
                    best_var = var
                    best_idx = idx
                    best_thr = thr
        if best_idx is None:
            return (None, None, None)
        return (best_idx, best_thr, best_var)

    def _build_tree(self, X, y, depth=0):
        n_samples = len(y)
        if depth >= self.max_depth or n_samples <= 1:
            return np.mean(y)
        (idx, thr, var) = self._best_split(X, y)
        if idx is None:
            return np.mean(y)
        left_mask = X[:, idx] <= thr
        right_mask = ~left_mask
        left = self._build_tree(X[left_mask], y[left_mask], depth + 1)
        right = self._build_tree(X[right_mask], y[right_mask], depth + 1)
        return {'index': idx, 'threshold': thr, 'left': left, 'right': right}

    def fit(self, X, y):
        self.tree = self._build_tree(X, y)
        return self

    def _predict_single(self, x, tree):
        if isinstance(tree, dict):
            if x[tree['index']] <= tree['threshold']:
                return self._predict_single(x, tree['left'])
            else:
                return self._predict_single(x, tree['right'])
        return tree

    def predict(self, X):
        return np.array([self._predict_single(x, self.tree) for x in X])
def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """Gradient Boosting Decision Tree (GBDT) regressor."""
    base_pred = np.mean(y_train)
    y_pred_train = np.full_like(y_train, base_pred)
    y_pred_test = np.full(X_test.shape[0], base_pred)
    for _ in range(n_estimators):
        residuals = y_train - y_pred_train
        tree = RegressionTree(max_depth=max_depth)
        tree.fit(X_train, residuals)
        update_train = tree.predict(X_train)
        update_test = tree.predict(X_test)
        y_pred_train += learning_rate * update_train
        y_pred_test += learning_rate * update_test
    return [round(float(x), 4) for x in y_pred_test]
