import math
def c(n: int) -> float:
    """Compute the expected path length correction."""
    if n <= 1:
        return 0
    return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n
def compute_phi(n: int) -> float:
    """Compute the normalization factor φ."""
    return 2 * math.log(n - 1) - 2 * (n - 1) / n
class IsolationTree:

    def __init__(self, height_limit: int):
        self.height_limit = height_limit
        self.split_feature = None
        self.split_threshold = None
        self.size = 0
        self.left = None
        self.right = None

    def fit(self, X: np.ndarray, current_height: int=0):
        """Build the isolation tree recursively."""
        self.size = len(X)
        if current_height >= self.height_limit or self.size <= 1:
            return
        n_features = X.shape[1]
        self.split_feature = np.random.randint(0, n_features)
        feature_min = X[:, self.split_feature].min()
        feature_max = X[:, self.split_feature].max()
        if feature_min == feature_max:
            return
        self.split_threshold = np.random.uniform(feature_min, feature_max)
        left_mask = X[:, self.split_feature] < self.split_threshold
        X_left = X[left_mask]
        X_right = X[~left_mask]
        if 0 < len(X_left) < len(X):
            self.left = IsolationTree(self.height_limit)
            self.left.fit(X_left, current_height + 1)
        if 0 < len(X_right) < len(X):
            self.right = IsolationTree(self.height_limit)
            self.right.fit(X_right, current_height + 1)

    def path_length(self, x: np.ndarray, current_height: int=0) -> float:
        """Compute the path length of a sample."""
        if self.left is None and self.right is None:
            return current_height + c(self.size)
        if x[self.split_feature] < self.split_threshold:
            if self.left is None:
                return current_height + c(self.size)
            return self.left.path_length(x, current_height + 1)
        else:
            if self.right is None:
                return current_height + c(self.size)
            return self.right.path_length(x, current_height + 1)
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation."""
    np.random.seed(42)
    if epsilon <= 0:
        return []
    n_samples = data.shape[0]
    actual_sample_size = min(sample_size, n_samples)
    height_limit = math.ceil(math.log2(actual_sample_size))
    trees = []
    for _ in range(n_trees):
        indices = np.random.choice(n_samples, actual_sample_size, replace=False)
        tree = IsolationTree(height_limit)
        tree.fit(data[indices])
        trees.append(tree)
    path_lengths = np.zeros(n_samples)
    for i in range(n_samples):
        sample_path_lengths = [tree.path_length(data[i]) for tree in trees]
        path_lengths[i] = np.mean(sample_path_lengths)
    phi = compute_phi(n_samples)
    scores = 2 ** (-path_lengths / phi)
    n_outliers = int(epsilon * n_samples)
    if n_outliers == 0:
        return []
    outlier_indices = np.argsort(scores)[-n_outliers:]
    return sorted(outlier_indices.tolist())
