def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    unique_labels = np.unique(y_train)
    n_labels = len(unique_labels)
    n_features = X_train.shape[1]
    feature_values = {}
    for j in range(n_features):
        feature_values[j] = np.unique(X_train[:, j])
    feature_funcs = {}
    curr_idx = 0
    for feat_idx in range(n_features):
        for feat_val in feature_values[feat_idx]:
            for label in unique_labels:
                feature_funcs[feat_idx, feat_val, label] = curr_idx
                curr_idx += 1
    n_feature_funcs = len(feature_funcs)
    weights = np.zeros(n_feature_funcs)
    emp_exp = np.zeros(n_feature_funcs)
    for i in range(len(X_train)):
        (x, y) = (X_train[i], y_train[i])
        for j in range(n_features):
            feat_val = x[j]
            if (j, feat_val, y) in feature_funcs:
                idx = feature_funcs[j, feat_val, y]
                emp_exp[idx] += 1
    emp_exp /= len(X_train)
    for iteration in range(n_iter):
        model_exp = np.zeros(n_feature_funcs)
        max_update = 0
        for i in range(len(X_train)):
            x = X_train[i]
            Z_x = 0
            label_scores = {}
            for y in unique_labels:
                score = 0
                for j in range(n_features):
                    feat_val = x[j]
                    if (j, feat_val, y) in feature_funcs:
                        idx = feature_funcs[j, feat_val, y]
                        score += weights[idx]
                label_scores[y] = score
                Z_x += math.exp(score)
            for y in unique_labels:
                p_y_given_x = math.exp(label_scores[y]) / Z_x
                for j in range(n_features):
                    feat_val = x[j]
                    if (j, feat_val, y) in feature_funcs:
                        idx = feature_funcs[j, feat_val, y]
                        model_exp[idx] += p_y_given_x
        model_exp /= len(X_train)
        for j in range(n_feature_funcs):
            if emp_exp[j] > 0:
                update = math.log(emp_exp[j] / model_exp[j]) if model_exp[j] > 0 else 0
                weights[j] += update
                max_update = max(max_update, abs(update))
        if max_update < epsilon:
            break
    predictions = []
    for x in X_test:
        label_probs = {}
        Z_x = 0
        for y in unique_labels:
            score = 0
            for j in range(n_features):
                feat_val = x[j]
                if (j, feat_val, y) in feature_funcs:
                    idx = feature_funcs[j, feat_val, y]
                    score += weights[idx]
            label_probs[y] = math.exp(score)
            Z_x += label_probs[y]
        for y in label_probs:
            label_probs[y] /= Z_x
        best_label = max(label_probs.items(), key=lambda x: x[1])[0]
        predictions.append(best_label)
    return predictions
