import numpy as np
def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*."""
    if N == 1:
        unigram_counts = Counter(corpus)
        corpus_length = len(corpus)
        total_log_prob = 0
        for token in sequence:
            count = unigram_counts.get(token, 0)
            if count == 0:
                return float('-inf')
            total_log_prob += np.log(count / corpus_length)
        return round(total_log_prob, 4)
    ngram_counts = Counter()
    n_minus_1_gram_counts = Counter()
    for i in range(len(corpus) - N + 1):
        ngram = tuple(corpus[i:i + N])
        n_minus_1_gram = tuple(corpus[i:i + N - 1])
        ngram_counts[ngram] += 1
        n_minus_1_gram_counts[n_minus_1_gram] += 1
    total_log_prob = 0
    for i in range(len(sequence) - N + 1):
        current_ngram = tuple(sequence[i:i + N])
        current_n_minus_1_gram = tuple(sequence[i:i + N - 1])
        ngram_count = ngram_counts.get(current_ngram, 0)
        n_minus_1_gram_count = n_minus_1_gram_counts.get(current_n_minus_1_gram, 0)
        if ngram_count == 0 or n_minus_1_gram_count == 0:
            return float('-inf')
        total_log_prob += np.log(ngram_count / n_minus_1_gram_count)
    return round(total_log_prob, 4)

import numpy as np
def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]): Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or −1 on invalid input.
    """
    try:
        S = np.array(S)
        A = np.array(A)
        B = np.array(B)
        observations = np.array(observations)
    except:
        return -1
    if len(observations) == 0:
        return -1
    n_states = len(S)
    if n_states == 0:
        return -1
    if S.shape != (n_states,) or A.shape != (n_states, n_states) or len(B.shape) != 2 or (B.shape[0] != n_states):
        return -1
    n_symbols = B.shape[1]
    if np.any(observations < 0) or np.any(observations >= n_symbols):
        return -1
    forward = np.zeros((len(observations), n_states))
    try:
        forward[0] = S * B[:, observations[0]]
        for t in range(1, len(observations)):
            for j in range(n_states):
                forward[t, j] = np.sum(forward[t - 1] * A[:, j]) * B[j, observations[t]]
        result = np.sum(forward[-1])
        if not np.isfinite(result) or result < 0:
            return -1
        return round(float(result), 4)
    except:
        return -1

from collections import Counter, defaultdict
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result must be deterministic:
      • Inside each item-set the items have to be sorted alphabetically.
      • The outer list has to be sorted by `(len(itemset), itemset)`.
    If *transactions* is empty or no item-set meets the threshold return an
    empty list.
    """
    if not transactions or min_support < 1:
        return []
    item_counts = Counter((item for trans in transactions for item in trans))
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_support}
    if not frequent_items:
        return []
    items_by_freq = sorted(frequent_items.items(), key=lambda x: (-x[1], x[0]))
    item_order = {item: i for (i, (item, _)) in enumerate(items_by_freq)}

    class FPNode:

        def __init__(self, item=None, count=0):
            self.item = item
            self.count = count
            self.children = {}
            self.parent = None
            self.link = None

    def build_fptree():
        root = FPNode()
        header_table = defaultdict(list)
        for transaction in transactions:
            filtered_trans = [item for item in transaction if item in frequent_items]
            filtered_trans.sort(key=lambda x: item_order[x])
            if filtered_trans:
                insert_tree(filtered_trans, root, header_table)
        return (root, header_table)

    def insert_tree(items, node, header_table):
        first_item = items[0]
        child = node.children.get(first_item)
        if child is None:
            child = FPNode(first_item, 1)
            child.parent = node
            node.children[first_item] = child
            if header_table[first_item]:
                current = header_table[first_item][-1]
                current.link = child
            header_table[first_item].append(child)
        else:
            child.count += 1
        if len(items) > 1:
            insert_tree(items[1:], child, header_table)

    def find_prefix_path(node):
        path = []
        while node.parent and node.parent.item is not None:
            path.append(node.parent.item)
            node = node.parent
        return path

    def mine_tree(header_table, prefix, frequent_itemsets):
        sorted_items = sorted(header_table.keys(), key=lambda x: item_order[x])
        for item in sorted_items:
            new_prefix = prefix + [item]
            new_prefix.sort()
            frequent_itemsets.append(new_prefix)
            cond_pattern_base = []
            node = header_table[item][0]
            while node:
                prefix_path = find_prefix_path(node)
                if prefix_path:
                    cond_pattern_base.append((prefix_path, node.count))
                node = node.link
            cond_tree_transactions = []
            for (path, count) in cond_pattern_base:
                cond_tree_transactions.extend([path] * count)
            if cond_tree_transactions:
                cond_fp_growth(cond_tree_transactions, min_support, new_prefix, frequent_itemsets)

    def cond_fp_growth(transactions, min_support, prefix, frequent_itemsets):
        item_counts = Counter((item for trans in transactions for item in trans))
        frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_support}
        if not frequent_items:
            return
        root = FPNode()
        header_table = defaultdict(list)
        for transaction in transactions:
            filtered_trans = [item for item in transaction if item in frequent_items]
            filtered_trans.sort(key=lambda x: item_order[x])
            if filtered_trans:
                insert_tree(filtered_trans, root, header_table)
        mine_tree(header_table, prefix, frequent_itemsets)
    (root, header_table) = build_fptree()
    frequent_itemsets = []
    mine_tree(header_table, [], frequent_itemsets)
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    for (item, count) in frequent_items.items():
        if count >= min_support:
            frequent_itemsets.insert(0, [item])
    return frequent_itemsets

import numpy as np
import random
def kmeans(X: np.ndarray, K: int, max_iter: int=100, random_state: int | None=None) -> list[list[float]]:
    """Perform K-Means clustering with K-Means++ initialisation."""
    if random_state is not None:
        random.seed(random_state)
        np.random.seed(random_state)
    (m, n) = X.shape

    def squared_distance(x1, x2):
        return np.sum((x1 - x2) ** 2)
    centroids = [X[random.randint(0, m - 1)].tolist()]
    for _ in range(1, K):
        distances = np.array([min((squared_distance(x, c) for c in centroids)) for x in X])
        probs = distances / distances.sum()
        cumulative_probs = np.cumsum(probs)
        r = random.random()
        for (i, p) in enumerate(cumulative_probs):
            if r <= p:
                centroids.append(X[i].tolist())
                break
    centroids = np.array(centroids)
    prev_labels = None
    for _ in range(max_iter):
        distances = np.array([[squared_distance(x, c) for c in centroids] for x in X])
        labels = np.argmin(distances, axis=1)
        if prev_labels is not None and np.array_equal(labels, prev_labels):
            break
        prev_labels = labels
        new_centroids = np.zeros_like(centroids)
        for k in range(K):
            points_k = X[labels == k]
            if len(points_k) > 0:
                new_centroids[k] = points_k.mean(axis=0)
            else:
                new_centroids[k] = centroids[k]
        centroids = new_centroids
    centroids = np.round(centroids, 4)
    centroids_list = centroids.tolist()
    centroids_list.sort(key=lambda x: (x[0], tuple(x)))
    return centroids_list

from collections import deque
import math
def escape_fire_maze(grid: list[str]) -> int:
    """Escape from a maze with spreading fire.

    Uses BFS to simultaneously track fire spread and agent movement.
    Fire spreads first, then agent moves.
    """
    n = len(grid)
    maze = [list(row) for row in grid]
    if maze[0][0] == 'F' or maze[n - 1][n - 1] == 'F':
        return -1
    if n == 1 and maze[0][0] == '.':
        return 0
    fire_queue = deque()
    for i in range(n):
        for j in range(n):
            if maze[i][j] == 'F':
                fire_queue.append((i, j, 0))
    agent_queue = deque([(0, 0, 0)])
    visited = {(0, 0)}
    fire_times = [[math.inf] * n for _ in range(n)]
    for i in range(n):
        for j in range(n):
            if maze[i][j] == 'F':
                fire_times[i][j] = 0
            elif maze[i][j] == '#':
                fire_times[i][j] = 0
    directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]
    while fire_queue:
        (r, c, time) = fire_queue.popleft()
        for (dr, dc) in directions:
            (new_r, new_c) = (r + dr, c + dc)
            if 0 <= new_r < n and 0 <= new_c < n and (maze[new_r][new_c] == '.') and (fire_times[new_r][new_c] == math.inf):
                fire_times[new_r][new_c] = time + 1
                fire_queue.append((new_r, new_c, time + 1))
    while agent_queue:
        (r, c, time) = agent_queue.popleft()
        if r == n - 1 and c == n - 1:
            return time
        for (dr, dc) in directions:
            (new_r, new_c) = (r + dr, c + dc)
            if 0 <= new_r < n and 0 <= new_c < n and (maze[new_r][new_c] != '#') and ((new_r, new_c) not in visited) and (time + 1 < fire_times[new_r][new_c]):
                visited.add((new_r, new_c))
                agent_queue.append((new_r, new_c, time + 1))
    return -1

import numpy as np
def fit_lasso(X: np.ndarray, y: np.ndarray, lambda_param: float=1.0, max_iters: int=100, fit_intercept: bool=True) -> tuple[list[float], float]:
    """Fit Lasso (L1-regularised) linear regression using coordinate descent."""
    X = np.asarray(X)
    y = np.asarray(y)
    (m, n) = X.shape
    X_aug = np.column_stack([np.ones(m) if fit_intercept else np.zeros(m), X])
    w = np.zeros(n + 1)

    def soft_threshold(a, tau):
        """Soft-thresholding operator."""
        return np.sign(a) * np.maximum(np.abs(a) - tau, 0)
    for _ in range(max_iters):
        if fit_intercept:
            residual = y - X_aug[:, 1:] @ w[1:]
            w[0] = np.mean(residual)
        for j in range(1, n + 1):
            w_j_old = w[j]
            w[j] = 0
            r_j = y - X_aug @ w
            x_j = X_aug[:, j]
            dot_prod = np.dot(x_j, r_j)
            x_j_sq_sum = np.sum(x_j ** 2)
            if x_j_sq_sum == 0:
                w[j] = 0
                continue
            if j == 0:
                w[j] = dot_prod / x_j_sq_sum
            else:
                w[j] = soft_threshold(dot_prod, lambda_param * m) / x_j_sq_sum
            if abs(w[j] - w_j_old) < 1e-10:
                w[j] = w_j_old
    weights = [round(float(wi), 4) for wi in w[1:]]
    bias = round(float(w[0]), 4)
    return (weights, bias)

import numpy as np
from typing import Iterable, Union, Callable
def metric(name: str) -> Callable[[Iterable, Iterable], Union[float, int]]:
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def validate_and_convert(x, y):
        """Helper function to validate inputs and convert to numpy arrays."""
        try:
            x_arr = np.array(x, dtype=float)
            y_arr = np.array(y, dtype=float)
            if x_arr.ndim != 1 or y_arr.ndim != 1 or len(x_arr) == 0 or (len(y_arr) == 0):
                return None
            if len(x_arr) != len(y_arr):
                return None
            return (x_arr, y_arr)
        except (ValueError, TypeError):
            return None

    def euclidean(x, y):
        arrays = validate_and_convert(x, y)
        if arrays is None:
            return -1
        (x_arr, y_arr) = arrays
        return round(float(np.sqrt(np.sum((x_arr - y_arr) ** 2))), 4)

    def manhattan(x, y):
        arrays = validate_and_convert(x, y)
        if arrays is None:
            return -1
        (x_arr, y_arr) = arrays
        return round(float(np.sum(np.abs(x_arr - y_arr))), 4)

    def chebyshev(x, y):
        arrays = validate_and_convert(x, y)
        if arrays is None:
            return -1
        (x_arr, y_arr) = arrays
        return round(float(np.max(np.abs(x_arr - y_arr))), 4)

    def cosine(x, y):
        arrays = validate_and_convert(x, y)
        if arrays is None:
            return -1
        (x_arr, y_arr) = arrays
        x_norm = np.linalg.norm(x_arr)
        y_norm = np.linalg.norm(y_arr)
        if x_norm == 0 or y_norm == 0:
            return -1
        cos_sim = np.dot(x_arr, y_arr) / (x_norm * y_norm)
        cos_sim = min(1.0, max(-1.0, cos_sim))
        return round(float(1 - cos_sim), 4)
    metrics = {'euclidean': euclidean, 'manhattan': manhattan, 'chebyshev': chebyshev, 'cosine': cosine}
    return metrics.get(name, lambda x, y: -1)

def calculate_score(hand: list[int]) -> int:
    """Calculate the highest valid score for a hand."""
    total = sum(hand)
    if 1 in hand and total + 10 <= 21:
        total += 10
    return total
def is_natural_blackjack(hand: list[int]) -> bool:
    """Check if a hand is a natural blackjack."""
    return len(hand) == 2 and sorted(hand) == [1, 10]
def is_bust(score: int) -> bool:
    """Check if a score is a bust."""
    return score > 21
def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """
    player_score = calculate_score(player)
    dealer_score = calculate_score(dealer)
    if is_bust(player_score):
        return -1.0
    if is_bust(dealer_score):
        return 1.0 if not is_natural_blackjack(player) else 1.5
    if player_score > dealer_score:
        return 1.5 if is_natural_blackjack(player) else 1.0
    elif player_score < dealer_score:
        return -1.0
    else:
        return 0.0

