from collections import Counter
def entropy(labels):
    """Calculate Shannon entropy of a list of labels."""
    if not labels:
        return 0.0
    counts = Counter(labels)
    total = len(labels)
    return -sum((count / total * math.log2(count / total) for count in counts.values()))
def best_split(feature, target):
    """Determine the numerical threshold that produces the highest information gain.

    Parameters
    ----------
    feature : list[int | float] | 1-D numpy.ndarray
        Numerical values of a single attribute.
    target  : list[int] | 1-D numpy.ndarray
        Corresponding class labels.

    Returns
    -------
    tuple
        (threshold, information_gain) where
        * threshold – float rounded to 4 decimals or None when no useful split exists;
        * information_gain – float rounded to 4 decimals.
    """
    n = len(feature)
    if n == 0:
        return (None, 0.0)
    pairs = sorted(zip(feature, target))
    if pairs[0][0] == pairs[-1][0]:
        return (None, 0.0)
    parent_entropy = entropy(target)
    if parent_entropy == 0:
        return (None, 0.0)
    thresholds = []
    left_values = []
    for i in range(n - 1):
        if pairs[i][0] < pairs[i + 1][0]:
            threshold = (pairs[i][0] + pairs[i + 1][0]) / 2
            thresholds.append(threshold)
            left_values.append(i + 1)
    if not thresholds:
        return (None, 0.0)
    max_gain = 0.0
    best_threshold = None
    for (threshold, left_size) in zip(thresholds, left_values):
        left_labels = [pairs[i][1] for i in range(left_size)]
        right_labels = [pairs[i][1] for i in range(left_size, n)]
        left_entropy = entropy(left_labels)
        right_entropy = entropy(right_labels)
        gain = parent_entropy - (left_size / n * left_entropy + (n - left_size) / n * right_entropy)
        if gain > max_gain or (gain == max_gain and (best_threshold is None or threshold < best_threshold)):
            max_gain = gain
            best_threshold = threshold
    if max_gain <= 0:
        return (None, 0.0)
    return (round(best_threshold, 4), round(max_gain, 4))

import numpy as np
def average_pool2d(X: np.ndarray, pool_shape: tuple[int, int], stride: tuple[int, int], accum_grad: np.ndarray) -> tuple[list, list]:
    """Performs forward and backward passes of a 2-D average-pooling layer.

    The function must return a tuple (pooled_output, grad_input), both rounded
    to the nearest 4-th decimal and converted to regular Python lists using
    `tolist()`.
    """
    (N, C, H, W) = X.shape
    (p_h, p_w) = pool_shape
    (s_h, s_w) = stride
    H_out = (H - p_h) // s_h + 1
    W_out = (W - p_w) // s_w + 1
    output = np.zeros((N, C, H_out, W_out))
    for n in range(N):
        for c in range(C):
            for h in range(H_out):
                for w in range(W_out):
                    h_start = h * s_h
                    w_start = w * s_w
                    window = X[n, c, h_start:h_start + p_h, w_start:w_start + p_w]
                    output[n, c, h, w] = np.mean(window)
    grad_input = np.zeros_like(X)
    for n in range(N):
        for c in range(C):
            for h in range(H_out):
                for w in range(W_out):
                    h_start = h * s_h
                    w_start = w * s_w
                    grad_val = accum_grad[n, c, h, w] / (p_h * p_w)
                    grad_input[n, c, h_start:h_start + p_h, w_start:w_start + p_w] += grad_val
    output_rounded = np.round(output, decimals=4).tolist()
    grad_input_rounded = np.round(grad_input, decimals=4).tolist()
    return (output_rounded, grad_input_rounded)

