from typing import List
def ngram_log_prob(corpus: List[str], query: str, N: int) -> float:
    """Return the total log-probability of *query* under an N-gram MLE model."""

    def get_padded_tokens(sentence: str, n: int) -> list:
        """Split sentence into tokens and add padding if needed."""
        tokens = sentence.split()
        if n == 1:
            return tokens
        return ['<bol>'] * (n - 1) + tokens + ['<eol>']

    def get_ngrams(tokens: list, n: int) -> list:
        """Extract n-grams from token sequence."""
        return [tuple(tokens[i:i + n]) for i in range(len(tokens) - n + 1)]
    query_tokens = query.split()
    if len(query_tokens) < N:
        return float('-inf')
    ngram_counts = Counter()
    prefix_counts = Counter()
    for sentence in corpus:
        tokens = get_padded_tokens(sentence, N)
        if N == 1:
            ngram_counts.update(tokens)
            prefix_counts.update(['<total>'])
        else:
            ngrams = get_ngrams(tokens, N)
            prefixes = get_ngrams(tokens, N - 1)
            ngram_counts.update(ngrams)
            prefix_counts.update(prefixes)
    query_tokens = get_padded_tokens(query, N)
    query_ngrams = get_ngrams(query_tokens, N)
    total_log_prob = 0.0
    for ngram in query_ngrams:
        if N == 1:
            numerator = ngram_counts[ngram[0]]
            denominator = sum(ngram_counts.values())
        else:
            numerator = ngram_counts[ngram]
            prefix = ngram[:-1]
            denominator = prefix_counts[prefix]
        if numerator == 0 or denominator == 0:
            return float('-inf')
        prob = numerator / denominator
        total_log_prob += math.log(prob)
    return round(total_log_prob, 4)

