def build_fp_tree(transactions: list[list[str]], min_support: int) -> dict:
    """Construct an FP-Tree using only nested dictionaries."""
    item_counts = Counter((item for transaction in transactions for item in transaction))
    frequent_items = sorted([(item, count) for (item, count) in item_counts.items() if count >= min_support], key=lambda x: (-x[1], x[0]))
    frequent_item_set = {item for (item, _) in frequent_items}
    root = {'support': 0, 'children': {}}

    def insert_transaction(transaction: list[str], tree: dict) -> None:
        """Insert a single transaction into the tree."""
        current = tree
        for item in transaction:
            if item not in current['children']:
                current['children'][item] = {'support': 0, 'children': {}}
            current['children'][item]['support'] += 1
            current = current['children'][item]
    for transaction in transactions:
        filtered_transaction = []
        seen = set()
        for item in transaction:
            if item in frequent_item_set and item not in seen:
                filtered_transaction.append(item)
                seen.add(item)
        filtered_transaction.sort(key=lambda x: next((i for (i, (item, _)) in enumerate(frequent_items) if item == x)))
        if filtered_transaction:
            insert_transaction(filtered_transaction, root)
    return root

