def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    X = np.array(X, dtype=float)
    if Y is None:
        Y = X
    else:
        Y = np.array(Y, dtype=float)
    (N, C) = X.shape
    M = Y.shape[0]
    if Y.shape[1] != C:
        return -1
    if sigma is None:
        sigma = np.sqrt(C / 2)
    if isinstance(sigma, list):
        sigma = np.array(sigma, dtype=float)
        if len(sigma) != C:
            return -1
    else:
        sigma = float(sigma)
        sigma = np.full(C, sigma)
    if np.any(sigma <= 0):
        return -1
    try:
        diff = X[:, np.newaxis, :] - Y[np.newaxis, :, :]
        scaled_diff = diff / sigma
        squared_distances = np.sum(scaled_diff ** 2, axis=2)
        kernel_matrix = np.exp(-0.5 * squared_distances)
        result = np.round(kernel_matrix, decimals=4).tolist()
        return result
    except:
        return -1

