def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions. Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """

    def build_fptree(transactions, min_sup):
        item_counts = Counter((item for trans in transactions for item in trans))
        frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_sup}
        if not frequent_items:
            return (None, None)
        root = FPNode()
        header_table = defaultdict(list)
        for transaction in transactions:
            ordered_items = sorted([item for item in transaction if item in frequent_items], key=lambda x: (-frequent_items[x], x))
            if ordered_items:
                insert_tree(ordered_items, root, header_table)
        return (root, header_table)

    def insert_tree(items, node, header_table):
        first_item = items[0]
        child = node.children.get(first_item)
        if child is None:
            child = FPNode(first_item, 1, node)
            node.children[first_item] = child
            if header_table[first_item]:
                current = header_table[first_item][-1]
                current.next = child
            header_table[first_item].append(child)
        else:
            child.count += 1
        if len(items) > 1:
            insert_tree(items[1:], child, header_table)

    def get_path_to_root(node):
        path = []
        while node and node.item:
            path.append((node.item, node.count))
            node = node.parent
        return path

    def mine_tree(tree, header_table, prefix, min_sup, frequent_sets):
        items = sorted(header_table.keys())
        for item in items:
            new_prefix = prefix + (item,)
            support = sum((node.count for node in header_table[item]))
            if support >= min_sup:
                frequent_sets.append(new_prefix)
                patterns = []
                for node in header_table[item]:
                    pattern = get_path_to_root(node.parent)
                    if pattern:
                        patterns.append((pattern, node.count))
                if patterns:
                    (cond_tree, cond_header) = build_conditional_fptree(patterns, min_sup)
                    if cond_header:
                        mine_tree(cond_tree, cond_header, new_prefix, min_sup, frequent_sets)

    def build_conditional_fptree(patterns, min_sup):
        cond_tree = FPNode()
        cond_counts = defaultdict(int)
        for (pattern, count) in patterns:
            for (item, _) in pattern:
                cond_counts[item] += count
        cond_header = defaultdict(list)
        for (pattern, count) in patterns:
            frequent_pattern = [(item, count) for (item, _) in pattern if cond_counts[item] >= min_sup]
            if frequent_pattern:
                ordered_items = [item for (item, _) in sorted(frequent_pattern, key=lambda x: (-cond_counts[x[0]], x[0]))]
                insert_tree(ordered_items, cond_tree, cond_header)
        return (cond_tree, cond_header)
    frequent_sets = []
    (tree, header_table) = build_fptree(transactions, min_sup)
    if tree is not None:
        mine_tree(tree, header_table, (), min_sup, frequent_sets)
    return sorted(frequent_sets, key=lambda x: (len(x), x))

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """
    if isinstance(z, (int, float)):
        return float(np.round(z * (1 - z), 4))
    if not z:
        return []
    z_array = np.array(z)
    grad = z_array * (1 - z_array)
    grad = np.round(grad, 4)
    if isinstance(z, list):
        if z_array.ndim == 1:
            return grad.tolist()
        else:
            return [row.tolist() for row in grad]
    return grad

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if hasattr(root, 'y'):
        return root.y
    feature_value = features[root.label]
    if task_type == 'classification':
        for child in root.child:
            if hasattr(child, 'x') and child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    elif feature_value <= root.s:
        return predict_sample(root.child[0], features, task_type)
    else:
        return predict_sample(root.child[1], features, task_type)

