        """Build the isolation tree recursively."""
        self.size = len(X)
        if current_height >= self.height_limit or self.size <= 1:
            return
        n_features = X.shape[1]
        self.split_feature = np.random.randint(0, n_features)
        feature_min = X[:, self.split_feature].min()
        feature_max = X[:, self.split_feature].max()
        if feature_min == feature_max:
            return
        self.split_threshold = np.random.uniform(feature_min, feature_max)
        left_mask = X[:, self.split_feature] < self.split_threshold
        X_left = X[left_mask]
        X_right = X[~left_mask]
        if 0 < len(X_left) < len(X):
            self.left = IsolationTree(self.height_limit)
            self.left.fit(X_left, current_height + 1)
        if 0 < len(X_right) < len(X):
            self.right = IsolationTree(self.height_limit)
            self.right.fit(X_right, current_height + 1)

    def path_length(self, x: np.ndarray, current_height: int=0) -> float:
        """Compute the path length of a sample."""
        if self.left is None and self.right is None:
            return current_height + c(self.size)
        if x[self.split_feature] < self.split_threshold:
            if self.left is None:
                return current_height + c(self.size)
            return self.left.path_length(x, current_height + 1)
        else:
            if self.right is None:
                return current_height + c(self.size)
            return self.right.path_length(x, current_height + 1)
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation."""
    np.random.seed(42)
    if epsilon <= 0:
        return []
    n_samples = data.shape[0]
    actual_sample_size = min(sample_size, n_samples)
    height_limit = math.ceil(math.log2(actual_sample_size))
    trees = []
    for _ in range(n_trees):
        indices = np.random.choice(n_samples, actual_sample_size, replace=False)
        tree = IsolationTree(height_limit)
        tree.fit(data[indices])
        trees.append(tree)
    path_lengths = np.zeros(n_samples)
    for i in range(n_samples):
        sample_path_lengths = [tree.path_length(data[i]) for tree in trees]
        path_lengths[i] = np.mean(sample_path_lengths)
    phi = compute_phi(n_samples)
    scores = 2 ** (-path_lengths / phi)
    n_outliers = int(epsilon * n_samples)
    if n_outliers == 0:
        return []
    outlier_indices = np.argsort(scores)[-n_outliers:]
    return sorted(outlier_indices.tolist())

import math
from collections import defaultdict
import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    unique_labels = np.unique(y_train)
    n_labels = len(unique_labels)
    n_features = X_train.shape[1]
    feature_values = {}
    for j in range(n_features):
        feature_values[j] = np.unique(X_train[:, j])
    feature_funcs = {}
    curr_idx = 0
    for feat_idx in range(n_features):
        for feat_val in feature_values[feat_idx]:
            for label in unique_labels:
                feature_funcs[feat_idx, feat_val, label] = curr_idx
                curr_idx += 1
    n_feature_funcs = len(feature_funcs)
    weights = np.zeros(n_feature_funcs)
    emp_exp = np.zeros(n_feature_funcs)
    for i in range(len(X_train)):
        (x, y) = (X_train[i], y_train[i])
        for j in range(n_features):
            feat_val = x[j]
            if (j, feat_val, y) in feature_funcs:
                idx = feature_funcs[j, feat_val, y]
                emp_exp[idx] += 1
    emp_exp /= len(X_train)
    for iteration in range(n_iter):
        model_exp = np.zeros(n_feature_funcs)
        max_update = 0
        for i in range(len(X_train)):
            x = X_train[i]
            Z_x = 0
            label_scores = {}
            for y in unique_labels:
                score = 0
                for j in range(n_features):
                    feat_val = x[j]
                    if (j, feat_val, y) in feature_funcs:
                        idx = feature_funcs[j, feat_val, y]
                        score += weights[idx]
                label_scores[y] = score
                Z_x += math.exp(score)
            for y in unique_labels:
                p_y_given_x = math.exp(label_scores[y]) / Z_x
                for j in range(n_features):
                    feat_val = x[j]
                    if (j, feat_val, y) in feature_funcs:
                        idx = feature_funcs[j, feat_val, y]
                        model_exp[idx] += p_y_given_x
        model_exp /= len(X_train)
        for j in range(n_feature_funcs):
            if emp_exp[j] > 0:
                update = math.log(emp_exp[j] / model_exp[j]) if model_exp[j] > 0 else 0
                weights[j] += update
                max_update = max(max_update, abs(update))
        if max_update < epsilon:
            break
    predictions = []
    for x in X_test:
        label_probs = {}
        Z_x = 0
        for y in unique_labels:
            score = 0
            for j in range(n_features):
                feat_val = x[j]
                if (j, feat_val, y) in feature_funcs:
                    idx = feature_funcs[j, feat_val, y]
                    score += weights[idx]
            label_probs[y] = math.exp(score)
            Z_x += label_probs[y]
        for y in label_probs:
            label_probs[y] /= Z_x
        best_label = max(label_probs.items(), key=lambda x: x[1])[0]
        predictions.append(best_label)
    return predictions
