def calculate_brightness(img):
    if len(img) == 0:
        return -1
    row_length = len(img[0])
    if row_length == 0:
        return -1
    for row in img:
        if len(row) != row_length:
            return -1
    for row in img:
        for pixel in row:
            if not 0 <= pixel <= 255:
                return -1
    total_brightness = sum((sum(row) for row in img))
    total_pixels = len(img) * row_length
    average_brightness = total_brightness / total_pixels
    return round(average_brightness, 2)
