import numpy as np
def load_encoder_hparams_and_params(model_size: str='124M', models_dir: str='models'):

    class DummyBPE:

        def __init__(self):
            self.encoder_dict = {'hello': 1, 'world': 2, '<UNK>': 0}

        def encode(self, text: str):
            tokens = text.strip().split()
            return [self.encoder_dict.get(token, self.encoder_dict['<UNK>']) for token in tokens]

        def decode(self, token_ids: list):
            reversed_dict = {v: k for (k, v) in self.encoder_dict.items()}
            return ' '.join([reversed_dict.get(tok_id, '<UNK>') for tok_id in token_ids])
    hparams = {'n_ctx': 1024, 'n_head': 12}
    params = {'wte': np.random.rand(3, 10), 'wpe': np.random.rand(1024, 10), 'blocks': [], 'ln_f': {'g': np.ones(10), 'b': np.zeros(10)}}
    encoder = DummyBPE()
    return (encoder, hparams, params)
def gen_text(prompt: str, n_tokens_to_generate: int=40):
    np.random.seed(42)
    (encoder, hparams, params) = load_encoder_hparams_and_params()
    token_ids = encoder.encode(prompt)
    for _ in range(n_tokens_to_generate):
        context = token_ids[-hparams['n_ctx']:]
        next_token_logits = gpt2_forward(context, hparams, params)
        next_token_probs = softmax(next_token_logits)
        next_token = np.random.choice(len(next_token_probs), p=next_token_probs)
        token_ids.append(next_token)
    generated_text = encoder.decode(token_ids)
    return generated_text
def gpt2_forward(token_ids, hparams, params):
    """Forward pass through the GPT-2 model to get logits for next token prediction"""
    token_embeddings = np.array([params['wte'][token_id] for token_id in token_ids])
    positions = np.arange(len(token_ids))
    position_embeddings = params['wpe'][positions]
    hidden_states = token_embeddings + position_embeddings
    hidden_states = apply_transformer_blocks(hidden_states, hparams)
    hidden_states = layer_norm(hidden_states, params['ln_f']['g'], params['ln_f']['b'])
    logits = hidden_states[-1] @ params['wte'].T
    return logits
def apply_transformer_blocks(hidden_states, hparams):
    """Apply a sequence of transformer blocks to the hidden states"""
    attention_output = multi_head_attention(hidden_states, hparams['n_head'])
    hidden_states = hidden_states + attention_output
    ffn_output = feed_forward_network(hidden_states)
    hidden_states = hidden_states + ffn_output
    return hidden_states
def multi_head_attention(hidden_states, n_heads):
    """Apply multi-head self-attention mechanism"""
    seq_length = hidden_states.shape[0]
    mask = np.tril(np.ones((seq_length, seq_length)))
    attention_output = np.zeros_like(hidden_states)
    for i in range(seq_length):
        weights = mask[i][:seq_length] / (np.sum(mask[i][:seq_length]) + 1e-10)
        attention_output[i] = weights.reshape(-1, 1) * hidden_states
        attention_output[i] = np.sum(attention_output[i], axis=0)
    return attention_output * 0.1
def feed_forward_network(hidden_states):
    """Apply a feed-forward network to each position"""
    return hidden_states * 0.1
def layer_norm(x, g, b, eps=1e-05):
    """Apply layer normalization"""
    mean = np.mean(x, axis=-1, keepdims=True)
    var = np.var(x, axis=-1, keepdims=True)
    return g * (x - mean) / np.sqrt(var + eps) + b
def softmax(x):
    """Compute softmax values for the vector x"""
    exp_x = np.exp(x - np.max(x))
    return exp_x / np.sum(exp_x)
