def matrixmul(a: list[list[int | float]], b: list[list[int | float]]) -> list[list[int | float]]:
    if not a or not b:
        return -1
    rows_a = len(a)
    if not all((len(row) == len(a[0]) for row in a)) or not all((len(row) == len(b[0]) for row in b)):
        return -1
    cols_a = len(a[0])
    rows_b = len(b)
    if cols_a != rows_b:
        return -1
    cols_b = len(b[0])
    result = [[0 for _ in range(cols_b)] for _ in range(rows_a)]
    for i in range(rows_a):
        for j in range(cols_b):
            for k in range(cols_a):
                result[i][j] += a[i][k] * b[k][j]
    return result

def determinant_4x4(matrix: list[list[int | float]]) -> float:
    """
    Calculate the determinant of a 4x4 matrix using Laplace's expansion.

    Args:
        matrix: A 4x4 matrix represented as a list of lists

    Returns:
        The determinant value as a float
    """
    if len(matrix) != 4 or any((len(row) != 4 for row in matrix)):
        raise ValueError('Matrix must be 4x4')

    def get_minor(matrix, i, j):
        return [[matrix[row][col] for col in range(len(matrix)) if col != j] for row in range(len(matrix)) if row != i]

    def calculate_determinant(matrix):
        n = len(matrix)
        if n == 2:
            return matrix[0][0] * matrix[1][1] - matrix[0][1] * matrix[1][0]
        det = 0
        for j in range(n):
            minor = get_minor(matrix, 0, j)
            det += matrix[0][j] * calculate_determinant(minor) * (-1) ** j
        return det
    return calculate_determinant(matrix)

class Value:

    def __init__(self, data, _children=(), _op=''):
        self.data = data
        self.grad = 0
        self._backward = lambda : None
        self._prev = set(_children)
        self._op = _op

    def __add__(self, other):
        other = other if isinstance(other, Value) else Value(other)
        out = Value(self.data + other.data, (self, other), '+')

        def _backward():
            self.grad += out.grad
            other.grad += out.grad
        out._backward = _backward
        return out

    def __mul__(self, other):
        other = other if isinstance(other, Value) else Value(other)
        out = Value(self.data * other.data, (self, other), '*')

        def _backward():
            self.grad += other.data * out.grad
            other.grad += self.data * out.grad
        out._backward = _backward
        return out

    def relu(self):
        out = Value(0 if self.data < 0 else self.data, (self,), 'ReLU')

        def _backward():
            self.grad += (self.data > 0) * out.grad
        out._backward = _backward
        return out

    def backward(self):
        topo = []
        visited = set()

        def build_topo(v):
            if v not in visited:
                visited.add(v)
                for child in v._prev:
                    build_topo(child)
                topo.append(v)
        build_topo(self)
        self.grad = 1.0
        for node in reversed(topo):
            node._backward()

    def __radd__(self, other):
        return self + other

    def __rmul__(self, other):
        return self * other

    def __neg__(self):
        return self * -1

    def __sub__(self, other):
        return self + -other

    def __rsub__(self, other):
        return other + -self

    def __repr__(self):
        return f'Value(data={self.data}, grad={self.grad})'

