def adaboost_fit(X, y, n_clf):
    (n_samples, n_features) = X.shape
    y = np.where(y <= 0, -1, 1)
    weights = np.ones(n_samples) / n_samples
    classifiers = []
    for _ in range(n_clf):
        min_error = float('inf')
        best_clf = {}
        for feature_idx in range(n_features):
            feature_values = X[:, feature_idx]
            thresholds = np.unique(feature_values)
            for threshold in thresholds:
                for polarity in [1, -1]:
                    predictions = np.ones(n_samples)
                    if polarity == 1:
                        predictions[feature_values < threshold] = -1
                    else:
                        predictions[feature_values >= threshold] = -1
                    misclassified = predictions != y
                    error = np.sum(weights[misclassified])
                    if error < min_error:
                        min_error = error
                        best_clf = {'feature_idx': feature_idx, 'threshold': threshold, 'polarity': polarity}
        epsilon = 1e-10
        alpha = 0.5 * math.log((1 - min_error) / (min_error + epsilon))
        predictions = np.ones(n_samples)
        if best_clf['polarity'] == 1:
            predictions[X[:, best_clf['feature_idx']] < best_clf['threshold']] = -1
        else:
            predictions[X[:, best_clf['feature_idx']] >= best_clf['threshold']] = -1
        weights = weights * np.exp(-alpha * y * predictions)
        weights = weights / np.sum(weights)
        best_clf['alpha'] = round(alpha, 4)
        best_clf['threshold'] = round(best_clf['threshold'], 4)
        classifiers.append(best_clf)
    return classifiers

