class Dense(Layer):

    def __init__(self, n_units, input_shape=None):
        self.layer_input = None
        self.input_shape = input_shape
        self.n_units = n_units
        self.trainable = True
        self.W = None
        self.w0 = None
        self.W_optimizer = None
        self.w0_optimizer = None

    def initialize(self, optimizer):
        limit = 1 / math.sqrt(self.input_shape[0])
        self.W = np.random.uniform(-limit, limit, size=(self.input_shape[0], self.n_units))
        self.w0 = np.zeros((1, self.n_units))
        self.W_optimizer = copy.copy(optimizer)
        self.w0_optimizer = copy.copy(optimizer)

    def parameters(self):
        return np.prod(self.W.shape) + np.prod(self.w0.shape)

    def forward_pass(self, X, training=True):
        self.layer_input = X
        output = X.dot(self.W) + self.w0
        return np.round(output, 4)

    def backward_pass(self, accum_grad):
        grad_X = accum_grad.dot(self.W.T)
        if self.trainable:
            grad_w = self.layer_input.T.dot(accum_grad)
            grad_w0 = np.sum(accum_grad, axis=0, keepdims=True)
            self.W = self.W_optimizer.update(self.W, grad_w)
            self.w0 = self.w0_optimizer.update(self.w0, grad_w0)
        return np.round(grad_X, 4)

    def output_shape(self):
        return (self.n_units,)

import numpy as np
def simple_conv2d(input_matrix: np.ndarray, kernel: np.ndarray, padding: int, stride: int):
    (input_height, input_width) = input_matrix.shape
    (kernel_height, kernel_width) = kernel.shape
    if padding > 0:
        padded_matrix = np.zeros((input_height + 2 * padding, input_width + 2 * padding))
        padded_matrix[padding:padding + input_height, padding:padding + input_width] = input_matrix
    else:
        padded_matrix = input_matrix.copy()
    output_height = (padded_matrix.shape[0] - kernel_height) // stride + 1
    output_width = (padded_matrix.shape[1] - kernel_width) // stride + 1
    output = np.zeros((output_height, output_width))
    for i in range(output_height):
        for j in range(output_width):
            i_start = i * stride
            j_start = j * stride
            region = padded_matrix[i_start:i_start + kernel_height, j_start:j_start + kernel_width]
            output[i, j] = np.sum(region * kernel)
    output = np.round(output, 4)
    return output.tolist()

