def rref(matrix):
    """
    Converts a matrix to its Reduced Row Echelon Form (RREF).

    Args:
        matrix: A 2D list or numpy array representing a matrix

    Returns:
        The RREF of the input matrix as a list of lists
    """
    A = np.array(matrix, dtype=float)
    (rows, cols) = A.shape
    r = 0
    for c in range(cols):
        pivot_row = None
        for i in range(r, rows):
            if abs(A[i, c]) > 1e-10:
                pivot_row = i
                break
        if pivot_row is not None:
            if pivot_row != r:
                A[[r, pivot_row]] = A[[pivot_row, r]]
            pivot_value = A[r, c]
            A[r] = A[r] / pivot_value
            for i in range(rows):
                if i != r:
                    factor = A[i, c]
                    A[i] = A[i] - factor * A[r]
            r += 1
            if r == rows:
                break
    A[abs(A) < 1e-10] = 0
    return A.tolist()

