def gaussian_elimination(A, b):
    """
    Solve the linear system Ax = b using Gaussian elimination with partial pivoting.

    Parameters:
    A (array-like): Coefficient matrix
    b (array-like): Right-hand side vector

    Returns:
    list: Solution vector rounded to 4 decimal places
    """
    A = np.array(A, dtype=float)
    b = np.array(b, dtype=float)
    if b.ndim == 1:
        b = b.reshape(-1, 1)
    n = len(A)
    aug = np.column_stack((A, b))
    for i in range(n):
        pivot_row = i
        for j in range(i + 1, n):
            if abs(aug[j, i]) > abs(aug[pivot_row, i]):
                pivot_row = j
        if pivot_row != i:
            aug[[i, pivot_row]] = aug[[pivot_row, i]]
        if abs(aug[i, i]) < 1e-10:
            raise ValueError('Matrix is singular or nearly singular')
        for j in range(i + 1, n):
            factor = aug[j, i] / aug[i, i]
            aug[j, i:] = aug[j, i:] - factor * aug[i, i:]
    x = np.zeros(n)
    for i in range(n - 1, -1, -1):
        x[i] = aug[i, -1]
        for j in range(i + 1, n):
            x[i] = x[i] - aug[i, j] * x[j]
        x[i] = x[i] / aug[i, i]
    x = np.round(x, 4)
    return x.tolist()

def compressed_col_sparse_matrix(dense_matrix):
    """
    Convert a dense matrix into its Compressed Column Sparse (CSC) representation.

    :param dense_matrix: List of lists representing the dense matrix
    :return: Tuple of (values, row indices, column pointer)
    """
    if not dense_matrix or not dense_matrix[0]:
        return ([], [], [0])
    num_rows = len(dense_matrix)
    num_cols = len(dense_matrix[0])
    values = []
    row_indices = []
    column_pointer = [0]
    for col in range(num_cols):
        for row in range(num_rows):
            if dense_matrix[row][col] != 0:
                values.append(dense_matrix[row][col])
                row_indices.append(row)
        column_pointer.append(len(values))
    return (values, row_indices, column_pointer)

