def matrix_image(A):
    """
    Compute the column space (image) of matrix A.

    Args:
        A: Input matrix as a 2D array or list

    Returns:
        A list of vectors that form a basis for the column space
    """
    A = np.array(A, dtype=float)
    (m, n) = A.shape
    aug = np.hstack((A.copy(), np.eye(m)))
    r = 0
    pivot_cols = []
    for j in range(n):
        pivot_found = False
        for i in range(r, m):
            if abs(aug[i, j]) > 1e-10:
                if i != r:
                    aug[[i, r]] = aug[[r, i]]
                pivot_found = True
                break
        if pivot_found:
            aug[r] = aug[r] / aug[r, j]
            for i in range(m):
                if i != r:
                    aug[i] = aug[i] - aug[i, j] * aug[r]
            pivot_cols.append(j)
            r += 1
            if r == m:
                break
    basis = A[:, pivot_cols]
    return np.round(basis, 8).tolist()

def calculate_brightness(img):
    if len(img) == 0:
        return -1
    row_length = len(img[0])
    if row_length == 0:
        return -1
    for row in img:
        if len(row) != row_length:
            return -1
    for row in img:
        for pixel in row:
            if not 0 <= pixel <= 255:
                return -1
    total_brightness = sum((sum(row) for row in img))
    total_pixels = len(img) * row_length
    average_brightness = total_brightness / total_pixels
    return round(average_brightness, 2)

