from typing import List, Dict, Tuple, Hashable
def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                       itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemset (stored as a tuple of
        sorted items) to its absolute support count.  If no itemset reaches
        the threshold the function must return an empty dictionary.
    """
    if not transactions or minsup <= 0 or minsup > 1:
        return {}
    transaction_sets = [set(transaction) for transaction in transactions]
    n_transactions = len(transaction_sets)
    min_support_count = math.ceil(minsup * n_transactions)
    item_counts = Counter()
    for transaction in transaction_sets:
        for item in transaction:
            item_counts[item] += 1
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_support_count}
    frequent_items = sorted(frequent_items)
    result = {(item,): count for (item, count) in item_counts.items() if count >= min_support_count}
    k = 2
    prev_frequent_itemsets = [frozenset([item]) for item in frequent_items]
    while prev_frequent_itemsets:
        candidates = []
        for i in range(len(prev_frequent_itemsets)):
            for j in range(i + 1, len(prev_frequent_itemsets)):
                itemset1 = prev_frequent_itemsets[i]
                itemset2 = prev_frequent_itemsets[j]
                if len(itemset1.union(itemset2)) == k:
                    candidates.append(itemset1.union(itemset2))
        candidate_counts = Counter()
        for transaction in transaction_sets:
            for candidate in candidates:
                if candidate.issubset(transaction):
                    candidate_counts[candidate] += 1
        frequent_k_itemsets = [itemset for (itemset, count) in candidate_counts.items() if count >= min_support_count]
        for itemset in frequent_k_itemsets:
            sorted_itemset = tuple(sorted(itemset))
            result[sorted_itemset] = candidate_counts[itemset]
        prev_frequent_itemsets = frequent_k_itemsets
        k += 1
        if not frequent_k_itemsets:
            break
    return result

import numpy as np
def is_stochastic(X: list[list[float]] | 'np.ndarray') -> bool:
    """Return True if *X* is a row-stochastic matrix, otherwise False.

    A matrix is row-stochastic when every element is a probability (0 ≤ p ≤ 1)
    and each row sums to 1 (within a small numerical tolerance).

    Args:
        X: Matrix given as a list of lists or a NumPy array.

    Returns:
        bool: True if *X* is row-stochastic, False otherwise.
    """
    if isinstance(X, list):
        try:
            X = np.array(X, dtype=float)
        except:
            return False
    if not isinstance(X, np.ndarray) or X.ndim != 2:
        return False
    if not np.all((X >= 0) & (X <= 1)):
        return False
    row_sums = np.sum(X, axis=1)
    return np.allclose(row_sums, 1.0, rtol=0, atol=1e-08)

