def softplus(z):
    """Compute the numerically stable softplus activation.

    The softplus function is defined as ln(1 + e**z).  This implementation
    uses ``numpy.logaddexp`` to avoid overflow/underflow issues.

    Args:
        z (int | float | list | np.ndarray): Scalar or array-like input.

    Returns:
        float | list: Softplus value(s) rounded to 4 decimal places. For
        array-like inputs the returned structure mirrors the input's shape but
        is converted to a pure Python ``list``. For scalar inputs a single
        ``float`` is returned.
    """
    if isinstance(z, list):
        z_array = np.array(z)
    else:
        z_array = z
    result = np.logaddexp(0.0, z_array)
    result = np.round(result, 4)
    if np.isscalar(z):
        return float(result)
    else:
        return result.tolist()

import numpy as np
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """

    class Node:

        def __init__(self, feature=None, threshold=None, left=None, right=None, value=None):
            self.feature = feature
            self.threshold = threshold
            self.left = left
            self.right = right
            self.value = value

    def gini_impurity(y):
        m = len(y)
        if m == 0:
            return 0
        (_, counts) = np.unique(y, return_counts=True)
        probabilities = counts / m
        return 1 - np.sum(probabilities ** 2)

    def majority_class(y):
        if len(y) == 0:
            return 0
        (unique_classes, counts) = np.unique(y, return_counts=True)
        return unique_classes[np.argmax(counts)]

    def find_best_split(X, y):
        (m, n_features) = X.shape
        if m <= 1 or len(np.unique(y)) == 1:
            return (None, None, None, None, None)
        best_gain = -1
        best_feature = None
        best_threshold = None
        best_left_indices = None
        best_right_indices = None
        parent_gini = gini_impurity(y)
        for feature_idx in range(n_features):
            feature_values = X[:, feature_idx]
            sorted_indices = np.argsort(feature_values)
            sorted_values = feature_values[sorted_indices]
            sorted_labels = y[sorted_indices]
            midpoints = []
            for i in range(1, len(sorted_values)):
                if sorted_values[i] != sorted_values[i - 1]:
                    midpoints.append((sorted_values[i] + sorted_values[i - 1]) / 2)
            for threshold in midpoints:
                left_indices = np.where(X[:, feature_idx] <= threshold)[0]
                right_indices = np.where(X[:, feature_idx] > threshold)[0]
                if len(left_indices) == 0 or len(right_indices) == 0:
                    continue
                left_gini = gini_impurity(y[left_indices])
                right_gini = gini_impurity(y[right_indices])
                left_weight = len(left_indices) / m
                right_weight = len(right_indices) / m
                weighted_gini = left_weight * left_gini + right_weight * right_gini
                gain = parent_gini - weighted_gini
                if gain > best_gain:
                    best_gain = gain
                    best_feature = feature_idx
                    best_threshold = threshold
                    best_left_indices = left_indices
                    best_right_indices = right_indices
        return (best_gain, best_feature, best_threshold, best_left_indices, best_right_indices)

    def build_tree(X, y, depth=0):
        if max_depth is not None and depth >= max_depth or len(np.unique(y)) == 1:
            return Node(value=majority_class(y))
        (gain, feature, threshold, left_indices, right_indices) = find_best_split(X, y)
        if gain is None or gain <= 0:
            return Node(value=majority_class(y))
        left_node = build_tree(X[left_indices], y[left_indices], depth + 1)
        right_node = build_tree(X[right_indices], y[right_indices], depth + 1)
        return Node(feature=feature, threshold=threshold, left=left_node, right=right_node)

    def predict_sample(node, sample):
        if node.value is not None:
            return node.value
        if sample[node.feature] <= node.threshold:
            return predict_sample(node.left, sample)
        else:
            return predict_sample(node.right, sample)
    root = build_tree(X_train, y_train)
    predictions = []
    for sample in X_test:
        predictions.append(predict_sample(root, sample))
    return predictions

