from typing import Callable, Union, List, Tuple, Iterable, Any
def metric(name: str) -> Callable[[Any, Any], Union[float, int]]:
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def validate_vectors(x: Any, y: Any) -> Union[Tuple[np.ndarray, np.ndarray], bool]:
        """Validate input vectors according to requirements."""
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return False
        try:
            x_arr = np.array(x, dtype=float)
            y_arr = np.array(y, dtype=float)
            if x_arr.ndim != 1 or y_arr.ndim != 1:
                return False
            if x_arr.size == 0 or y_arr.size == 0:
                return False
            if x_arr.size != y_arr.size:
                return False
            return (x_arr, y_arr)
        except (ValueError, TypeError):
            return False

    def euclidean_distance(x: Any, y: Any) -> Union[float, int]:
        """Compute Euclidean distance between two vectors."""
        validated = validate_vectors(x, y)
        if validated is False:
            return -1
        (x_arr, y_arr) = validated
        return round(np.sqrt(np.sum((x_arr - y_arr) ** 2)), 4)

    def manhattan_distance(x: Any, y: Any) -> Union[float, int]:
        """Compute Manhattan distance between two vectors."""
        validated = validate_vectors(x, y)
        if validated is False:
            return -1
        (x_arr, y_arr) = validated
        return round(np.sum(np.abs(x_arr - y_arr)), 4)

    def chebyshev_distance(x: Any, y: Any) -> Union[float, int]:
        """Compute Chebyshev distance between two vectors."""
        validated = validate_vectors(x, y)
        if validated is False:
            return -1
        (x_arr, y_arr) = validated
        return round(np.max(np.abs(x_arr - y_arr)), 4)

    def cosine_distance(x: Any, y: Any) -> Union[float, int]:
        """Compute Cosine distance between two vectors."""
        validated = validate_vectors(x, y)
        if validated is False:
            return -1
        (x_arr, y_arr) = validated
        x_norm = np.linalg.norm(x_arr)
        y_norm = np.linalg.norm(y_arr)
        if x_norm == 0 or y_norm == 0:
            return -1
        cosine_sim = np.dot(x_arr, y_arr) / (x_norm * y_norm)
        cosine_sim = max(min(cosine_sim, 1.0), -1.0)
        return round(1 - cosine_sim, 4)
    metrics = {'euclidean': euclidean_distance, 'manhattan': manhattan_distance, 'chebyshev': chebyshev_distance, 'cosine': cosine_distance}
    return metrics.get(name, lambda x, y: -1)

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def calculate_score(hand: list[int]) -> int:
        aces = hand.count(1)
        score = sum(hand) - aces
        score += aces
        if aces > 0 and score + 10 <= 21:
            score += 10
        return score

    def is_natural_blackjack(hand: list[int]) -> bool:
        return len(hand) == 2 and 1 in hand and (10 in hand)
    player_score = calculate_score(player)
    dealer_score = calculate_score(dealer)
    if player_score > 21:
        return -1.0
    if dealer_score > 21:
        if is_natural_blackjack(player):
            return 1.5
        return 1.0
    if player_score > dealer_score:
        if is_natural_blackjack(player):
            return 1.5
        return 1.0
    elif player_score < dealer_score:
        return -1.0
    else:
        return 0.0

