from collections import Counter
def best_split(feature, target):
    """Determine the numerical threshold that produces the highest information gain.

    Parameters
    ----------
    feature : list[int | float] | 1-D numpy.ndarray
        Numerical values of a single attribute.
    target  : list[int] | 1-D numpy.ndarray
        Corresponding class labels.

    Returns
    -------
    tuple
        (threshold, information_gain) where
        * threshold – float rounded to 4 decimals or None when no useful split exists;
        * information_gain – float rounded to 4 decimals.
    """
    n = len(feature)
    if n == 0:
        return (None, 0.0)
    parent_counter = Counter(target)
    parent_entropy = entropy(parent_counter, n)
    if len(parent_counter) <= 1:
        return (None, 0.0)
    sorted_indices = sorted(range(n), key=lambda i: feature[i])
    sorted_features = [feature[i] for i in sorted_indices]
    sorted_targets = [target[i] for i in sorted_indices]
    if sorted_features[0] == sorted_features[-1]:
        return (None, 0.0)
    thresholds = []
    for i in range(1, n):
        if sorted_features[i] != sorted_features[i - 1]:
            thresholds.append((sorted_features[i] + sorted_features[i - 1]) / 2)
    max_gain = 0.0
    best_threshold = None
    left_counter = Counter()
    right_counter = Counter(sorted_targets)
    for threshold in thresholds:
        left_counter.clear()
        right_counter = Counter(sorted_targets)
        split_idx = 0
        while split_idx < n and sorted_features[split_idx] < threshold:
            left_counter[sorted_targets[split_idx]] += 1
            right_counter[sorted_targets[split_idx]] -= 1
            if right_counter[sorted_targets[split_idx]] == 0:
                del right_counter[sorted_targets[split_idx]]
            split_idx += 1
        left_count = split_idx
        right_count = n - left_count
        if left_count > 0 and right_count > 0:
            left_entropy = entropy(left_counter, left_count)
            right_entropy = entropy(right_counter, right_count)
            gain = parent_entropy - left_count / n * left_entropy - right_count / n * right_entropy
            if gain > max_gain or (gain == max_gain and threshold < best_threshold):
                max_gain = gain
                best_threshold = threshold
    if max_gain <= 0:
        return (None, 0.0)
    return (round(best_threshold, 4), round(max_gain, 4))
def entropy(counter, total):
    """Calculate Shannon entropy for a distribution of classes."""
    if total == 0:
        return 0
    entropy_val = 0
    for count in counter.values():
        p = count / total
        entropy_val -= p * math.log2(p)
    return entropy_val

import numpy as np
from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
@dataclass
class Node:
    """An internal decision-tree node.

    Attributes:
        feature (int):   Index of the feature to test.
        threshold (float): Threshold that splits the data.
        left (Node | Leaf):  Sub-tree for samples with feature value < threshold.
        right (Node | Leaf): Sub-tree for samples with feature value ≥ threshold.
    """
    feature: int
    threshold: float
    left: object
    right: object
def compare_trees(tree_a, tree_b):
    """Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if type(tree_a) != type(tree_b):
        return False
    if isinstance(tree_a, Leaf) and isinstance(tree_b, Leaf):
        return np.allclose(tree_a.value, tree_b.value, rtol=0, atol=1e-08)
    if isinstance(tree_a, Node) and isinstance(tree_b, Node):
        if tree_a.feature != tree_b.feature:
            return False
        if not np.isclose(tree_a.threshold, tree_b.threshold, rtol=0, atol=1e-08):
            return False
        return compare_trees(tree_a.left, tree_b.left) and compare_trees(tree_a.right, tree_b.right)
    return False

from typing import Any, Dict, List, Tuple
def to_networkx(G: Dict[str, Any]) -> Dict[Any, List[Tuple[Any, float]]]:
    """Convert a custom graph dictionary into a plain adjacency dictionary.

    Parameters
    ----------
    G : dict
        A graph represented as a dictionary with the keys:
          * 'is_directed' (bool)
          * '_V2I'        (dict mapping vertex label -> index)
          * '_G'          (list of adjacency lists, each edge is a
                           tuple (source_label, target_label, weight))

    Returns
    -------
    dict
        A dictionary where each key is a vertex label and the value is a list
        of `(neighbour, weight)` tuples.  For undirected graphs every edge
        appears once in each direction and the neighbour lists are sorted
        alphabetically.
    """
    if not G.get('_V2I', {}):
        return {}
    adj = {vertex: [] for vertex in G['_V2I']}
    for edges in G['_G']:
        for (source, target, weight) in edges:
            adj[source].append((target, weight))
            if not G['is_directed'] and source != target:
                adj[target].append((source, weight))
    for vertex in adj:
        if not G['is_directed']:
            seen = {}
            unique_edges = []
            for (neighbor, weight) in adj[vertex]:
                if neighbor not in seen:
                    seen[neighbor] = weight
                    unique_edges.append((neighbor, weight))
            adj[vertex] = unique_edges
        adj[vertex].sort(key=lambda x: x[0])
    return adj

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def check_space_continuity(space, is_composite):
        if not is_composite:
            return isinstance(space, Box)
        if isinstance(space, Tuple):
            return all((isinstance(subspace, Box) for subspace in space.spaces))
        elif isinstance(space, Dict):
            return all((isinstance(subspace, Box) for subspace in space.spaces.values()))
        else:
            return False
    cont_action = check_space_continuity(env.action_space, tuple_action)
    cont_obs = check_space_continuity(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in [0, 1, 2]:
        return -1
    is_scalar = np.isscalar(x)
    x_array = np.array(x, dtype=float)
    sigmoid_x = 1.0 / (1.0 + np.exp(-x_array))
    if order == 0:
        result = sigmoid_x
    elif order == 1:
        result = sigmoid_x * (1 - sigmoid_x)
    else:
        result = sigmoid_x * (1 - sigmoid_x) * (1 - 2 * sigmoid_x)
    result = np.round(result, 4)
    if is_scalar:
        return float(result)
    else:
        return result.tolist()

