from typing import Union, List, Optional
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    X_np = np.array(X, dtype=float)
    if Y is None:
        Y_np = X_np
    else:
        Y_np = np.array(Y, dtype=float)
    (N, C_X) = X_np.shape
    (M, C_Y) = Y_np.shape
    if C_X != C_Y:
        return -1
    if sigma is None:
        sigma_values = np.sqrt(C_X / 2) * np.ones(C_X)
    elif isinstance(sigma, (int, float)):
        if sigma <= 0:
            return -1
        sigma_values = sigma * np.ones(C_X)
    else:
        sigma_values = np.array(sigma, dtype=float)
        if len(sigma_values) != C_X:
            return -1
        if np.any(sigma_values <= 0):
            return -1
    kernel_matrix = np.zeros((N, M))
    for i in range(N):
        for j in range(M):
            squared_diff = ((X_np[i] - Y_np[j]) / sigma_values) ** 2
            kernel_matrix[i, j] = np.exp(-0.5 * np.sum(squared_diff))
    rounded_matrix = np.round(kernel_matrix, 4).tolist()
    return rounded_matrix

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """
    if isinstance(z, (int, float)):
        return float(np.round(z * (1 - z), 4))
    if isinstance(z, list) and len(z) == 0:
        return []
    if isinstance(z, np.ndarray):
        return np.round(z * (1 - z), 4)
    if isinstance(z, list):
        if all((isinstance(item, (int, float)) for item in z)):
            return [float(np.round(item * (1 - item), 4)) for item in z]
        else:
            return [sigmoid_grad(item) for item in z]
    raise TypeError('Input must be a float, list, or NumPy array')

import numpy as np
def spectral_clustering(data: np.ndarray, n_cluster: int, gamma: float=2.0, method: str='unnormalized') -> list[int]:
    """Perform spectral clustering on the given dataset.

    Args:
        data: A NumPy array of shape (N, d) containing N samples with d features.
        n_cluster: The number of clusters to form.
        gamma: The gamma parameter of the Gaussian (RBF) kernel used to build the similarity graph.
        method: Either 'unnormalized' or 'normalized' – specifies which Laplacian variant to use.

    Returns:
        A list of length N where the i-th element is an integer label identifying the
        cluster assignment of the i-th sample (labels are 0-based).
    """
    N = data.shape[0]
    if n_cluster == 1:
        return [0] * N
    squared_dists = np.zeros((N, N))
    for i in range(N):
        for j in range(N):
            diff = data[i] - data[j]
            squared_dists[i, j] = np.sum(diff * diff)
    W = np.exp(-gamma * squared_dists)
    D = np.diag(np.sum(W, axis=1))
    L = D - W
    if method == 'normalized':
        D_sqrt_inv = np.diag(1.0 / np.sqrt(np.diag(D) + 1e-10))
        L = D_sqrt_inv @ L @ D_sqrt_inv
    (eigenvalues, eigenvectors) = np.linalg.eigh(L)
    selected_eigenvectors = eigenvectors[:, :n_cluster]
    if method == 'normalized':
        row_norms = np.sqrt(np.sum(selected_eigenvectors * selected_eigenvectors, axis=1))
        selected_eigenvectors = selected_eigenvectors / row_norms[:, np.newaxis]
    centroids = selected_eigenvectors[:n_cluster].copy()
    labels = np.zeros(N, dtype=int)
    old_labels = np.ones(N, dtype=int) * -1
    while not np.array_equal(labels, old_labels):
        old_labels = labels.copy()
        for i in range(N):
            min_dist = float('inf')
            for j in range(n_cluster):
                dist = np.sum((selected_eigenvectors[i] - centroids[j]) ** 2)
                if dist < min_dist:
                    min_dist = dist
                    labels[i] = j
        for j in range(n_cluster):
            points_in_cluster = selected_eigenvectors[labels == j]
            if len(points_in_cluster) > 0:
                centroids[j] = np.mean(points_in_cluster, axis=0)
    new_labels = np.zeros(N, dtype=int)
    cluster_min_indices = {}
    for j in range(n_cluster):
        cluster_indices = np.where(labels == j)[0]
        if len(cluster_indices) > 0:
            cluster_min_indices[j] = np.min(cluster_indices)
    sorted_clusters = sorted(cluster_min_indices.items(), key=lambda x: x[1])
    label_mapping = {old_label: new_label for (new_label, (old_label, _)) in enumerate(sorted_clusters)}
    for i in range(N):
        new_labels[i] = label_mapping[labels[i]]
    return new_labels.tolist()

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if hasattr(root, 'y'):
        return root.y
    feature_value = features[root.label]
    if task_type == 'classification':
        for child in root.child:
            if hasattr(child, 'x') and child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    elif task_type == 'regression':
        if feature_value <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)

import numpy as np
import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    n_samples = data.shape[0]
    if epsilon == 0:
        return []
    actual_sample_size = min(sample_size, n_samples)
    height_limit = math.ceil(math.log2(actual_sample_size))
    phi = 2 * math.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples if n_samples > 1 else 1

    def c(n):
        if n <= 1:
            return 0
        return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    def build_isolation_tree(X, current_height=0):
        n = X.shape[0]
        if current_height >= height_limit or n <= 1:
            return {'type': 'leaf', 'size': n}
        feature_idx = np.random.randint(0, X.shape[1])
        min_val = np.min(X[:, feature_idx])
        max_val = np.max(X[:, feature_idx])
        if min_val == max_val:
            return {'type': 'leaf', 'size': n}
        threshold = min_val + np.random.random() * (max_val - min_val)
        left_indices = X[:, feature_idx] < threshold
        right_indices = ~left_indices
        if np.sum(left_indices) == 0 or np.sum(right_indices) == 0:
            return {'type': 'leaf', 'size': n}
        return {'type': 'node', 'feature_idx': feature_idx, 'threshold': threshold, 'left': build_isolation_tree(X[left_indices], current_height + 1), 'right': build_isolation_tree(X[right_indices], current_height + 1)}

    def compute_path_length(x, tree, current_height=0):
        if tree['type'] == 'leaf':
            return current_height + c(tree['size'])
        if x[tree['feature_idx']] < tree['threshold']:
            return compute_path_length(x, tree['left'], current_height + 1)
        else:
            return compute_path_length(x, tree['right'], current_height + 1)
    trees = []
    for _ in range(n_trees):
        sample_indices = np.random.choice(n_samples, actual_sample_size, replace=False)
        sample_data = data[sample_indices]
        tree = build_isolation_tree(sample_data)
        trees.append(tree)
    avg_path_lengths = np.zeros(n_samples)
    for i in range(n_samples):
        path_lengths = [compute_path_length(data[i], tree) for tree in trees]
        avg_path_lengths[i] = np.mean(path_lengths)
    scores = 2 ** (-avg_path_lengths / phi)
    n_outliers = int(epsilon * n_samples)
    if n_outliers == 0:
        return []
    outlier_indices = np.argsort(scores)[-n_outliers:]
    outlier_indices = sorted(outlier_indices)
    return outlier_indices.tolist()

from collections import Counter
from typing import Iterable, Tuple, List, Set, Any
def statistic_key_word(data: List[Tuple[Any, Iterable[str]]], cut_off: int | None=None) -> Tuple[List[Tuple[str, float]], Set[str]]:
    """Calculate how often each word appears across documents.

    The function returns a list with the document-frequency ratio of every word
    (sorted from highest to lowest) and a set containing only the *cut_off*
    most frequent words. When *cut_off* is ``None`` all words are placed in the
    set. Every ratio must be rounded to four decimal places.

    Args:
        data: Iterable of documents. Each document is represented by a tuple
              ``(label, words)`` where *label* is ignored and *words* is an
              iterable of strings.
        cut_off: Number of top words to include in the returned set, or
                  ``None`` to include every word.

    Returns:
        Tuple with (1) a list of (word, ratio) pairs and (2) a set of the most
        frequent words as described above.
    """
    if not data:
        return ([], set())
    word_doc_count = Counter()
    total_docs = len(data)
    for (_, words) in data:
        unique_words = set(words)
        for word in unique_words:
            word_doc_count[word] += 1
    word_freq_ratio = [(word, round(count / total_docs, 4)) for (word, count) in word_doc_count.items()]
    word_freq_ratio.sort(key=lambda x: x[1], reverse=True)
    if cut_off is None:
        top_words = {word for (word, _) in word_freq_ratio}
    elif cut_off <= 0:
        top_words = set()
    else:
        top_words = {word for (word, _) in word_freq_ratio[:cut_off]}
    return (word_freq_ratio, top_words)
