def matrix_dot_vector(a: list[list[int | float]], b: list[int | float]) -> list[int | float] | int:
    if not a or not b:
        return -1
    num_columns = len(a[0])
    if num_columns != len(b):
        return -1
    result = []
    for row in a:
        if len(row) != num_columns:
            return -1
        dot_product = sum((row[i] * b[i] for i in range(num_columns)))
        result.append(dot_product)
    return result
