def calculate_matrix_mean(matrix: list[list[float]], mode: str) -> list[float]:
    if not matrix:
        return []
    if mode == 'row':
        return [sum(row) / len(row) for row in matrix]
    elif mode == 'column':
        num_columns = len(matrix[0]) if matrix else 0
        means = []
        for col in range(num_columns):
            column_sum = 0.0
            column_count = 0
            for row in matrix:
                if col < len(row):
                    column_sum += row[col]
                    column_count += 1
            if column_count == 0:
                means.append(0.0)
            else:
                means.append(column_sum / column_count)
        return means
    else:
        raise ValueError("Mode must be either 'row' or 'column'")
