def calculate_covariance_matrix(vectors: list[list[float]]) -> list[list[float]]:
    if not vectors:
        return []
    num_features = len(vectors)
    num_observations = len(vectors[0]) if num_features > 0 else 0
    for vec in vectors:
        if len(vec) != num_observations:
            raise ValueError('All vectors must have the same number of observations')
    if num_observations == 0:
        return []
    means = [sum(feature) / num_observations for feature in vectors]
    covariance_matrix = [[0.0 for _ in range(num_features)] for _ in range(num_features)]
    for i in range(num_features):
        for j in range(num_features):
            covariance = 0.0
            for k in range(num_observations):
                covariance += (vectors[i][k] - means[i]) * (vectors[j][k] - means[j])
            covariance /= num_observations - 1
            covariance_matrix[i][j] = covariance
    return covariance_matrix
