def determinant_4x4(matrix: list[list[int | float]]) -> float:
    n = len(matrix)
    if n == 1:
        return matrix[0][0]
    elif n == 2:
        return matrix[0][0] * matrix[1][1] - matrix[0][1] * matrix[1][0]
    else:
        det = 0.0
        for col in range(n):
            minor = []
            for i in range(1, n):
                minor_row = []
                for j in range(n):
                    if j != col:
                        minor_row.append(matrix[i][j])
                minor.append(minor_row)
            sign = (-1) ** (0 + col)
            det += matrix[0][col] * sign * determinant_4x4(minor)
        return det
