def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str, parent_examples=None) -> dict:
    if not examples:
        return majority_value(parent_examples, target_attr)
    target_values = [example[target_attr] for example in examples]
    if all((v == target_values[0] for v in target_values)):
        return target_values[0]
    if not attributes:
        return majority_value(examples, target_attr)
    best_attr = choose_best_attribute(examples, attributes, target_attr)
    tree = {best_attr: {}}
    remaining_attrs = [attr for attr in attributes if attr != best_attr]
    for value in get_values(examples, best_attr):
        subset = [example for example in examples if example[best_attr] == value]
        subtree = learn_decision_tree(subset, remaining_attrs, target_attr, examples)
        tree[best_attr][value] = subtree
    return tree
def majority_value(examples, target_attr):
    if not examples:
        return None
    target_values = [example[target_attr] for example in examples]
    counter = Counter(target_values)
    majority = counter.most_common(1)[0][0]
    return majority
def choose_best_attribute(examples, attributes, target_attr):
    best_gain = -1
    best_attr = None
    for attr in attributes:
        gain = information_gain(examples, attr, target_attr)
        if gain > best_gain:
            best_gain = gain
            best_attr = attr
    return best_attr
def information_gain(examples, attr, target_attr):
    total_entropy = entropy([example[target_attr] for example in examples])
    attr_values = get_values(examples, attr)
    weighted_entropy = 0.0
    for value in attr_values:
        subset = [example for example in examples if example[attr] == value]
        subset_entropy = entropy([example[target_attr] for example in subset])
        weighted_entropy += len(subset) / len(examples) * subset_entropy
    return total_entropy - weighted_entropy
def entropy(target_values):
    if not target_values:
        return 0.0
    counts = Counter(target_values)
    total = len(target_values)
    entropy_val = 0.0
    for count in counts.values():
        probability = count / total
        entropy_val -= probability * math.log2(probability)
    return entropy_val
def get_values(examples, attr):
    return list(set((example[attr] for example in examples)))
